{_ corrigeTexto(texto)                            _}
{_  Devuelve texto modificado para que la voz lo lea    _}
{_  correctamente                                         _}


{_ --------------------- Funciones --------------------- _}
{SetState('shirka_context_last_word_feminine', false)}
{SetState('shirka_context_last_word_plural', false)}

{set esPlural(texto) to:
{_ Nos fijamos en la primera palabra _}
    {set itemobj to lcase(token(texto, " ", 0))}

    {if len(itemobj) > 0: {_ Comprobamos que tenemos una palabra con suficientes letras para procesar _}
        {_ Miramos primero si es plural mirando si acaba en 's' _}
        {set plural to slice(itemobj, len(itemobj)-1) = "s"}
        {SetState('shirka_context_last_word_plural', plural)}
        {return plural}
    }
}

{set esFemenino(texto) to:
    {_ Nos fijamos en la primera palabra _}
    {set itemobj to lcase(token(texto, " ", 0))}
    {SetState('shirka_context_last_word_feminine', false)}

    {if len(itemobj) > 1: {_ Comprobamos que tenemos una palabra con suficientes letras para procesar _}
        {_ Miramos primero si es plural _}
        {if esPlural(itemobj):
            {if find(excepcionesM, itemobj) !=- 1:
                {return false}
            |else:
                {_ comprobamos si la siguiente letra es una A o si tenemos una excepción _}
                {set femenino to (find(itemobj, "a", len(itemobj) - 2) != -1 || find(excepcionesF, itemobj) !=- 1)}
                {SetState('shirka_context_last_word_feminine', femenino)}
                {return femenino}
            }
        |else:
            {if find(excepcionesM, itemobj) !=- 1:
                {return false}
            |else:
                {set femenino to (find(itemobj, "a", len(itemobj) - 1) != -1 || find(excepcionesF, itemobj) !=- 1)}
                {SetState('shirka_context_last_word_feminine', femenino)}
                {return femenino}
            }
        }
    }
}

{set creaArticulos(texto) to:
    {if esPlural(texto):
        {if esFemenino(texto):
            {_ femenino plural _}
            {set del to "de las"} {set l to " las"} {set el to "las"} {set o to "a"} {set s to "s"} {set es to "es"} {set n to "n"} {set un to "unas"} {set e to "a"}
        |else:
            {_ masculino plural _}
            {set del to "de los"} {set l to " los"} {set el to "los"} {set o to "o"} {set s to "s"} {set es to "es"} {set n to "n"} {set un to "unos"} {set e to "o"}
        }
    |else:
        {if esFemenino(texto):
            {_ femenino singular _}
            {set del to "de la"} {set l to " la"} {set el to "la"} {set o to "a"} {set s to ""} {set es to ""} {set n to ""} {set un to "una"} {set e to "a"}
        |else:
            {_ masculino singular _}
            {set del to "del"} {set l to "l"} {set el to "el"} {set o to "o"} {set s to ""} {set es to ""} {set n to ""} {set un to "un"} {set e to "e"}
        }
    }
}


{_ Script que sustituye la función Humanize() adaptado al español _}

{set NumeroALetras(value, b) to:
    {if GetState('shirka_context_last_word_feminine'):
        {set a to 'a'}
    |else:
        {set a to 'o'}
    }

    {if value = 0: {return "cero"}
    |elif value = 1:
        {if GetState('shirka_context_last_word_feminine'):
            {return "una"}
        |else:
            {if decimales:
                {if b = 1: {return "un"}
                    |else: {return "uno"}}
                |elif b = 1:
                    {return "ún"}
            |else:
                {return "un"}
            }
        }
    |elif value = 2:
        {if b = 1:
            {return "dós"}
        |else:
            {return "dos"}
        }
    |elif value = 3: {return "tres"}
    |elif value = 4: {return "cuatro"}
    |elif value = 5: {return "cinco"}
    |elif value = 6: {return "seis"}
    |elif value = 7: {return "siete"}
    |elif value = 8: {return "ocho"}
    |elif value = 9: {return "nueve"}
    |elif value = 10: {return "diez"}
    |elif value = 11: {return "once"}
    |elif value = 12: {return "doce"}
    |elif value = 13: {return "trece"}
    |elif value = 14: {return "catorce"}
    |elif value = 15: {return "quince"}
    |elif value < 20: {return cat("dieci", NumeroALetras(value - 10, b))}
    |elif value = 20: {return "veinte"}
    |elif value < 30: {return cat("veinti", NumeroALetras(value - 20, 1))}
    |elif value = 30: {return "treinta"}
    |elif value = 40: {return "cuarenta"}
    |elif value = 50: {return "cincuenta"}
    |elif value = 60: {return "sesenta"}
    |elif value = 70: {return "setenta"}
    |elif value = 80: {return "ochenta"}
    |elif value = 90: {return "noventa"}
    |elif value < 100: {return cat(NumeroALetras(floor(value / 10) * 10, b), " y ", NumeroALetras(value % 10, b))}
    |elif value = 100: {return "cien"}
    |elif value < 200: {return cat("ciento ", NumeroALetras(value - 100, b))}
    |elif value = 200 || value = 300 || value = 400 || value = 600 || value = 800: {return cat(NumeroALetras(floor(value / 100), b), "cient", a, "s")}
    |elif value = 500: {return cat("quinient", a, "s")}
    |elif value = 700: {return cat("setecient", a, "s")}
    |elif value = 900: {return cat("novecient", a, "s")}
    |elif value < 1000: {return cat(NumeroALetras(floor(value / 100) * 100, b), " ", NumeroALetras(value % 100, b))}
    |elif value = 1000: {return "mil"}
    |elif value < 2000: {return cat("mil ", NumeroALetras(value % 1000, b))}
    |elif value < pow(10,6):
        {if (value % 1000) > 0:
            {return cat(NumeroALetras(floor(value / 1000), b), " mil ", Pause(25),  NumeroALetras(value % 1000, b))}
        |else:
            {return cat(NumeroALetras(floor(value / 1000), b), " mil")}
        }
    |elif value = pow(10,6): {return "un millón"}
    |elif value < 2 * pow(10,6): {return cat("un millón", Pause(150), NumeroALetras(value % pow(10,6), b))}
    |elif value < pow(10,12):
        {if (value - floor(value / pow(10,6)) * pow(10,6)) > 0:
            {return cat(NumeroALetras(floor(value / pow(10,6)), b), " millones", Pause(150), NumeroALetras(value - floor(value / pow(10,6)) * pow(10,6), b))}
        |else:
            {return cat(NumeroALetras(floor(value / pow(10,6)), b), " millones")}
        }
    |elif value = pow(10,12): {return "un bíllón"}
    |elif value < 2 * pow(10,12): {return cat("un bíllón", Pause(150), NumeroALetras(value -  floor(value / pow(10,12)) * pow(10,12), b))}
    |elif value < pow(10, 18):
        {if (value - floor(value / pow(10,12)) * pow(10,12)) > 0:
            {return cat(NumeroALetras(floor(value / pow(10,12)), b), " bíllónes", Pause(150), NumeroALetras(value - floor(value / pow(10,12)) * pow(10,12), b))}
        |else:
            {return cat(NumeroALetras(floor(value / pow(10,12)), b), " bíllónes")}
        }
    |elif value = pow(10,18): {return "un trillón"}
    |elif value < 2 * pow(10,18): {return cat("un trillón", Pause(150), NumeroALetras(value -  floor(value / pow(10,18)) * pow(10,18), b))}
    |elif value < pow(10, 24):
        {if (value - floor(value / pow(10,18)) * pow(10,18)) > 0:
            {return cat(NumeroALetras(floor(value / pow(10,18)), b), " trillones", Pause(150), NumeroALetras(value - floor(value / pow(10,18)) * pow(10,18), b))}
        |else:
            {return cat(NumeroALetras(floor(value / pow(10,18)), b), " trillones")}
        }
    |elif value = pow(10,24): {return "un cuatrillón"}
    |elif value < 2 * pow(10,24): {return cat("un cuatrillón", Pause(150), NumeroALetras(value -  floor(value / pow(10,24)) * pow(10,24), b))}
    |elif value < pow(10, 30):
        {if (value - floor(value / pow(10,24)) * pow(10,24)) > 0:
            {return cat(NumeroALetras(floor(value / pow(10,24)), b), " cuatrillones", Pause(150), NumeroALetras(value - floor(value / pow(10,24)) * pow(10,24), b))}
        |else:
            {return cat(NumeroALetras(floor(value / pow(10,24)), b), " cuatrillones")}
        }
    |else:
        {_ nunca vamos a llegar aquí porque nos salimos del rango de un número entero. Pero me hacía gracia =) }
        {return cat("muchiiiiisim", a, "s")}
    }
}

{set LeeNumero(numberAsString) to:
    {set entero to floor(numberAsString)}
    {set decimales to token(numberAsString, ".", 1)}
    
    {if decimales:
        {if rand(3) = 0:
            {set anotacion to " punto "}
        |elif rand(3) = 0:
            {set anotacion to " con "}
        |else:
            {set anotacion to " coma "}
        }
                {return cat(NumeroALetras(entero), anotacion, decimales))}
    |else:
        {if m: {set decimales to 1}}
        {return NumeroALetras(entero)}
    }
    {return fin}
}

{set log10(n) to:
    {set log to 0}
    {while n >= 10:
        {set n to div(n, 10)}
        {set log to log + 1}
    }
    {return log}
}

{set normalize(inputValue, orderMultiplierVal) to:
    {return [floor(inputValue / orderMultiplierVal), floor((inputValue % orderMultiplierVal) / (orderMultiplierVal / 10))]}
}

{set humaniza(numero) to:
    {set numeroarray to []}

    {_ Nos quedamos sólo con 2 decimales _}
    {set numero to round(numero, 2)}

    {if numero = 0:
        {return "cero"}
    |elif numero < 0:
        {set maybeMinus to "menos "}
        {_ Nos quedamos con la parte positiva y tratamos el número _}
        {set numero to abs(numero)}
    }

    {_ Calculamos la magnitud del número }
    {_ numero = 21524685 }
    {set magnitude to log10(numero)}
    {_ magnitude = 7 }

    {set orderMultiplier to pow(10, floor(magnitude / 3) * 3)}
    {_ orderMultiplier = 1.000.000 }

    {set numeroarray to normalize(numero, orderMultiplier)}
    {_ numero[0] = 21  numero[1] = 5 }

    {if magnitude = 2: {set numeroarray to normalize(numero, 1)}}
    {_ elif magnitude = 3: set numero to normalize(numero, 100)}

    {set number to numeroarray[0]}
    {set nextDigit to numeroarray[1]}

    {_ See if we have a whole number that is fully described within the largest order _}
    {_ Si el número es un número redondo (o es mas pequeño que 99.999), lo leemos directamente, sin redondear }
    {if (number * orderMultiplier = numero) || magnitude < 6:
        {set retorna to cat(maybeMinus, LeeNumero(numero)))}

    |elif number < 100:
        {_ See if we have a number whose numero can be expressed with a short decimal (i.e 1.3 million) _}
        {set shortDecimal to (number + (nextDigit / 10))}
        {if shortDecimal = round(numero / orderMultiplier,  1) && nextDigit != 0 && magnitude > 10:
            {if rand(3) = 0:
                {set anotacion to " punto "}
            |elif rand(3) = 0:
                {set anotacion to " con "}
            |else:
                {set anotacion to " coma "}
            }
            {set retorna to cat("alrededor de ", maybeMinus, LeeNumero(number), anotacion, LeeNumero(nextDigit), Pause(30), " ", LeeNumero(orderMultiplier))}
        }

        {_ Describe values for complex numbers where the largest order number does not exceed one hundred _}
        {if magnitude >= 9:
            {set andahalf to " y medio"}
        |else:
            {if rand(2):
                {set andahalf to " y medio"}
            |else:
                {set andahalf to cat(" ", LeeNumero(500 * (orderMultiplier / 1000)))}
            }
        }

        {if nextDigit = 0:
            {_ the figure we are saying is round enough already _}
            {set retorna to cat("alrededor de ", maybeMinus, LeeNumero(number * orderMultiplier))}
        |elif nextDigit = 1:
            {set retorna to cat("un poco más de ", maybeMinus, LeeNumero(number * orderMultiplier))}
        |elif nextDigit = 2:
            {set retorna to cat("más de ", maybeMinus, LeeNumero(number * orderMultiplier))}
        |elif nextDigit = 3:
            {set retorna to cat("mucho más de ", maybeMinus, LeeNumero(number * orderMultiplier))}
        |elif nextDigit = 4:
            {set retorna to cat("cerca de ", maybeMinus, LeeNumero(number * orderMultiplier), andahalf)}
        |elif nextDigit = 5:
            {set retorna to cat("alrededor de ", maybeMinus, LeeNumero(number * orderMultiplier), andahalf)}
        |elif nextDigit = 6:
            {set retorna to cat("un poco más de ", maybeMinus, LeeNumero(number * orderMultiplier), andahalf)}
        |elif nextDigit = 7:
            {set retorna to cat("más de ", maybeMinus, LeeNumero(number * orderMultiplier), andahalf)}
        |elif nextDigit = 8:
            {set retorna to cat("mucho más de ", maybeMinus, LeeNumero(number * orderMultiplier), andahalf)}
        |elif nextDigit = 9:
            {set retorna to cat("cerca de ", maybeMinus, LeeNumero((number + 1) * orderMultiplier))}
        }
        
    |else:
        {_ Round largest order numbers in the hundreds to the nearest 10, except where the number after the hundreds place is 20 or less _}
        {if (number - (number/100) * 100) >= 20:
            {set number to normalize(number, 10)}
            {set number to numero[0]}
            {set nextDigit to numero[1]}
            {set number to number * 10}
        }

        {if nextDigit = 0:
            {_ the figure we are saying is round enough already _}
            {set retorna to cat("alrededor de ", maybeMinus, LeeNumero(number * orderMultiplier))}
        |elif nextDigit < 3:
            {set retorna to cat("un poco más de ", maybeMinus, LeeNumero(number * orderMultiplier))}
        |elif nextDigit < 5:
            {set retorna to cat("un poco menos de ", maybeMinus, LeeNumero(number * orderMultiplier), " y medio")}
        |elif nextDigit = 5:
            {set retorna to cat("alrededor de ", maybeMinus, LeeNumero(number * orderMultiplier), " y medio")}
        |elif nextDigit < 8:
            {set retorna to cat("más de ", maybeMinus, LeeNumero(number * orderMultiplier), " y medio")}
        |elif nextDigit < 10:
            {set retorna to cat("cerca de ", maybeMinus, LeeNumero((number + 1) * orderMultiplier))}
        }
    }
    
    {if find(retorna,"ón",len(retorna)-3)!=-1 || find(retorna,"nes",len(retorna)-4)!=-1 || find(retorna,"dio",len(retorna)-4)!=-1: {set de to "de"}}
    {return retorna}
}

{set reemplazaTexto(texto, word, replace) to:
    {return join(split(texto, word), replace)}
}

{set corrigeTexto(texto) to:
    {set texto to lcase(texto)}
    {for palabra in flip(palabrasErroneas):
        {set texto to reemplazaTexto(texto, palabra, palabrasErroneas[palabra])}
    }
    {return texto}
}

{set leeTexto(texto) to:
    {if vehicle = "On Foot":
        {Transmit(corrigeTexto(texto))}
    |else:
        {corrigeTexto(texto)}
    }
}

{_ valores fijos usados para la funcion de getbodyvalue    _}
{set valuePlanetTypes to [
    "Mundo de amoníaco": 96932,
    "Mundo similar a la Tierra": 64831,
    "Gigante gaseoso con vida basada en el amoníaco": 300,
    "Gigante gaseoso con vida basada en el agua": 300,
