]}


{set getBodyValue(cuerpo, isMapped) to:

    {if cuerpo.bodytype = "Estrella":  {return cuerpo.estimatedvalue}}
    
    {if !defined(isMapped): {set isMapped to cuerpo.mapped}}

    {set k to valuePlanetTypes[cuerpo.planettype]}
    {if (cuerpo.terraformstate = "Candidato para terraformación" ||cuerpo.terraformstate = "Terraformado"):
        {if cuerpo.planettype = "Cuerpo con alto contenido metálico":
            {set k to k + 100677}
        |elif cuerpo.planettype = "Mundo acuático" || cuerpo.planettype = "Mundo similar a la Tierra":
            {set k to k + 116295}
        |else:
            {set k to k + 93328}
        }
    }

    {set q to 0.56591828}
    {set mappingMultiplier to 1}

    {if isMapped:
        {if !cuerpo.alreadydiscovered && !cuerpo.alreadymapped:
            {set mappingMultiplier to 3.699622554}
        |elif !cuerpo.alreadymapped:
            {set mappingMultiplier to 8.0956}
        |else:
            {set mappingMultiplier to 3.3333333333}
        }
    }
    
    {set value to (k + k * q * pow(cuerpo.earthmass,0.2)) * mappingMultiplier}
    {if isMapped:
        {if odyssey:
            {if (value * 0.3) > 555:
                {set value to value + (value * 0.3)}
            |else:
                {set value to value + 555}
            }
        }
    }
    {set value to max(500, value)}
    {if !cuerpo.alreadydiscovered: {set value to value * 2.6}}

    {return round(value)}
}

{set getMaxBodyValue(cuerpo) to:
    {return round(getBodyValue(cuerpo, true) * 1.25)}
}

{set esTerraformable(cuerpo) to:
    {return cuerpo.terraformState.invariantName != "Not terraformable"}
}

{set esValioso(cuerpo) to:
    {return (recommendAlreadyMapped || !cuerpo.alreadymapped) && ((esTerraformable(cuerpo) && reportTerraformables) || reportPlanetTypes[cuerpo.planettype] || (getMaxBodyValue(cuerpo) > highValueThreshold))}
}

