{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}
}

{_ Function to obtain current inventory of a material}
{set GetMaterialFromInventory(requiredMaterialName) to:
    {for material in materials:
        {if material.material = requiredMaterialName:
            {return material}
        }
    }
}

{set canbemade to 999}

{if blueprint:
    {_ Find the smallest number of this blueprint that can be made }
    
    {set cur to 0}
    {while cur < len(blueprint.materials):
        {set materialInventory to GetMaterialFromInventory(blueprint.materials[cur].material)}
        {if !materialInventory:
            {set canbemade to 0}
        |else:
            {if materialInventory.amount < canbemade:
                {set canbemade to materialInventory.amount}
            }
        }
        {set cur to cur + 1}
    }

    {if canbemade = 0:
        {set report to OneOf("No podemos fabricar ningún {blueprintname} de grado {blueprintgrade}", "No puedes fabricar ningún {blueprintname}")}
    |elif canbemade = 1:
        {set report to OneOf("Podemos fabricar una unidad de {blueprintname} grado {blueprintgrade}", "Podrías fabricar un {blueprintname}")}
    |else:
        {set report to OneOf("Podemos fabricar {blueprintname} de {blueprintgrade} hasta {Humanise(canbemade)} veces.", "Podrías fabricar {canbemade} {blueprintname}")}
    }
    
|else:
    {set report to "No dispongo de información suficiente."}
}

{leeTexto(report)}.

Blueprint material report.script:
{_ Context }
{SetState('eddi_context_last_subject', 'blueprint')}

{_ Fetch from context / state _}
{set blueprintname to GetState("eddi_context_blueprint_name")}
{set blueprintgrade to GetState("eddi_context_blueprint_grade")}

{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details _}
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}
}

{if blueprint:
    {set blueprintMaterials to []}
    {for material in blueprint.materials:
         {set blueprintMaterials to cat(blueprintMaterials, [material.material])}
    }
    {set report to cat(blueprintname, " de grado ", blueprintgrade, " requiere ")}
    {set report to cat(report, List(blueprintMaterials))}
|else:
    {set report to "No dispongo de información suficiente."}
}

{leeTexto(report)}.

Bodies mapped.script:
{_ Fetch from Context, if a context is set _}
{if GetState("eddi_context_system_name"):
    {set reportSystem to SystemDetails(GetState("eddi_context_system_name"))}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{_ Build a list of scanned bodies _}

{set mappedList to []}
{for body in reportSystem.bodies:
   {if body.mapped:
       {set mappedList to cat(mappedList, [P(body.shortname, "body")])} 
   }
}

{_ Begin speaking _}

{if len(mappedList) > 0:
    {if len(mappedList) = 1: El cuerpo |else: Los cuerpos}
    {List(mappedList)},
    {Occasionally(2, "ya")}
    {if len(mappedList) = 1: ha sido mapeado |else: han sido mapeados}.
|else:
	{OneOf("Aún", "Todavía", "")} no {OneOf("has", "tienes")} {OneOf("mapeado", "escaneado")} ningún cuerpo {OneOf("de este sistema", "en este sistema", "aquí", "")}.
}

Bodies to map.script:
{_ Fetch from Context, if a context is set _}
{if GetState("eddi_context_system_name"):
	{set reportSystem to SystemDetails(GetState("eddi_context_system_name"))}
|else:
	{set reportSystem to SystemDetails(system.systemname)}
}

{_ Identify bodies worth mapping _}
{set toMapList to []}
{set mappedBodies to 0}
{set totalBodies to 0}
{set valueCount to 0}

{if hasDSS:
    {for body in reportSystem.bodies:
        {set totalBodies to totalBodies + 1}
        {if body.bodytype.invariantName != "Star":
            {if body.mapped: {set mappedBodies to mappedBodies + 1}}
            {if esValioso(body):
                {set valueCount to valueCount + 1}
                {if !body.mapped:
                    {if !toMapList[P(body.shortname, "body")]: 
                        {set toMapList to cat(toMapList, [P(body.shortname, "body")])}
                    }
                }
            }
        }
    }
}

{set toMapCount to len(toMapList)}

{_ Begin speaking _}
{if toMapCount > 0:

    {if event.type = "Body mapped": 
    	{_ Mi easter egg dedicado a mis compañeros de Explorers of the Anarchy CMDT Nuredunna y CMDT Giordano Albino ;) _}
    	{if ShirkaSarcastica && !rand(30):
    		{set ademas to Emphasize("A más a más","strong")}
		|else:
    		{set ademas to OneOf("además,", "también")}
    	}
    }

	{if toMapCount = 1:
		{set report to cat("El cuerpo ", List(toMapList), " es ", ademas)}
		{OneOf(
			"{report} un {OneOf('recomendado', 'buen')} candidato {OneOf('para mapear', 'para cartografiar')}."
			"{report} un {OneOf('objeto', 'cuerpo')} {OneOf('valioso', 'mapeáble')}."
		)}
	|else:
		{set report to cat("Los cuerpos ", List(toMapList), " son ", ademas)}
		{OneOf(
			"{report} {Occasionally(3, 'unos')} {OneOf('recomendados', 'buenos')} candidatos {OneOf('para mapear', 'para cartografiar')}."
			"{report} {OneOf('objetos', 'cuerpos')} {OneOf('valiosos', 'mapeábles')}."
		)}
	}

|elif valueCount > 0 && mappedBodies >= valueCount && reportSystem.totalbodies = reportSystem.scannedbodies:
	{_ Ponemos a cero el contador de sistemas no valiosos para la personalidad de Shirka _}
	{_ Lo hacemos sólo cuando se han mapeado todos los cuerpos de un sistema, y no cuando encontramos un valioso, porque puede ser que esté muy alejado y no compense el viaje, por lo que ese sistema cuenta como malo _}
	{SetState("eddi_context_system_not_worthy", 0)}

   	{OneOf(
       	"{Occasionally(2, 'Ya')} has mapeado todos los cuerpos {if valueCount: {OneOf('valiosos', 'interesantes', 'que valen la pena')}} {Occasionally(2, 'de este sistema')}.",
       	"{Occasionally(2, 'Todos')} los cuerpos {if valueCount: {OneOf('valiosos', 'interesantes', 'que valen la pena')}} {Occasionally(2, 'de este sistema, ')} {Occasionaly(2, 'ya')} {OneOf('han sido', 'están')} {OneOf('mapeados', 'cartografiados')}."
   	)}

|elif !valueCount && reportSystem.scannedbodies = reportSystem.totalbodies && GetState("eddi_context_last_action") != 'honk':
	{set enEsteSistema to Occasionally(2, "en este sistema")}
	{if !enEsteSistema:
		{set deEsteSistema to Occasionally(2, "{OneOf('en este sistema', 'de este sistema')}")}
	}
	{set random to rand(4)}
	{if random = 0:
		{set report to cat('Ningún cuerpo ', deEsteSistema, ' ', OneOf("cumple con", "encaja con", "sigue"), ' los ', OneOf('parametros', 'valores'), " ", OneOf('proporcionados', 'definidos', 'establecidos', 'indicados', ''), " para ser mapeado ", enEsteSistema)}
	|elif random = 1:
		{set report to cat('Este sistema no tiene ningún cuerpo que ', OneOf("cumpla con", "encaje con", "siga"), ' los ', OneOf('parametros', 'valores'), " ", OneOf('proporcionados', 'definidos', 'establecidos', 'indicados', ''), " ", OneOf('que lo considere valioso', 'que lo considere mapeáble', 'para ser mapeado'))}
	|elif random = 2:
		{set report to cat('Los sensores no ', OneOf("han {OneOf('encontrado', 'detectado')}", OneOf("encuentran", "detectan")), ' cuerpos ', enEsteSistema)}
		{set random to rand(3)}
		{if !random:
			{set report to cat(report, 'que ', OneOf('cumplan', 'sigan'), ' los ', OneOf('parametros', 'valores'), " ", OneOf('proporcionados', 'definidos', 'establecidos', 'indicados', ''), " ", OneOf('que los consideren valiosos', 'que los consideren mapeábles','para ser mapeados'))}
		|else:
			{set report to cat(report, OneOf("valiosos", "mapeábles", "que valgan la pena {Occasionally(2, 'mapear')}"))}
		}
	|else:
		{set report to cat('No ', OneOf("se han {OneOf('encontrado', 'detectado')}", OneOf("he encontrado", "encuentro", "he detectado", "detecto", "hay")), ' cuerpos ', deEsteSistema)}
		{set random to rand(3)}
		{if !random:
			{set report to cat(report, 'que ', OneOf('cumplan', 'sigan'), ' los ', OneOf('parametros', 'valores'), " ", OneOf('proporcionados', 'definidos', 'establecidos', 'indicados', ''), " ", OneOf('que los consideren valiosos', 'que los consideren mapeábles', 'para ser mapeados'), " ", enEsteSistema)}
		|else:
			{set report to cat(report, OneOf("valiosos", "mapeábles", "que valgan la pena {Occasionally(2, 'mapear')}"), " ", enEsteSistema)}
		}
	}
	{report}.
	
	{_ Activamos a la Shirka sarcástica si el sistema tiene más de 35 cuerpos y ninguno es valioso	_}
	{_ o cuando nos hemos encontrado muchos sistemas seguidos sin ningún valioso					_}
	{if ShirkaSarcastica:
		{if reportSystem.totalbodies > 35:
			{set report to OneOf("Tanto cuerpo y ninguno {OneOf('valioso', 'vale la pena')}.", "Con {reportSystem.totalbodies} cuerpos... {OneOf('Menuda decepción...', 'ya es mala suerte...', 'esto {Pause(30)} estadísticamente, es mala suerte.')}", " ")}
			{Transmit(report)}
		|else:
			{if !GetState("eddi_context_system_not_worthy"): {SetState("eddi_context_system_not_worthy", 0)}}
			{SetState("eddi_context_system_not_worthy", GetState("eddi_context_system_not_worthy") + 1)}
			{if GetState("eddi_context_system_not_worthy") > 8:
				{set random to rand(6)}
				{if !random:
					{SetState("eddi_context_system_not_worthy", 0)}
					{set report to OneOf("¡Vaya racha de sistemas de mierda!", "¡Otro sistema que no vale para nada!", "¿Cuantos sistemas sin nada interesante van ya?", "Como el próximo sistema sea igual de malo, me desconecto.", "¡Esto es desesperante...!", "¡Increible! ¿Otro más?")}
					{Transmit(report)}
				}
			}
		}
	}

|elif reportSystem.scannedbodies < reportSystem.totalbodies && GetState("eddi_context_last_action") != 'honk' && len(reportSystem.factions) = 0:
	{set faltan to reportSystem.totalbodies - reportSystem.scannedbodies}
	{if faltan > 1: {set s to "s"}{set n to "n"}
	|else: {set s to ""}{set n to ""}
	}
	Aviso:
	{OneOf("Falta{n} {faltan} cuerpo{s} por escanear en este sistema {Occasionally(2,'estelar')}", "Este sistema {Occasionally(2,'estelar')} no está completamente escaneado")}.
}

Body atmosphere report.script:
{_ Define functions for converting temperature scales _}
{set ToCelsius(kelvin) to: {round(kelvin - 273)}}
{set ToFahrenheit(kelvin) to: {round((kelvin-273)*9/5 + 32)}}
{set TempCalc(kelvin) to: 
    {if reportAtmoTemperature = 1: {round(kelvin)}}
    {if reportAtmoTemperature = 2: {ToCelsius(kelvin)}}
    {if reportAtmoTemperature = 3: {ToFahrenheit(kelvin)}}
}

{_ Name our temperature scale _}
{set tempscale() to:
	{if reportAtmoTemperature = 1: Kelvin}
	{if reportAtmoTemperature = 2: {cat("grado", s, " ", OneOf('Celsius', 'centígrados', ''))}}
	{if reportAtmoTemperature = 3: {cat("grado", s," Fahrenheit")}}
}
{set s to "s"}

{_ Fetch from context _}
{set reportbody to BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_body_system"))}
{if !reportbody: 
    {set reportbody to BodyDetails(GetState("eddi_context_body_name"))}
}

{if !reportbody.name || reportbody.name = "":
    Lo siento, no me queda claro a que cuerpo estelar te refieres.

|else:
    {if find(reportbody.planettype, "Gigante") >= 0:
        {if find(reportbody.planettype, "Clase") >= 0: 
            {set sudarsky to Occasionally(3, "Sud-dar-eskai")}
        }
        {set planetdesc to cat("Este gigante gaseoso de clase ", Occasionally(3, "Sud-dar-eskai {Pause(50)}"))}
        {if find(reportbody.planettype, "Clase 5") >= 0:
            {planetdesc} 5 {Pause(50)} tiene
            {if reportAtmoTemperature: 
                {set tempmin to: {TempCalc(1373)}}
                temperaturas por encima de {humaniza(round(tempmin))} {tempscale()}, {OneOf("y", "con", "formando")}
            }
            una densa capa de nubes de {OneOf("hierro y silicatos en forma de vapor", "vapores de hierro y silicatos", "silicatos y hierro vaporizados")}.
            
        |elif find(reportbody.planettype, "Clase 4") >= 0:
            {planetdesc} 4 {Pause(50)} tiene
            {if reportAtmoTemperature: 
                {set tempmin to TempCalc(354)}
                {set tempmax to TempCalc(903)}
                temperaturas {OneOf("de", "que oscilan")} entre {humaniza(round(tempmin))} y {humaniza(round(tempmax))} {tempscale()}, {OneOf("y", "con", "formando")}
            }
            una densa capa de nubes de {OneOf("vapores metálicos alcalinos", "metales alcalinos en forma de vapor", "metales alcalinos vaporizados")}.   
            
        |elif find(reportbody.planettype, "Clase 3") >= 0:
            {planetdesc} 3 {Pause(50)} tiene
            {if reportAtmoTemperature: 
                {set tempmin to TempCalc(251)}
                {set tempmax to TempCalc(353)}
                temperaturas de superficie {OneOf("de", "que oscilan")} entre {humaniza(round(tempmin))} y {humaniza(round(tempmax))} {tempscale()},
            }
            una temperatura en la que {OneOf("ningún elemento gaseoso puede formar nubes", "no están disponibles los químicos necesarios para formar una capa de nubes", "la atmósfera no puede sostener una capa de nubes persistente")}.
            
        |elif find(reportbody.planettype, "Clase 2") >= 0:
            {planetdesc} 2 {Pause(50)} tiene
            {if reportAtmoTemperature: 
                {set tempmin to TempCalc(154)}
                {set tempmax to TempCalc(250)}
                temperaturas de superficie {OneOf("de", "que oscilan")} entre los {humaniza(round(tempmin))} y los {humaniza(round(tempmax))} {tempscale()}, {OneOf("y", "con", "formando")}
			}
			una densa capa de nubes de {OneOf("vapor de agua", "agua en forma de vapor")}.
			
        |elif find(reportbody.planettype, "Clase 1") >= 0:
            {planetdesc} 1 {Pause(50)} tiene
            {if reportAtmoTemperature: 
                {set tempmin to TempCalc(153)}
                {set tempmax to TempCalc(83)}
                temperaturas de superficie de {Occasionally(2, OneOf("por lo menos", "un máximo de"))} {humaniza(round(tempmax))} {tempscale()} {OneOf("y", "con", "formando")}
			}
			una densa capa de nubes de {OneOf("amoníaco", "vapores de amoníaco")}.
		}

	|elif reportbody.atmosphere = "Sin atmósfera" || !reportbody.atmosphere:
        {Occasionally(3, OneOf("No hay atmósfera.", "Está carente de cualquier tipo de atmósfera.", "los años han limpiado cualquier rastro de atmósfera."))}
        {if reportbody.temperature && reportAtmoTemperature:
            Las temperaturas de superficie {OneOf("se mueven en los", "oscilan alrededor de los", "varían entorno los")} {humaniza(round(TempCalc(reportbody.temperature)))} {tempscale()}.
		}

    |elif reportbody.pressure: 
        {if reportbody.pressure < 0.1:
            {set pressdesc to OneOf("casi inapreciable", "extremadamente delgada")}
        |elif reportbody.pressure < 0.25:
            {set pressdesc to "muy delgada"}
        |elif reportbody.pressure < 0.5:
            {set pressdesc to "delgada"}
        |elif reportbody.pressure < 2:
            {set pressdesc to "afable"}
        |elif reportbody.pressure < 10:
            {set pressdesc to "amplia"}
        |elif reportbody.pressure < 50:
            {set pressdesc to OneOf("densa", "muy amplia")}
        |elif reportbody.pressure < 100:
            {set pressdesc to OneOf("muy densa", "extremadamente amplia")}
        |elif reportbody.pressure >= 100:
            {set pressdesc to OneOf("aplastante", "extremadamente densa")}
        }
        
        {if find(reportbody.atmosphere, "Rica") >= 0:
            {set desc to OneOf("Detecto una ", "Hay una ", "Los sensores detectan una")}
            {OneOf("{desc} atmósfera {pressdesc} {reportbody.atmosphere}", "La atmósfera de este planeta es {pressdesc} y {reportbody.atmosphere}", "La {pressdesc} atmósfera de este planeta es {reportbody.atmosphere}")}
            
        |elif find(reportbody.atmosphere, "vapor") >= 0:
            {OneOf("Su {pressdesc} atmósfera es de {reportbody.atmosphere}", "Su atmósfera es {pressdesc} y está compuesta de {reportbody.atmosphere}", "Su {pressdesc} atmósfera se compone de {reportbody.atmosphere}")}
            
		|elif reportbody.planettype = "Mundo similar a la Tierra":
            La atmósfera es {OneOf("similar", "parecida")} a la de la Tierra, 
            
		|else:
            {set mostly to OneOf("principalmente", "sobre todo", "esencialmente", "mayoritariamente")}
            {OneOf("La {pressdesc} atmósfera está compuesta {mostly} de", "El componente principal de esta atmósfera {pressdesc} es")} 
            {reportbody.atmosphere}
        }
        
        {if reportAtmoPressure:
        	{set y to "y"}
        	{set reportAtmoPressure to humaniza(reportbody.pressure, 1)}
       		{if round(reportbody.pressure, 2) = 1: {set s to ""} |else: {set s to "s"}}
      		{Occasionally(2,Pause(30))}con una presión {Occasionally(2, "en superficie")} de {reportAtmoPressure} {de} atmósfera{s} {Occasionally(2, "terrestre{s}")}
        }
        
        {if reportbody.temperature && reportAtmoTemperature:
            {set temp to: {round(TempCalc(reportbody.temperature))}}
            {if abs(temp) = 1: {set s to ""} |else: {set s to "s"}}
            {if find(temp, "-") >= 0:
                {set temp to humaniza(abs(temp))}
				{set temp to cat(temp, " ", tempscale(), " bajocero")}
            |else:
                {set temp to humaniza(temp)}
				{set temp to cat(temp, " ", tempscale())}
            }
            {Occasionally(2,Pause(30))}
			{if y: {y} {Occasionally(2, "con")}
			|else: {Pause(30)} con }
			una media de temperatura {Occasionally(2, "en superficie")} de {temp}
        }.
	}
}

Body mapped.script:
{_ Context}
{SetState('eddi_context_last_action', 'scan')}
{SetState('eddi_context_last_subject', 'body')}
{SetState('eddi_context_body_system', system.name)}
{SetState('eddi_context_body_name', event.name)}
{SetState('eddi_context_last_scan_estimated_value', getBodyValue(BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_body_system"))))}
{SetState('eddi_context_last_scan_max_value', getMaxBodyValue(BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_body_system"))))}

{_ Process the event _}
{if ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose":
	{set scanValue to GetState("eddi_context_last_scan_estimated_value")}

	{set report to cat(OneOf(
		'{OneOf("Mapeado", "Mapeo", "Análisis")} {Occasionally(2, "de superficie")}	{OneOf("finalizado", "completado", "terminado", "realizado")}.'
		'{OneOf("Finalizado", "Completado", "Terminado", "Realizado")} {OneOf("mapeado", "mapeo", "análisis")} {Occasionally(2, "de superficie")}.'
	))}
	
	{if event.probesused <= event.efficiencytarget:
		{set scanValue to round(scanValue * 1.25)}
		{if reportEficiencia && !rand(reportEficiencia):
			{set eficiencia to OneOf(
    			'Bonificación {OneOf("de", "por")} eficiencia {OneOf("concedida", "obtenida")}.',
    			'{OneOf("concedida", "obtenida")} bonificación {OneOf("de", "por")} eficiencia.'
    			'Bono {OneOf("de", "por")} eficiencia {OneOf("concedido", "obtenido")}.',
    			'{OneOf("Concedido", "Obtenido")} bono {OneOf("de", "por")} eficiencia.'
    		)}
    	}
	}

	{if event.alreadymapped:
		{if eficiencia:
			{set report to OneOf(
				'{report} {eficiencia}',
				'{OneOf("Mapeado", "Mapeo", "Análisis")} {Occasionally(2, "de superficie")} con eficiencia {OneOf("finalizado", "completado", "terminado", "realizado")}.'
				'{OneOf("Finalizado", "Completado", "Terminado", "Realizado")} {OneOf("mapeado", "mapeo", "análisis")} {Occasionally(2, "de superficie")} con eficiencia.'
			)}
		}
	|else:
		{_ Añadimos un poco de sarcasmo a Shirka _}
		{if ShirkaSarcastica && !rand(5) && scanValue > 1800000:
			{set shirka to Transmit(OneOf(
				'Ahora vas, y te estrellas...',
				'¡No te estrelles!',
				'Recuerda que si me pasa algo, lo pierdes todo.',
				'Ya sabes: Si no lo entregas, no hay premio.',
				'Entrégalo antes de que te lo quiten.',
				'¡No es justo que sólo pongan tu nombre!'
			))}
		}

		{if reportPrimerMapeo && !rand(reportPrimerMapeo):
			{if eficiencia:
				{set report to cat(OneOf(
					'{OneOf("Has sido el primero", "Hemos sido los primeros")} en {OneOf("finalizar", "completar", "terminar", "realizar")} este {OneOf("mapeado", "mapeo", "análisis")} {Occasionally(2, "de superficie")} y {OneOf("además,", "encima", "este", "")} ha sido {OneOf("de forma eficiente", "eficientemente")}.',
					'{report} Bonificaciones {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")} y {OneOf("de", "por")} eficiencia {OneOf("concedidas", "obtenidas")}.',
					'{report} {OneOf("concedidas", "obtenidas")} bonificaciones {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")} y {OneOf("de", "por")} eficiencia.'
					'{report} Bonos {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")} y {OneOf("de", "por")} eficiencia {OneOf("concedidos", "obtenidos")}.',
					'{report} {OneOf("Concedidos", "Obtenidos")} bonos {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")} y {OneOf("de", "por")} eficiencia.'
					))}
			|else:
				{set report to cat(OneOf(
					'{OneOf("Has sido el primero", "Hemos sido los primeros")} en {OneOf("finalizar", "completar", "terminar", "realizar")} este {OneOf("mapeado", "mapeo", "análisis")} {Occasionally(2, "de superficie")}.',
					'{report} Bonificación {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")} {OneOf("concedida", "obtenida")}.',
					'{report} {OneOf("concedida", "obtenida")} bonificación {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")}.'
					'{report} Bono {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")} {OneOf("concedido", "obtenido")}.',
					'{report} {OneOf("Concedido", "Obtenido")} bono {OneOf("de", "por")} primer {OneOf("mapeado", "mapeo")}.'
					))
				}
			}
		}
	}

	{_ Leemos el informe _}
	{Pause(3000)}
	{report}

	{_ Leemos cuanto hemos ganado _}
	{if reportValue:
		{set scanValue to humaniza(scanValue)}
		{OneOf(
			'{scanValue} {de} créditos {OneOf("otorgados", "obtenidos", "reservados", "adjudicados", "abonados", "adquiridos")}.',
			'{OneOf("otorgados", "obtenidos", "reservados", "adjudicados", "abonados", "adquiridos")} {scanValue} {de} créditos.'
		)}
		{shirka}
	}

	{_ Recomendamos otros cuerpos según preferencias _}
    {if recommendOtherMapping: {F("Bodies to map")}}
    
    {_ Leemos otros informes del cuerpo según preferencias _}
    {if bodyReport = 1:
        {F("Body report summary")}
	|elif bodyReport = 2:
		{F("Body report")}
	|elif bodyReport = 3:
    	{F("Body report summary")}
    	{F("Body report")}
    |elif bodyReport = 4:
    	{OneOf(F("Body report summary"), F("Body report"))}
    |elif bodyReport > 4:
    	{OneOf(F("Body report summary"), F("Body report"), "")}
    }

}

Body materials report.script:
{_ Fetch from context _}
{set reportbody to BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_body_system"))}
{if !reportbody: 
    {set reportbody to BodyDetails(GetState("eddi_context_body_name"))}
}

{if !reportbody.name || reportbody.name = "":
	{_ If it's still not possible to get the body name...  _}
	{Occasionally(2,"Lo siento. ")}
	{OneOf("Hay interferencias en el sistema electrónico, y no consigo resultados óptimos en el escaneo de este cuerpo estelar",
		"No consigo datos claros del escáner",
		"Los datos del escáner se han corrompido antes de su recepción",
		"Parece que hay interferencias solares que no nos permiten el correcto escaneo de este planeta"
	)}


|else:
	{_ Define a function to obtain a material from inventory}
	{set _inventory(required) to:
		{for material in materials:
			{if material.material = required:
				{return material}
			}
		}
	}

	{if reportbody.landable:

		{set goodmaterials to []}
		{set greatmaterials to []}
		{set othermaterials to []}

		{for material in reportbody.materials:
			{set inventoryMat to _inventory(material.material)}
			{if (reportMats >= 0 && inventoryMat.amount < inventoryMat.minimum && inventoryMat.minimum > 0)
				|| (reportMats >= 1 && inventoryMat.amount < inventoryMat.desired && inventoryMat.desired > 0)
				|| (reportMats >= 2):
				{if material.percentage >= material.definition.greatpctbody && reportMatConc > -1:
					{set greatmaterials to cat(greatmaterials, [material])}
				|elif material.percentage >= material.definition.goodpctbody && reportMatConc > 0:
					{set goodmaterials to cat(goodmaterials, [material])}
				|elif reportMatConc > 1:
					{set othermaterials to cat(othermaterials, [material])}
				}
			}
		}

		{_ Start speaking _}

		{if len(goodmaterials) > 0 || len(greatmaterials) > 0 || len(othermaterials) > 0:
			{if reportbody.name = reportbody.shortname:
				{P(reportbody.shortname, "body")}
			|else:
				{OneOf("Este planeta", cat("El cuerpo ", P(reportbody.shortname, "body"),","))} 
			}
			{OneOf("contiene", "tiene")}
		}

		{if len(greatmaterials) > 0:
			{OneOf("niveles muy altos", "muy altos niveles", "niveles muy elevados", "muchísimos depósitos")} de
			{set cur to 0}
			{set length to len(greatmaterials) - 1}
			{while cur < len(greatmaterials):
				{P(greatmaterials[cur].definition.name)}
				{if reportMatConc > 0: en un {round(greatmaterials[cur].percentage, 1)}% }
				{set cur to cur + 1}
				{if cur = length: y
				|elif cur < length:,
				}
			}

			{if len(goodmaterials) > 0 && len(othermaterials) = 0:
				y
			|elif len(goodmaterials) > 0 || len(othermaterials) > 0:
				,
			}
		}

		{if len(goodmaterials) > 0:
			{OneOf("altos niveles", "niveles altos", "niveles elevados", "multitud de depósitos")} de
			{set cur to 0}
			{set length to len(goodmaterials) - 1}
			{while cur < len(goodmaterials):
				{P(goodmaterials[cur].definition.name)}
				{if reportMatPercent > 1: en un {round(goodmaterials[cur].percentage, 1)}% }
				{set cur to cur + 1}
				{if cur = length: y
				|elif cur < length:,
				}
			}

			{if len(othermaterials) > 0:
	            y
			|else:
				,
	        }
		}

		{if len(othermaterials) > 0:
            {OneOf("niveles moderados", "niveles bajos", "bajas concentraciones", "una cantidad modesta de depósitos")} de
            {set cur to 0}
            {set length to len(othermaterials) - 1}
            {while cur < len(othermaterials):
                {P(othermaterials[cur].definition.name)}
                {if reportMatPercent > 2: en un {round(othermaterials[cur].percentage, 0)}% }
                {set cur to cur + 1}
                {if cur = length: y
                |elif cur < length:,
				}
			}
		}

		{if len(goodmaterials) > 0 || len(greatmaterials) > 0 || len(othermaterials) > 0:
            .
        }
    }
}

Body Name Tidy.script:
{_ Body Name Tidy _}
{_ Tidy up body names _}
{_ If the body name contains the system name, it is removed _}
{_ Adds Star, Planet, or Moon depending on body location within the system _}
{_ Corrects pronunciation of letters in body names. No more 'hour' for 'h' with some TTS voices _}
{_ Returns 'body' and 'body_type 'for use in other scripts _}


{if len(reportbody.shortname) = 0:
    {if event:
        {if !event.name:
            {_ For events that have different variables _}
            {set reportbody to BodyDetails(event.body, event.system)}
        |else:
            {set reportbody to BodyDetails(event.name, system.name)}
        }
    |elif reportbody.stellarclass:
        {set reportbody to BodyDetails(GetState("eddi_context_star_name"), GetState("eddi_context_star_system"))}
    |else:
        {set reportbody to BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_system_system"))}
    }
}

{set body to reportbody.name}
{set system to reportbody.systemname}

{if body = system:
    {set body to "The primary star"}
    {set body_type to "Star"}

|elif find(body, system) > -1:

    {set parent to split(body, " ")}
    {set p_body to parent[len(parent)-2]}
    {set parent to join(except(parent, [len(parent)-1:"", len(parent)-1:""]), " ")}

    {if (parent != system) && len(p_body) = 1:
        {set parent to BodyDetails(parent, system).type}
    }

    {_ Remove system name from the body name _}
    {if reportbody.shortname:
        {set body to reportbody.shortname}
    |else:
        {set body to slice(body, len(system)+1)}
    }

    {set type to reportbody.type}
    {set body_type to type}

    {_ Remove Ring designation if it's a ring _}
    {if find(body, "Ring") > -1:
        {set body to split(body, " ")}
        {set body to join(except(body, [len(body)-1:"", len(body)-2:""]), " ")}
    }

    {_ Replacing spaces with dashes makes all letters spoken as alphabetic letters _}
    {set body to ucase(join(split(body, " "), "-"))}

    {if find(body, "+") > -1:
        {_ For scans that list two planets. Probably redundant with the new scanning. _}
        {set body to List(split(body, "+"))}
        {set body_type to "Planets"}
    |elif type = "Planet" && (parent = "Planet" || ord(slice(body, len(body)-1)) > 64):
        {set body_type to "Moon"}
    }

    {set body to cat(body_type, "-", body)}
}

Body report.script:
{_ Fetch from context _}
{set reportbody to BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_body_system"))}
{if !reportbody: 
    {set reportbody to BodyDetails(GetState("eddi_context_body_name"))}
}

{if !reportbody.name || reportbody.name = "":
    No consigo entender a que cuerpo estelar te refieres.

|elif reportbody.planettype = "Ninguno":
    Esto no es ningún planeta, Comandante.

|else:
	{set report to []}
	{set cuerpo to "El "}
    {if reportbody.shortname != reportbody.name: {set cuerpo to cat(cuerpo, OneOf("cuerpo", "cuerpo estelar", "objeto"))} }
    {set cuerpo to cat(cuerpo, " ", P(reportbody.shortname, 'body'), ", es un ", P(reportbody.planettype, 'planettype'))}
	
	{if reportGravity:
    	{if reportbody.gravity < 0.5:
        	{set report to cat(report, ["de baja gravedad"])}
    	|elif reportbody.gravity <2:
        	{set report to cat(report, ["de media gravedad"])}
    	|elif reportbody.gravity <4:
        	{set report to cat(report, ["de alta gravedad"])}
    	|else:
        	{set report to cat(report, ["de extrema gravedad"])}
    	}
    }

	{if reportRings:
    	{if len(reportbody.rings) = 1:
    		{set report to cat(report, [OneOf("con anillo de asteroides", "anillado")])}
	    |elif len(reportbody.rings) > 1:
    		{set report to cat(report, [cat("con ", len(reportbody.rings), " anillos de asteroides")])}
    	}
    }

    {if reportTerraformState && reportbody.terraformstate != "no terraformable": 
		{set report to cat(report, [reportbody.terraformstate])}
    }

	{if reportLandable:
    	{if reportbody.landable:
    		{set report to cat(report, [OneOf("{OneOf('adecuado', 'apto')} para aterrizar", "aterrizable")])}
	    |else:
			{set report to cat(report, [OneOf("{OneOf('inadecuado', 'no apto')} para aterrizar", "no aterrizable")])}
    	}
    }

    {if reportRotation:
    	{set rotacion to cat(Occasionally(2, "que"), " tiene un período de rotación ")}
        {if reportbody.rotationalperiod < -20.0:
        	{set rotacion to cat(rotacion, OneOf("retrógrado", "inverso"), " ", OneOf(" bajo", " lento"))}
        |elif reportbody.rotationalperiod < -0.5:
            {set rotacion to cat(rotacion, OneOf("retrógrado", "inverso"))}
        |elif reportbody.rotationalperiod < 0:
            {set rotacion to cat(rotacion, OneOf("retrógrado", "inverso"), " ", OneOf(" alto", " rápido"))}
        |elif reportbody.rotationalperiod <= 0.5:
            {set rotacion to cat(rotacion, OneOf("alto", "rápido"))}
        |elif reportbody.rotationalperiod > 20.0:
            {set rotacion to cat(rotacion, OneOf("bajo", "lento"))}
        }
        {set rotacion to cat(rotacion, " de ")}
        {if abs(reportbody.rotationalperiod) < 1:
        	{set horas to round(24 * (abs(reportbody.rotationalperiod)))}
        	{if horas != 1: {set s to "s"}}
        	{set rotacion to cat(rotacion, horas, " hora", s)}
        |else:
        	{if floor(abs(reportbody.rotationalperiod)) != 1:
        		{set s to "s"}
        		{set rotacion to cat(rotacion, floor(abs(reportbody.rotationalperiod)), " días")}
        	|else:
        		{set rotacion to cat(rotacion, " un dia")}
        	}
        	{set horas to round(24 * (abs(reportbody.rotationalperiod) - floor(abs(reportbody.rotationalperiod))))}
        	{if horas:
        		{set s to ""}
        		{if horas != 1: {set s to "s"}}
        		{set rotacion to cat(rotacion, " y ", horas, " hora", s)}
        	}
        }
        {set rotacion to cat(rotacion, " terrestre", s)}
	    {if reportbody.tidallylocked = true:
    		{set rotacion to cat(rotacion, " con ", OneOf("acoplamiento de marea", "rotación sincrónica"))}
    	}
    	{set report to cat(report, [rotacion])}
    }
    
    {_ comienza el reporte _}

	{set reportCount to len(report)}
	{set cur to 0}
    {while cur < reportCount:
        {if cur = reportCount - 1:
        	{if find(report[cur + 1], "i") = 0:
        		{set reportReport to cat(reportReport, Pause(500), " e ")}
        	|else:
        		{set reportReport to cat(reportReport, Pause(500), " y ")}
        	}
        |elif cur = floor(reportCount/2):
        	{set reportReport to cat(reportReport, ", ", Pause(200))}
        |else:
        	{set reportReport to cat(reportReport, ", ")}
		}
		{set reportReport to cat(reportReport, report[cur])}
        {set cur to cur + 1}
	}
	
	{cuerpo} {reportReport}.
	
	{if reportVolcanism: {F("Body volcanism report")} }

 	{if reportAtmosphere: {F("Body atmosphere report")} }

	{if reportMaterials = 1 || (reportMaterials = 2 && reportbody.volcanism):
		{F("Body materials report")}
	}
}

Body report summary.script:
{_ Define functions for converting temperature scales _}
{set ToCelsius(kelvin) to: {return kelvin - 273.15}}
{set ToFahrenheit(kelvin) to: {return (kelvin-273.15)*9/5 + 32}}
{set TempCalc(kelvin) to: 
    {if reportSummaryTemperature = 1:
    	{return round(kelvin))}
    |elif reportSummaryTemperature = 2:
    	{return ToCelsius(round(kelvin))}
    |elif reportSummaryTemperature = 3:
    	{return ToFahrenheit(round(kelvin))}
    }
}

{_ Name our temperature scale _}
{if reportSummaryTemperature = 1:
	{set tempscale to "Kelvin"}
|elif reportSummaryTemperature = 2:
	{set tempscale to cat("grados ", Occasionally(2, 'centígrados'))}
|elif reportSummaryTemperature = 3:
	{set tempscale to  "grados Fahrenheit"}
}

{_ Fetch from context _}
{set reportbody to BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_body_system"))}
{if !reportbody: 
    {set reportbody to BodyDetails(GetState("eddi_context_body_name"))}
}

{_ Begin speaking _}
{if !reportbody.name || reportbody.name = "":

    No tengo claro a que cuerpo estelar se refiere.

|else:
{_ Begin processing _}
	{set notables to []}
	
	{if reportInterestingOrbits:
	    {set orbitNotables to []}
	    {if reportbody.eccentricityprobability > 99.9 && reportExtremes > 0:
	        {set orbitNotables to cat(orbitNotables, [cat("increiblemente ", OneOf("elíptica", "ovalada", "alargada"))])}
	    |elif reportbody.eccentricityprobability > 99 && reportExtremes > 1:
	        {set orbitNotables to cat(orbitNotables, [cat("extremadamente ", OneOf("elíptica", "ovalada", "alargada"))])}
	    |elif reportbody.eccentricityprobability > 97 && reportExtremes > 2:
	        {set orbitNotables to cat(orbitNotables, [cat("inusualmente ", OneOf("elíptica", "ovalada", "alargada"))])}
		}
	
	    {if reportbody.inclinationprobability > 99.9 && reportExtremes > 0:
	        {set orbitNotables to cat(orbitNotables, ["increiblemente inclinada"])}
	    |elif reportbody.inclinationprobability > 99 && reportExtremes > 1:
	        {set orbitNotables to cat(orbitNotables, ["extremadamente inclinada"])}
	    |elif reportbody.inclinationprobability > 97 && reportExtremes > 2:
	        {set orbitNotables to cat(orbitNotables, ["inusualmente inclinada"])}
	    }
	
	    {if reportbody.orbitalperiod < 0:
	    	{set orbitRetrograde to " retrógrada"}
	    }
	    {if reportbody.orbitalperiodprobability < 0.1 && reportExtremes > 0:
	        {set orbitNotables to cat(orbitNotables, ["increiblemente rápida"])}
	    |elif reportbody.orbitalperiodprobability < 1 && reportExtremes > 1:
	        {set orbitNotables to cat(orbitNotables, ["extremadamente rápida"])}
	    |elif reportbody.orbitalperiodprobability < 3 && reportExtremes > 2:
	        {set orbitNotables to cat(orbitNotables, ["inusualmente rápida"])}
		}

		{if len(orbitNotables):
			{set notables to cat(notables, [cat("una órbita" , orbitRetrograde, " ", List(orbitNotables))])}
		}
	}
	
	{if reportRapidRotation:
		{if reportbody.rotationalperiod < 0:
			{set rotationRetrograde to "retrógrado"}
		}
	    {if reportbody.rotationalperiodprobability < 0.1 && reportExtremes > 0:
	        {set rotationNotables to cat("un período de rotación ", rotationRetrograde, " increiblemente corto")}
	    |elif reportbody.rotationalperiodprobability < 1 && reportExtremes > 1:
	        {set rotationNotables to cat("un período de rotación ", rotationRetrograde, " extremadamente corto")}
	    |elif reportbody.rotationalperiodprobability < 3 && reportExtremes > 2:
	        {set rotationNotables to cat("un período de rotación ", rotationRetrograde, " inusualmente corto")}
	    }
	    {if rotationNotables:
	        {set rotationalperiod to humaniza(abs(reportbody.rotationalperiod))}
	    	{set notables to cat(notables, [cat(rotationNotables, " ", Occasionally(2, "de {rotationalperiod} días"))])}
	    }
	}
	
	{if reportNotableMass:
	    {if reportbody.massprobability < 0.1 && reportExtremes > 0:
	        {set massNotables to cat("una masa ", OneOf('extremadamente pequeña', 'minúscula muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.massprobability < 1 && reportExtremes > 1:
	        {set massNotables to cat("una masa ", OneOf('inusualmente pequeña', 'muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.massprobability < 3 && reportExtremes > 2:
	        {set massNotables to cat("una masa ", OneOf('relativamente pequeña', 'por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.massprobability > 99.9 && reportExtremes > 0:
	        {set massNotables to cat("una masa ", OneOf('extremadamente grande', 'enorme muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.massprobability > 99 && reportExtremes > 1:
	        {set massNotables to cat("una masa ", OneOf('inusualmente grande', 'muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.massprobability > 97 && reportExtremes > 2:
	        {set massNotables to cat("una masa ", OneOf('relativamente grande', 'por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    }
	    {if massNotables:
	        {set earthmass to humaniza(round(reportbody.earthmass, 1))}
	        {set notables to cat(notables, [cat(massNotables, " ", Occasionally(2, "de {earthmass} veces la de la Tierra"))])}
	    }
	}

	{if reportNotableRadius:
		{if reportbody.radiusprobability < 0.1 && reportExtremes > 0:
	        {set radiusNotables to cat("un radio ", OneOf('extremadamente pequeño', 'minúsculo muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.radiusprobability < 1 && reportExtremes > 1:
	        {set radiusNotables to cat("un radio ", OneOf('inusualmente pequeño', 'muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.radiusprobability < 3 && reportExtremes > 2:
	        {set radiusNotables to cat("un radio ", OneOf('relativamente pequeño', 'por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.radiusprobability > 99.9 && reportExtremes > 0:
	        {set radiusNotables to cat("un radio ", OneOf('extremadamente grande', 'enorme muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.radiusprobability > 99 && reportExtremes > 1:
	        {set radiusNotables to cat("un radio ", OneOf('inusualmente grande', 'muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.radiusprobability > 97 && reportExtremes > 2:
	        {set radiusNotables to cat("un radio ", OneOf('relativamente grande', 'por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    }
	    {set radiusValue to humaniza(round(reportbody.radius))}
		{if radiusNotables:
			{set notables to cat(notables, [cat(radiusNotables, " ", Occasionally(2, "de {radiusValue} {de} kilómetros"))])}
	    }
	}
	
	{if reportSummaryTemperature:
	    {if reportbody.tempprobability < 0.1 && reportExtremes > 0:
	        {set tempNotables to cat("una temperatura ", Occasionally(2, 'superficial'), " ", OneOf('extremadamente baja', 'extrema muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.tempprobability < 1 && reportExtremes > 1:
	        {set tempNotables to cat("una temperatura ", Occasionally(2, 'superficial'), " ", OneOf('inusualmente baja', 'muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.tempprobability < 3 && reportExtremes > 2:
	        {set tempNotables to cat("una temperatura ", Occasionally(2, 'superficial'), " ", OneOf('relativamente baja', 'por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.tempprobability > 99.9 && reportExtremes > 0:
	        {set tempNotables to cat("una temperatura ", Occasionally(2, 'superficial'), " ", OneOf('extremadamente alta', 'extrema muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.tempprobability > 99 && reportExtremes > 1:
	        {set tempNotables to cat("una temperatura ", Occasionally(2, 'superficial'), " ", OneOf('inusualmente alta', 'muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.tempprobability > 97 && reportExtremes > 2:
	        {set tempNotables to cat("una temperatura ", Occasionally(2, 'superficial'), " ", OneOf('relativamente alta', 'por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    }
	    {if tempNotables:
	        {set temperature to humaniza(TempCalc(reportbody.temperature))}
	    	{set notables to cat(notables, [cat(tempNotables, " ", Occasionally(2, "de {temperature} {tempscale}"))])}
	    }
	}
	
	{if reportNotableGravity && reportbody.landable:
	    {if reportbody.gravityprobability < 0.1 && reportExtremes > 0:
	        {set gravNotables to cat("una gravedad ", OneOf('extremadamente baja', 'ínfima muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
