	        {set gravNotables to cat("una gravedad ", OneOf('inusualmente baja', 'muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.gravityprobability < 3 && reportExtremes > 2:
	        {set gravNotables to cat("una gravedad ", OneOf('relativamente baja', 'por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
