	        {set gravNotables to cat("una gravedad ", OneOf('extremadamente alta', 'extrema muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.gravityprobability > 99 && reportExtremes > 1:
	        {set gravNotables to cat("una gravedad ", OneOf('inusualmente alta', 'muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.gravityprobability > 97 && reportExtremes > 2:
