	{if reportNotableDensity:
	    {if reportbody.densityprobability < 0.1 && reportExtremes > 0:
	        {set densityNotables to cat("una densidad ", OneOf('extremadamente baja', 'extrema muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.densityprobability < 1 && reportExtremes > 1:
	        {set densityNotables to cat("una densidad ", OneOf('inusualmente baja', 'muy por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.densityprobability < 3 && reportExtremes > 2:
	        {set densityNotables to cat("una densidad ", OneOf('relativamente baja', 'por debajo de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.densityprobability > 99.9 && reportExtremes > 0:
	        {set densityNotables to cat("una densidad ", OneOf('extremadamente alta', 'extrema muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.densityprobability > 99 && reportExtremes > 1:
	        {set densityNotables to cat("una densidad ", OneOf('inusualmente alta', 'muy por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    |elif reportbody.densityprobability > 97 && reportExtremes > 2:
	        {set densityNotables to cat("una densidad ", OneOf('relativamente alta', 'por encima de {OneOf("la media", "lo normal", "lo esperable")}'))}
	    }
	    {if densityNotables:
	        {set density to humaniza(round(reportbody.density))}
	    	{set notables to cat(notables, [cat(densityNotables, " ", Occasionally(2, "de {density} kilogramos por metro cúbico"))])}
	    }
	}
	
	{if len(notables) > 1: {set prefijo to Occasionally(2, 'entre los de su tipo')}}
	
	{if reportSummaryVolcanism:
	    {if reportbody.volcanism && reportbody.landable:
	        {set notables to cat(notables, [cat("actividad ", OneOf("volcánica", "tectónica"))])}
	    }
	}
	
	{if reportSummaryRings:
	    {if reportbody.rings:
	        {set ringcompositions to []}
	        {for ring in reportbody.rings:
                {if find(ringcompositions, ring.composition) = -1:
					{set ringcompositions to cat(ringcompositions, [cat(ring.composition)])}
	            }
	        }

	        {if ringcompositions:
	            {if len(ringcompositions) = 1:
                    {set un to "un "}
	                {set rings to "anillo"}
	            |else:
	            	{set un to OneOf("varios", "unos")}
	                {set rings to "anillos"}
	                {set anillos to []}
					{for composicion in ringcompositions:
						{set anillos to cat(anillos, [token(composicion, " ", 0, cat(split(composicion, " ")[0], "s"))])}
					}
					{set ringcompositions to anillos}
	            }
	            {if reportbody.reserves = "Menor":
	                {set reserves to "reservas menores en "}
	            |elif reportbody.reserves = "Común":
	                {set reserves to "reservas comunes en "}
				|elif reportbody.reserves = "Mayor":
					{set reserves to "reservas mayores en "}
				|elif reportbody.reserves = "Prístina":
					{set reserves to cat("reservas ", OneOf('prístinas', 'vírgenes'), " en ")}
				|elif reportbody.reserves = "Agotada":
					{set reserves to "reservas agotadas en "}
	            }
	            {set notables to cat(notables, [cat(reserves, un, " ", rings, " ", List(ringcompositions))])}
	        }
	    }
	}
	
	{if reportSummaryTerraformables:
	    {if reportbody.terraformstate = "Candidato para terraformación":
	        {set notables to cat(notables, [OneOf("potencial para terraformación", "condiciones terraformables")])}
	    |elif reportbody.terraformstate = "Terraformed":
	        {set notables to cat(notables, ["condiciones terraformadas"])}
	    }
	}
	
	{if reportSummaryPlanetTypes:
	    {if reportbody.planettype = "Mundo similar a la Tierra":
	    	{if len(notables) > 1:
	    		{set habitable to "la Tierra"}
	    	|else:
	    		{set habitable to OneOf('esta', 'ella')}
	    	}
	    	{set notables to cat(notables, [cat("condiciones ", OneOf('habitables', 'de habitabilidad'), " como las de ", habitable)])}
	    |elif reportbody.planettype = "Mundo acuático":
	        {set notables to cat(notables, [OneOf("abundante agua líquida", "abundante agua líquida en la superficie")])}
	    }
	}

    {if len(notables):
        {if reportbody.shortname != reportbody.bodyname:
            {if reportbody.bodytype:
             	{if reportbody.bodytype = "Luna":
             		{set report to "La"}
             	|else:
             		{set report to "El"}
             	}
             	{set report to cat(report, " ", reportbody.bodytype)}
            |else:
            	{set report to "El cuerpo"}
            }
            {set report to cat(report, " ", P(reportbody.shortname, "body"))}
        |else:
            {set report to P(reportbody.bodyname, "body")}
        }


		{set sufijo to OneOf('al tener', 'que tiene', 'por tener')}
		{if len(notables) = 1:
			{set sufijo to OneOf('con', 'que tiene')}
        |elif len(notables) = 2:
            {set desc to OneOf("interesante", "a tener en cuenta", "remarcable", "a destacar", "destacable", "a remarcar")}
        |elif len(notables) = 3:
            {set desc to OneOf("muy interesante", "muy a tener en cuenta", "muy remarcable", "muy destacable")}
        |elif len(notables) = 4:
            {set desc to OneOf("excepcional", "como pocos", "extraordinario", "muy peculiar")}
        |elif len(notables) >= 5:
            {set desc to OneOf("sin par", "único", "sin igual")}
        }
        
        {set pausa to OneOf(Occasionally(2,Pause(40)), ',')}
        {set report to cat(report, ", es un ", P(reportbody.planettype,  "planettype"), " ", desc, " ", prefijo, pausa, " ", sufijo, " ", List(notables), ".")}

		{_ Di el informe _}
    	{report}
    }
}
    {if reportSummaryMaterials: {F("Body materials report")}

}

Body scanned.script:
{_ Context }
{SetState('eddi_context_last_subject', 'body')}
{SetState('eddi_context_last_action', 'scan')}
{SetState('eddi_context_body_system', event.systemname)}
{SetState('eddi_context_body_name', event.bodyname)}
{SetState('eddi_context_last_scan_estimated_value', getBodyValue(BodyDetails(event.bodyname, event.systemname)))}
{SetState('eddi_context_last_scan_max_value', getMaxBodyValue(BodyDetails(event.bodyname, event.systemname)))}

{if !GetState("eddi_context_body_lastscan_cooldown"): {SetState('eddi_context_body_lastscan_cooldown', 0)}}


{if ((SecondsSince(GetState("eddi_context_body_lastscan_timestamp")) > GetState("eddi_context_body_lastscan_cooldown")) && (system.totalbodies != system.scannedbodies)) || esValioso(event):
	{_ Establece espera entre escaneos _}
	{SetState('eddi_context_body_lastscan_timestamp', SecondsSince(0))}
										
	{set a to "o"}
	{set cuerpo to cat(OneOf("{event.bodytype} {if event.bodytype = 'Luna': {set a to 'a'}}", "cuerpo {Occasionally(4, OneOf('astronómico', 'estelar'))}", "objeto {Occasionally(4, OneOf('astronómico', 'estelar'))}"), " ", P(event.shortname, "body"))}
	{if reportPlanetType = 1:
		{set a to "o"}
		{set cuerpo to event.planettype}
	|elif reportPlanetType > 1:
		{set cuerpo to OneOf("{event.planettype}{set a to 'o'}", cuerpo)}
	}

	{if event.alreadydiscovered:
		{set report to OneOf(
			'{cuerpo} {Pause(30)} {OneOf("identificad{a}", "analizad{a}", "escanead{a}", "explorad{a}")}.',
			'{OneOf("Escanead{a}", "Identificad{a}", "Detectad{a}", "Analizad{a}", "Explorad{a}")} {cuerpo}.',
			'{OneOf("Escaneo", "Análisis")} de {cuerpo} {Pause(30)} {OneOf("completado", "terminado", "finalizado", "concluido", "realizado")}.',
			'{OneOf("Identificación", "Detección", "Catalogación", "Exploración")} de {cuerpo} {Pause(30)} {OneOf("completada", "terminada", "finalizada", "concluida", "realizada")}.'
		)}
	|else:
		{set report to OneOf(
			'{cuerpo} {Pause(30)} {OneOf("descubiert{a}", "escanead{a} por primera vez", "catalogad{a}", "añadid{a} al mapa galáctico", "añadid{a} a la cartografía universal")}.',
			'{OneOf("Descubiert{a}", "Catalogad{a}")} {cuerpo}.',
			'{OneOf("Escanead{a}", "Identificad{a}")} {cuerpo} {Pause(30)} por primera vez.',
			'Añadid{a} {cuerpo} {Pause(30)} {OneOf("al mapa galáctico", "a la cartografía universal")}.',
			'Primer {OneOf("escaneo", "análisis")} de {cuerpo} {Pause(30)} {OneOf("completado", "terminado", "finalizado", "concluido", "realizado")}.',
			'Primera {OneOf("identificación", "detección", "catalogación")} de {cuerpo} {Pause(30)} {OneOf("completada", "terminada", "finalizada", "concluida", "realizada")}.'
		)}
	}

	{SetState('eddi_context_body_lastscan_cooldown', round(len(report) * 0.05))}
	{report}

	{if ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose":
		{if esValioso(event):
			{Pause(25)}
			{set report to F("Report last scan value")}
			{_ Espera entre escaneos: 0,05 segundos por letra _}
			{SetState('eddi_context_body_lastscan_cooldown', round(GetState('eddi_context_body_lastscan_cooldown') + (len(report) * 0.05)))}
			{report}
		}

		{if reportSummary:
			{Pause(25)}
			{set report to F("Body report summary")}
			{_ Espera entre escaneos: 0,05segundos por letra _}
			{SetState('eddi_context_body_lastscan_cooldown', round(GetState('eddi_context_body_lastscan_cooldown') + (len(report) * 0.05)))}
			{report}
		}
	}
}

