Bond awarded.script:
{_ Bond awarded }
{_ Triggered when you are awarded a combat bond }


{_ Context }
{SetState('eddi_context_last_subject', 'bond')}
{SetState('eddi_context_last_action', 'award')}
{SetState('eddi_context_bond_amount', event.reward)}
{SetState('eddi_context_bond_faction', event.awardingfaction)}

{set reward to humaniza(event.reward)}

{if event.victimfaction = "Thargoid":
	{set report to
		OneOf(
			'{OneOf("Derrotado", "Eliminado", "Derribado", "Abatido")} Targoide',
			'Targoide  {OneOf("derrotado", "eliminado", "derribado", "abatido")}',
			'Baja Targoide confirmada'
		)
	}
|else:
	{set report to
		OneOf(
			'{OneOf("Derrotado", "Eliminado", "Derribado", "Abatido")} {OneOf("enemigo", "rival", "adversario", "oponente")}',
			'{OneOf("Enemigo", "Rival", "Adversario", "Oponente")} {OneOf("derrotado", "eliminado", "derribado", "abatido")}',
			'Baja confirmada'
		)
	}
	{set report to cat(report, Occasionally(4, " de {Occasionally(2, 'la facción')} {P(event.victimfaction, 'faction')}"))}
}

{set report to
	cat(report, "; ",
		OneOf(
			'{OneOf("ganados", "otorgados", "recibidos")} {reward} créditos',
			'{reward} créditos {OneOf("ganados ", "otorgados ", "recibidos ")}'
		),
		" ", Occasionally(2, "en bonos de combate "), Occasionally(4, " de {Occasionally(2, 'la facción')} {P(event.awardingfaction, 'faction')}")
	)
}

{leeTexto(report)}.

{set felicitacion to "{OneOf(
	'Muy bien',
	'Sigue así',
	'Buen trabajo',
	'Bien hecho',
	'Así se hace',
	'No hay quién te pare',
	'A por ellos'
	)} {Occasionally(4, F('Honorific'))}"
}

{if ShirkaSarcastica: {Occasionally(10, Transmit("¡{felicitacion}!"))}}

Bond redeemed.script:
{_ Context }
{SetState('eddi_context_last_subject', 'bond')}
{SetState('eddi_context_last_action', 'redeem')}
{SetState('eddi_context_bond_amount', event.amount)}

{set amount to humaniza(event.amount)}

{set report to cat(OneOf("Cobrados", "Entregados", "Reclamados"), " bonos de combate por valor de ", amount, " ", de, " créditos")} 

{if rand(2):
    {if len(event.rewards) = 1 && len(event.rewards[0].faction) > 0:
        {set report to cat(report, "de ", P(event.rewards[0].faction))}
    |else:
        {set cur to 0}
        {while cur < len(event.rewards):
            {if len(event.rewards[cur].faction) > 0:
                {if cur = 0:
                    {set report to cat(report, ":")}
                |elif cur < len(event.rewards) - 1:
                    {set report to cat(report, ",")} 
                |else:
                    {set report to cat(report, " y")}
                }
                {set report to cat(report, " ", event.rewards[cur].amount, Occasionally(2," créditos"), " de ", P(event.rewards[cur].faction))}
                {set cur to cur + 1}
            }
        }
    }
}

{if event.brokerpercentage && event.brokerpercentage > 0:
    {set report to cat(report, "después de deducir ", OneOf('un pago', 'una comisión'), " de ", round((event.amount / (100 - event.brokerpercentage)) * event.brokerpercentage, 0), " créditos")}
}

{leeTexto(report)}.

