Carrier jump engaged.script:
{_ Context }
{SetState('eddi_context_last_subject', 'carrier jump')}
{SetState('eddi_context_last_action', 'start')}
{SetState('eddi_context_system_name', event.systemname)}
{SetState('eddi_context_system_lastvisit', system.lastVisitSeconds)}

{if event.docked || (status.vehicle = "On Foot" && station.marketId = carrier.Market.marketId):

    {_ Zero the 'remaining jump' context to only use values following this event _}
    {SetState('eddi_context_remaining_jumps', 0)}

    {set reportsystem to SystemDetails(event.systemname)}

    {OneOf("Salto en progreso", "Entrando en hypersalto")} hacia el sistema {P(reportsystem.systemname, "starsystem")}
    {if len(event.shortname) > 0:
        {set reportBody to BodyDetails(event.bodyname, event.systemname)} 
        cerca
        {if len(reportBody.bodytype) > 0: de {reportBody.bodytype} |else: del cuerpo}
        {P(event.shortname, "body")}
    }.

    {if reportsystem.systemname = homesystem.systemname:
        {OneOf("Hogar dulce hogar", "Bienvenido a casa")}, {F("Honorific")}
    |else:       
        {if lastsystem.allegiance && lastsystem.allegiance != "Ninguno" && lastsystem.allegiance != "Independiente" && (!reportsystem.allegiance || reportsystem.allegiance = "Ninguno" || reportsystem.allegiance = "Independiente"):
            {OneOf("estamos abandonando el espacio de", "hemos dejado", "ya no estamos en territorio de")} {lastsystem.allegiance}.
        |elif lastsystem.allegiance && lastsystem.allegiance != reportsystem.allegiance && reportsystem.allegiance && reportsystem.allegiance != "Ninguno":
            {OneOf("ahora estamos en", "hemos llegado a un", "estamos entrando en")} {OneOf("espacio","territorio")}
            {if system.allegiance = "Independiente": de un gobierno independiente
            |elif system.allegiance = "La Alianza": de la Alianza
            |elif system.allegiance = "El Imperio": del Imperio
            |elif system.allegiance = "La Federación": de La Federación
            |elif system.allegiance = "Pirata": Pirata
            |elif system.allegiance = "Guardián": Guardián
            |elif system.allegiance = "Thargoide": Zargoide
            |elif system.allegiance = "Federación de Pilotos": de La Federación de Pilotos
            }.
            {if cmdr.title != "Comandante":
                 El Gobierno local le da la bienvenida {OneOf("como", "con el título de")} {F("Honorific")}.
            }
        }

        {if reportsystem.visits = 0:
            {OneOf("Es nuestra primera visita", "Es la primera vez que estamos", "Nunca antes habíamos estado")} {OneOf("por aquí", "en este sistema")}
        |elif reportsystem.visits = 1:
            {OneOf("Es nuestra segunda visita", "Es la segunda vez que estamos")} {OneOf("por aquí", "en este sistema")}
        |elif reportsystem.visits = 2:
            {Occasionally(2, "{OneOf('Es nuestra tercera visita', 'Es la tercera vez que estamos')} {OneOf('por aquí', 'en este sistema')}")}
        |elif reportsystem.visits = 3:
            {Occasionally(2, "{OneOf('Es nuestra cuarta visita', 'Es la cuarta vez que estamos')} {OneOf('por aquí', 'en este sistema')}")}
        |else:
            {Occasionally(3, "Hemos {OneOf('visitado', 'estado en', 'pasado por')} este sistema {reportsystem.visits} veces")}
        }.

        {if reportsystem.population && reportsystem.population > 0:
            {if SecondsSince(reportsystem.lastVisitSeconds) > 3600:
                {F("System report")}
            }
        |else:
            {OneOf("Este sistema está despoblado", "En este sistema no hay {OneOf('presencia humana', 'asentamientos humanos', 'ninguna colonia humana', 'colonias humanas')}", "No hay {OneOf('presencia humana', 'asentamientos humanos', 'ninguna colonia humana', 'colonias humanas')} en este sistema", "La Humanidad aún no ha colonizado este sistema")}.

        }

        {if reportsystem.distancefromhome:
            {set ReportDistanceFromHome() to:
                {return cat(
                    OneOf('Nos encontramos', 'La posición actual está', 'Actualmente estamos'), " ",
                    OneOf('a unos', 'aproximádamente a', 'a'), " ",
                    Humanise(reportsystem.distancefromhome), " años luz de ",
                    OneOf('casa', '{P(homesystem.name, "starsystem")}'), "."
                )}
            }
            {Occasionally(7, ReportDistanceFromHome())}
        }

        {if reportsystem.comment:
            {OneOf("Ha hecho", "Tiene", "He encontrado")} {OneOf("una anotación", "un comentario", "una observación")} {OneOf("sobre", "acerca de", "en relación a")} este sistema. Dice así: {reportsystem.comment}. Fin de la {OneOf("nota","cita")}.
        }
    }
    
|else:

    {OneOf('Su portanaves', 'Su carrier', 'Su portaflotas', carrier.name)} está llegando al sistema {P(event.systemname, "starsystem")}
    {if event.bodyname != event.systemname && len(event.shortname) > 0:
        {set reportBody to BodyDetails(event.bodyname, event.systemname)} 
        cerca
        {if len(reportBody.bodytype) > 0: de {reportBody.bodytype} |else: del cuerpo}
        {P(event.shortname, "body")}
    }.
    
}

