Carrier service changed.script:
{set GetService() to:
    {if event.invariantService = "Bartender":
        {return OneOf("el bar", "el servicio de barra", "el servicio de camarero")}
    |elif event.invariantService = "Black Market":
        {return OneOf("{Occasionally(2, 'los servicios comerciales d')}el mercado negro", "el mercado de {Occasionally(2, 'productos de')} contrabando")}
    |elif event.invariantService = "Commodities":
        {return OneOf("el mercado de productos básicos", "los servicios de comercio de productos básicos")}
    |elif event.invariantService = "Exploration":
        {return OneOf("el servicio de Cartografía {OneOf('Universal', 'Estelar')}", "los servicios de Cartografía {OneOf('Universal', 'Estelar')}")}
    |elif event.invariantService = "Outfitting":
        {return OneOf("los servicios de equipamiento", "el servicio de equipamiento")}
    |elif event.invariantService = "Pioneer Supplies":
        {return OneOf("Payoner Suplies", "los servicios de equipamiento personal")}
    |elif event.invariantService = "Rearm":
        {return OneOf("el servicio de {OneOf('armería', 'rearme')}", "los servicios de {OneOf('la armería', 'rearme')}")}
    |elif event.invariantService = "Redemption Office":
        {return cat("el servicio de ", OneOf('Redención', 'cobro'), " de ", OneOf('Bonos', 'Vales'), " de combate")}
    |elif event.invariantService = "Repair":
        {return OneOf("el {OneOf('servicio', 'taller')} de reparaci{OneOf('ón', 'ones')}", "los servicios de reparaci{OneOf('ón', 'ones')}")}
    |elif event.invariantService = "Shipyard":
        {return OneOf("el astillero para {OneOf('invitados', 'huéspedes')}", "los servicios de astillero para {OneOf('invitados', 'huéspedes')}")}
    |elif event.invariantService = "Tritium Depot":
        {return OneOf("el depósito de Tritio", "los servicios de reabastecimiento de Tritio")}
    |elif event.invariantService = "Vista Genomics":
        {return OneOf("Vista GENOMICS", "los servicios de Exobiología")}
    |else:
        {return event.service}
    }
}

{if event.operation = "Replace":
    {_ This is a carrier crew reassignment }
    {if event.invariantService = "Captain":
        {event.crew} 
        {OneOf("ha tomado el mando de", "estará a cargo del puente de mando de")}
        {if carrier && len(carrier.name) > 0:
            {carrier.name}
        |else:
            su {OneOf("portanaves", "portaflotas", "CARRIER")}.
        }
    |else:
    	{set service to GetService()}
    	{if find(service, "el") = 0:
        	{set de to "d"}
    	|else:
        	{set de to "de "}
    	}
        {event.crew} {OneOf("ha tomado el mando", "estará a cargo")} {de}{service}
    }     
|elif len(event.operation) > 0:
    {set service to GetService()}
    {service}
    {if find(service, "servicios") > -1:
    	{set s to "s"}
        {OneOf("han sido", "se han{set s to ''}")}
    |else:
    	{set s to ""}
        {OneOf("ha sido", "se ha")}
    }
    {if event.operation = "Activate":
        {OneOf("activado{s}", "abierto{s}")}
    |elif event.operation = "Deactivate":
        {OneOf("desactivado{s}", "cerrado{s}")}
    |elif event.operation = "Pause":
        {OneOf("pausado{s}", "suspendido{s}")}
    |elif event.operation = "Resume":
        {OneOf("restaurado{s}", "restablecido{s}")}
    |else:
        {event.operation}
    }.
}

