Commodity purchase check.script:
{for commodity in station.commodities:
    {if commodity.stock > 0 && commodity.buyprice < commodity.avgprice:
        {set discount to commodity.avgprice - commodity.buyprice}
        {if discount > best.avgprice - best.buyprice:
            {set thirdbest to secondbest}
            {set secondbest to best}
            {set best to commodity}
        |elif discount > secondbest.avgprice - secondbest.buyprice:
            {set thirdbest to secondbest}
            {set secondbest to commodity}
        |elif discount > thirdbest.avgprice - thirdbest.buyprice:
            {set thirdbest to commodity}
        }
    }
}

{if best:
    El mercado local está vendiendo {best.name} al mejor precio
    {if secondbest:
       , además de {secondbest.name}
        {if thirdbest:
            y {thirdbest.name}
        }
    }
.}

Commodity purchased.script:
{_ Context }
{SetState('eddi_context_last_subject', 'commodity')}
{SetState('eddi_context_last_action', 'purchase')}
{SetState('eddi_context_commodity_name', event.commodity)}
{SetState('eddi_context_commodity_amount', event.amount)}

{_ If cargo is full, wait for COVAS to inform you _}
{set cargocarried to 0}
{if len(inventory) > 0:
    {for cargo in inventory:
        {set cargocarried to cargocarried + cargo.total}
	}
}
{if cargocarried = ship.cargocapacity: {Pause(3500)}}

{if event.amount > 1: {set s to "s"}}

{_ Cambiamos las toneladas/unidades por lotes/grupos en el caso de que sean esclavos _}
{if find(lcase(event.commodity), "esclavos") != -1:
	{set unidad to OneOf("grupo", "lote")}
	{set unidades to OneOf("grupos", "lotes")}
	{set un to "un"}{set la to "el"}
	{set report to OneOf('Acabas de adquirir', 'Has adquirido', 'Adquirido{s}')}
|else:
	{set unidad to OneOf("tonelada", "unidad")}
	{set unidades to OneOf("toneladas", "unidades")}
	{set un to "una"}{set la to "la"}
	{set report to OneOf('Acabas de comprar', 'Has comprado', 'Comprada{s}')}
}

{if event.amount > 1:
	{set report to cat(report, " ", event.amount, " ", unidades, " de ", corrigeTexto(event.commodity), ", a ", humaniza(event.price), " ", de, " créditos ", OneOf('por', la), " ", unidad, ".")}
	{set total to humaniza(event.amount * event.price)}
	{set random to rand(3)}
	{if random = 0:
		{set report to cat(report, " El ", OneOf('importe', 'coste', 'valor'))}
		{if rand(1):
            {set report to cat(report, " total es de ", total, " ", de, " créditos")}
		|else:
            {set report to cat(report, " es de ", total, " ", de, " créditos en total")}
		}
	|elif random = 1:
		{set report to cat(report, " ", OneOf('Con un', 'Por'), " ", OneOf('importe', 'coste', 'valor'))}
		{if rand(1):
            {set report to cat(report, " ", Occasionally(2, 'total'), " de ", total," ", de, " créditos")}
		|else:
            {set report to cat(report, " de ", total, " ", de, " créditos en total")}
		}
	|elif random = 2:
		{set report to cat(report, " Por un total de ", total, " ", de, " créditos")}
	}
|else:
	{set report to cat(report, " ", un, " ", unidad, " de ", corrigeTexto(event.commodity), " por ", humaniza(event.price), " ", de, " créditos")}
}

{leeTexto(report)}.

