Commodity sale check.script:
{_ Function to find a given commodity in market}
{set market(commodity) to:
    {for marketcommodity in station.commodities:
        {if marketcommodity.name = commodity.name:
            {return marketcommodity}
        }
    }
}

{set totalprofit to 0}
{set missioncommodities to []}
{set profitablecommodities to []}
{set stolencommodities to []}
{set unknowncommodities to []}

{for cargo in inventory:
    {if cargo.name != 'Drón' && cargo.total > 0:
        {set marketcommodity to market(cargo.commodity)}
        {set profit to (marketcommodity.sellprice - cargo.price) * cargo.owned}
        {if cargo.owned > 0 && marketcommodity && profit > 0:
            {set profitablecommodities to cat(profitablecommodities, [cargo.commodity])}
            {set totalprofit to totalprofit + profit}
        |elif cargo.owned > 0 && !marketcommodity && lcase(cargo.category) != "powerplay":
            {set unknowncommodities to cat(unknowncommodities, [cargo.commodity])}
        |elif cargo.stolen > 0:
            {set stolencommodities to cat(stolencommodities, [cargo.commodity])}
        |elif cargo.haulage > 0:
            {set missioncommodities to cat(missioncommodities, [cargo.commodity])}
        }
    }
}

{_ Let the user know about any commodities they can sell here for a profit}
{if len(profitablecommodities) > 0:
	{set cargocommodities to []}
	{for cargo in profitablecommodities:
		{set cargocommodities to cat(cargocommodities, [corrigeTexto(cargo.name)])}
	}

	{creaArticulos(profitablecommodities[0].name)}
    Puede vender su{s} {List(cargocommodities)}
    {set totalprofit to humaniza(totalprofit)}
    {Occasionally(3, ",")} con {OneOf("un beneficio", "una rentabilidad")} {Occasionally(4, "total")} de {totalprofit} {de} créditos.
}

{_ Let the user know about any commodities about which we are unsure if they can sell for a profit}
{if len(unknowncommodities) > 0:
	{set cargocommodities to []}
	{for cargo in unknowncommodities:
		{set cargocommodities to cat(cargocommodities, [corrigeTexto(cargo.name)])}
	}

    {if len(profitablecommodities) > 0: {OneOf("Además", "también")}}
    {creaArticulos(unknowncommodities[0].name)}
    Podría vender su{s} {List(cargocommodities)}
    , aunque mejor verifique el margen de {OneOf("beneficio", "rentabilidad")} de ést{e}{when(len(unknowncommodities) > 1, "s", "o")} antes de confirmar la transacción.
}

{_ Let the user know about any stolen commodities they can sell on the black market}
{if len(stolencommodities) > 0:
    {if station.hasblackmarket:
    	{set cargocommodities to []}
		{for cargo in stolencommodities:
			{set cargocommodities to cat(cargocommodities, [corrigeTexto(cargo.name)])}
		}
        Aquí puede deshacerse de las mercancias robadas {List(cargocommodities)}.
    |else:
        Recuerde que llevamos a bordo {OneOf("mercancía robada", "bienes robados", "cargamento robado", "bultos marcados con propiedad ajena")}.
    }
}

{_ Let the user know about any mission related commodities they can sell on the black market}
{if len(missioncommodities) > 0 && station.hasblackmarket:
	{set cargocommodities to []}
	{for cargo in missioncommodities:
		{set cargocommodities to cat(cargocommodities, [corrigeTexto(cargo.name)])}
	}
    Puede {when(len(stolencommodities) > 0, "además")} {OneOf('descargar', 'entregar')} aquí sus paquetes de misión {List(cargocommodities)}.
}

Commodity sold.script:
{_ Context _}
{SetState('eddi_context_last_subject', 'commodity')}
{SetState('eddi_context_last_action', 'sell')}
{SetState('eddi_context_commodity_name', event.commodity)}
{SetState('eddi_context_commodity_amount', event.amount)}

{if event.amount > 1: {set s to "s"}}

{_ Cambiamos las toneladas/unidades por lotes/grupos en el caso de que sean esclavos _}
{if find(lcase(event.commodity), "esclavos") != -1:
	{if slaver:
		{set unidades to OneOf("grupos", "lotes")}
		{set unidad to OneOf("grupo", "lote")}
		{set report to OneOf('Acabas de vender', 'Has vendido', 'Vendido{s}')}
	|else:
		{set unidades to "grupos"}
		{set unidad to "grupo"}
		{set report to OneOf('Acabas de liberar', 'Has liberado', 'Liberado{s}')}
	}
	{set un to "un"}{set la to "el"}
|else:
	{set unidades to OneOf("toneladas", "unidades")}
	{set unidad to OneOf("tonelada", "unidad")}
	{set un to "una"}{set la to "la"}
	{set report to OneOf('Acabas de vender', 'Has vendido', 'vendida{s}')}
}

{set total to humaniza(event.amount * event.price)}

{if s:
	{set report to cat(report, " ", event.amount, " ", unidades, " de ", corrigeTexto(event.commodity))}
	{set random to rand(3)}
	{if random = 0:
		{set report to cat(report, ", el ", OneOf('importe', 'valor'))}
		{if rand(1): {set report to cat(report, " total es de ", total, " ", de, " créditos")}
		|else: {set report to cat(report, " es de ", total, " ", de, " créditos en total")}
		}
	|elif random = 1:
		{set report to cat(report, ", ", OneOf('con un', 'por'), " ", OneOf('importe', 'valor'))}
		{if rand(1): {set report to cat(report, " ", Occasionally(2, 'total'), " de ", total, " ", de, " créditos")}
		|else: {set report to cat(report, " de ", total, " ", de, " créditos en total")}
		}
	|elif random = 2:
		{set report to cat(report, ", por un total de ", total, " ", de, " créditos")}
	}
|else:
	{set report to cat(report, " ", un, " ", unidad, " de ", corrigeTexto(event.commodity), " por ", total, " ", de, " créditos")}
}

{_ Report the profit, if any _}
{if event.price != event.profit:
	{set beneficio to humaniza(abs(event.profit * event.amount))}
    {if event.profit < 0:
		{set report to cat(report, ", con una pérdida en la ", OneOf('transacción', 'operación'), " de ", beneficio, " ", de, " créditos.")}
    |elif event.profit = 0:
		{set report to cat(report, ", ", OneOf('al', 'el'), " mismo ", OneOf('precio', 'coste'), " que ", OneOf('su compra', 'se compró'))}
    |else:
    	{set report to cat(report, ", ", Occasionally(2, 'y'), " con ", OneOf('un beneficio', 'una rentabilidad'), " ", Occasionally(2, 'final'), " de ", beneficio, " ", de, " créditos")}
    }
}

{leeTexto(report)}.

