Crime check station.script:
{_ Crime check station _}
{_ Check claims, fines & bounties related to the current station _}

{set CheckPad(current_station) to:
    {set largest_pad to lcase(current_station.largestpad)}
    {if largest_pad != "ninguno":
        {if largest_pad = "grande":
        	{return true}
        |elif largest_pad = "mediano":
            {if lcase(ship.size) = "grande":
            	{return false}
            |else:
            	{return true}
        	}
        }
        {if lcase(ship.size) = "pequeño":
        	{return true}
        }
    }
    {return false}
}

{set HasService(service) to:
    {if CheckPad(current_station):
        {if find(current_station.stationservices, service) > -1:
            {return true}
        |else:
            {return false}
        }
    |else: {return false}
    }
}

{if GetState("eddi_context_body_type") = 'Estación' && station.name && station.name != "":
    {set current_station to StationDetails(station.name)}
    {set system_claim_records to []}
    {set system_claims to 0}
    {set system_fine_records to []}
    {set system_fines to 0}
    {set system_bounty_records to []}
    {set system_bounties to 0}
    {set interstellar_fine_records to []}
    {set interstellar_fines to 0}
    {set interstellar_bounty_records to []}
    {set interstellar_bounties to 0}  

    {for record in criminalrecord:
    	{set factionMatched to false}
        {for faction in system.factions:
            {if record.faction = faction.name:
            
            	{_ This record can be connected to a local faction }
                {set factionMatched to true}

                {if record.claims > 0:
                    {set system_claim_records to cat(system_claim_records, [record.faction])}
                    {set system_claims to system_claims + record.claims}
                }

				{set reports to record.finesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
						{set system_fines to system_fines + report.amount}
						{if find(system_fine_records, record.faction) < 0:
							{set system_fine_records to cat(system_fine_records, [record.faction])}
                        }
                    }
                }

                {set reports to record.bountiesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
						{set system_bounties to system_bounties + report.amount}
						{if find(system_bounty_records, record.faction) < 0:
							{set system_bounty_records to cat(system_bounty_records, [record.faction])}
                        }
                    }
                }
            }
        }

		{if !factionMatched:
        
            {_ This record requires interstellar factors }
            
			{set reports to record.finesIncurred}
			{if len(reports) > 0:
				{for report in reports:
					{set interstellar_fines to interstellar_fines + report.amount}
					{if find(interstellar_fine_records, record.faction) < 0:
						{set interstellar_fine_records to cat(interstellar_fine_records, [record.faction])}
					}
				}
			}

			{set reports to record.bountiesIncurred}
			{if len(reports) > 0:
				{for report in reports:
					{set interstellar_bounties to interstellar_bounties + report.amount}
					{if find(interstellar_bounty_records, record.faction) < 0:
						{set interstellar_bounty_records to cat(interstellar_bounty_records, [record.faction])}
					}
				}
			}
		}
    }

    {set canPaySystemBounties to system_bounties > 0 && suggestSurrender && HasService("Contactos")}
    {set canPaySystemFines to system_fines > 0 && HasService("Contactos")}
    {set canPayInterstellarBounties to interstellar_bounties > 0 && HasService("Contacto de Interstellar Factors")}
    {set canPayInterstellarFines to interstellar_fines > 0 && HasService("Contacto de Interstellar Factors")}
    {set canRedeemClaims to system_claims > 0 && HasService("Contactos")}
    
	{if canPaySystemBounties || canPaySystemFines || canRedeemClaims || canPayInterstellarBounties || canPayInterstellarFines:
        {set actions to []}
        {if canPaySystemBounties || canPayInterstellarBounties:
            {set action to:
                {OneOf("limpiar", "redimir")}
                {if len(system_bounty_records) + len(interstellar_bounty_records) = 1:
                	su recompensa 
                	{set es to ""}{set s to ""}
                |else:
                	sus recompensas 
                	{set es to "es"}{set s to "s"}
                }
                {if canPaySystemBounties: local{es} }
                {if canPaySystemBounties && canPayInterstellarBounties: e }
                {if canPayInterstellarBounties: interestelar{es} }
                {Occasionally(3, "pendiente{s}")} 
                
            }
            {set actions to cat(actions, [action])}            
        }
        {if canPaySystemFines || canPayInterstellarFines: 
            {set action to:
                {OneOf("pagar", "redimir")}
                {if len(system_fine_records) + len(interstellar_fine_records) = 1:
                	su multa
                	{set es to ""}{set s to ""}
                |else:
                	sus multas
                	{set es to "es"}{set s to "s"}
                }
                {if canPaySystemFines: local{es} }
                {if canPaySystemFines && canPayInterstellarFines: e }
                {if canPayInterstellarFines: interestelar{es} }
                {Occasionally(3, "pendiente{s}")}                
            }
            {set actions to cat(actions, [action])}            
        }
        {if canRedeemClaims:
            {set action to:
                {OneOf("cobrar", "reclamar")}
                {if len(system_claim_records) = 1:
                	{OneOf("su recompensa", "su retribución", "su remuneración")}
                	{set s to ""}
                |else:
                	{OneOf("sus recompensas", "sus retribuciones", "sus remuneraciones")}
                	{set s to "s"}
                }
                {Occasionally(3, "pendiente{s}{Pause(30)}")} 
                por los {OneOf("servicios", "trabajos")} {OneOf("prestados como", "de")} cazarrecompensas
            }
            {set actions to cat(actions, [action])}
        }
        {OneOf("Hay instalaciones legales {OneOf('en esta estación', 'aquí', '')} para poder",
        	"{OneOf('en esta estación', 'aquí')} podría",
        	"{OneOf('Esta estación tiene', 'Aquí encontrará')} los servicios necesarios para")
        } {List(actions)}.
    }
}

Crime check system.script:
{_ Crime check system }
{_ Check claims, fines & bounties related to the current system }

{set system_claim_records to []}
{set system_claims to 0}
{set system_fine_records to []}
{set system_fines to 0}
{set system_bounty_records to []}
{set system_bounties to 0}
{set interstellar_fine_records to []}
{set interstellar_fines to 0}
{set interstellar_bounty_records to []}
{set interstellar_bounties to 0}
{set wanted_faction_count to 0}

{for record in criminalrecord:
    {set factionMatched to false}
    {for faction in system.factions:
        {if record.faction = faction.name:

            {_ This record pertains to a faction which is present in the system }
            {set factionMatched to true}
                         
            {if record.claims > 0:
                {set system_claim_records to cat(system_claim_records, [record.faction])}
                {set system_claims to system_claims + record.claims}
            }

            {set reports to record.finesIncurred}
            {if len(reports) > 0:
                {for report in reports:
                    {set system_fines to system_fines + report.amount}
                    {if find(system_fine_records, record.faction) < 0:
                        {set system_fine_records to cat(system_fine_records, [record.faction])}
                        {if find(system_bounty_records, record.faction) < 0:
                            {set wanted_faction_count to wanted_faction_count + 1}
                        }
                    }
                }
            }

            {set reports to record.bountiesIncurred}
            {if len(reports) > 0:
                {for report in reports:
                    {set system_bounties to system_bounties + report.amount}
                    {if find(system_bounty_records, record.faction) < 0:
                        {set system_bounty_records to cat(system_bounty_records, [record.faction])}
                        {if find(system_fine_records, record.faction) < 0:
                            {set wanted_faction_count to wanted_faction_count + 1}
                        }
                    }
                }
            }
        }
    }
    
    {if !factionMatched:

        {_ This record pertains to a faction not present in the system }
        {set reports to record.finesIncurred}
        {if len(reports) > 0:
            {for report in reports:
                {set interstellar_fines to interstellar_fines + report.amount}
                {if find(system_fine_records, record.faction) < 0:
                    {set interstellar_fine_records to cat(interstellar_fine_records, [record.faction])}
                }
            }
        }
        {set reports to record.bountiesIncurred}
        {if len(reports) > 0:
            {for report in reports:
                {set interstellar_bounties to interstellar_bounties + report.amount}
                {if find(system_bounty_records, record.faction) < 0:
                    {set interstellar_bounty_records to cat(interstellar_bounty_records, [record.faction])}
                }
            }
        }
    }    
}

{if wanted_faction_count > 0:
	{set system_total to humaniza(system_fines + system_bounties)}
	
    {OneOf("Advertencia", "Cuidado", "Alerta")}:
    Está buscado {Occasionally(2, "en este sistema")} por {wanted_faction_count}
    {if wanted_faction_count = 1: facción |else: facciones},
    por 
    {if system_fines > 0 && system_bounties > 0:
        multas y recompensas
    |elif system_fines > 0:
        multas
    |else:
        recompensas
    }
    de un total de {system_total} {de} créditos.
}

{if len(system_claim_records) > 0:
	{set system_claims to humaniza(system_claims)}

    {if wanted_faction_count > 0: {OneOf("También tiene", "Tiene también", "Además, tiene")} |else: Tiene}
    por {OneOf("reclamar", "cobrar")} {if len(system_claim_records) = 1: una {OneOf("recompensa", "retribución", "remuneración")} |else: varias {OneOf("recompensas", "retribuciones", "remuneraciones")} {set total to "en total"}} 
    de {system_claims} {de} créditos {total} por sus {OneOf("servicios", "trabajos")} {OneOf("prestados como", "de")} cazarrecompensas.
}

Crime report.script:
{_ Crime report}
{_ Report on all pending claims, fines, and bounties }


{set factions_claims to []}
{set claims_count to 0}
{set factions_fines to []}
{set fines_count to 0}
{set factions_bounties to []}
{set systems_bounties to []}

{for record in criminalrecord:
    {set faction to FactionDetails(record.faction)}
    {if faction:
        {if record.bounties > 0:
            {set factions_bounties to cat(factions_bounties, [record.faction])}
            {for presence in faction.presences:
                {if find(systems_bounties, presence.systemName) = -1:
                    {set systems_bounties to cat(systems_bounties, [presence.systemName])}
                }
            }
        }
        {if record.fines > 0:
            {set factions_fines to cat(factions_fines, [record.faction])}
        }
        {if record.claims > 0:
            {set factions_claims to cat(factions_claims, [record.faction])}
        }

        {for report in record.factionReports:
            {if !report.bounty && report.crimeEDName != "none":
                {set fines_count to fines_count + 1}
            }
            {if report.crimeEDName = "none":
                {set claims_count to claims_count + 1}
            }
        }
    }
}

{set factions_count to len(factions_bounties)}
{set systems_count to len(systems_bounties)}

{if factions_count > 0:
    Estás buscado por {factions_count} {if factions_count = 1: facción |else: facciones}
    en {systems_count} {if systems_count = 1: sistema |else: sistemas} por un total de
    {Humanise(bounties)} créditos.
}

{set factions_count to len(factions_fines)}
{if factions_count > 0:
    Tienes {fines_count} {if fines_count = 1: {set s to ""} |else: {set s to "s"}} multa{s} pendiente{s} de
    {factions_count} {if factions_count = 1: facción |else: facciones},
    de un total de {Humanise(fines)} créditos.
}

{set factions_count to len(factions_claims)}
{if factions_count > 0:
    Tienes {claims_count} {if claims_count = 1: recompensa |else: recompensas} sin recoger de
    {factions_count} {if factions_count = 1: facción |else: facciones},
    {if factions_count = 1: de |else: que suman} un total de {Humanise(claims)} créditos.
}

