Destination arrived.script:
{if event.name = carrier.name:
    {OneOf("Su carrier", "Su portanaves", carrier.name)} le da la bienvenida
    {F("Honorific")}.
|else:

    {set reportSystem to SystemDetails(system.systemname)}
    {if find(reportSystem.carriersignalsources, event.name) > -1:
        {OneOf("La portanaves", "La carrier")}
        {event.name} 
        {OneOf(
            "{OneOf('se encuentra situada', 'está orbitando', 'está estacionada')} en este punto",
            "{OneOf('está orbitando', 'está estacionada')} {OneOf('en este lugar', 'en esta zona', 'en este sector')}",
            "{OneOf('se encuentra en rango', 'está estacionada en la zona')}"
        )}.
    |else:
        {set entering to OneOf("Llegando a ", "Entrando en ")}
        
        {set warninglevel to:
            {if event.threat < 2:
                {entering}
            |elif event.threat = 2:
                Cuidado: {entering} zona de amenaza media.
            |elif event.threat = 3:
                Atención: {entering} zona de gran riesgo.
            |elif event.threat = 4:
                Peligro: {entering} zona de alto riesgo.
            |elif event.threat = 5:
                Peligro extremo: {Occasionally(2, entering)} zona de muy alto riesgo.
            |elif event.threat = 6:
                Peligro extremo: {Occasionally(2, entering)} zona de extremo riesgo.
            |elif event.threat = 7:
                Peligro extremo: {Occasionally(2, entering)} zona de riesgo extremadamente algo.
            |else:
                Peligro Extremo: {Occasionally(2, entering)} zona de amenaza de nivel {event.threat}.
            }
        }
        
        {warninglevel} {if event.isSignalSource: señal de} {event.name}.
        
        {if system.government = "Anárquico" && event.name = "Emisiones degradadas" && system.population > 0: 
            {if rand(2):
                {Pause(1000)}
                
                {set local to OneOf('locales', 'de la zona')}
                {set chatter to OneOf('cotilleos', 'habladurías', 'rumores')}
                {set indicate to OneOf('comentan', 'sugieren', 'indican', 'reportan')}
                {set where to OneOf('en esta zona', 'en las inmediaciones', 'en este sector', 'en este sistema')}
                {set threat to:
                    {OneOf('bastante', 'una elevada', 'una gran')}
                    {OneOf('actividad', 'presencia')}
                    {OneOf('criminal', 'de piratas', 'de {OneOf("grupos", "clanes", "sindicatos")} {OneOf("piratas", "criminales")}')}
                }
                
                Cuidado:
                {if rand(2):
                    {OneOf(
                        '{Occasionally(2, "según")} Transmisiones {local} {indicate} {Occasionally(3, "que hay")}',
                        '{Occasionally(2, "según")} {chatter} {local} {indicate} {Occasionally(3, "que hay")}',
                        'suele detectarse',
                        'los sensores indican {Occasionally(3, "que hay")}'
                    )}
                    {threat}
                    {where}.
                |else:
                    {where}
                    {OneOf('suele detectarse', 'suele haber', 'es posible encontrar')}
                    {threat}.
                }
                {Occasionally(3, F("Honorific"))}
                {OneOf('Extreme las precauciones', 'Tenga mucho cuidado', '{OneOf("Permanezca", "Esté")} alerta', '{OneOf("Tenga", "Vaya con")} {OneOf("cautela", "precaución")}')}.
           }
        }
    }
}

