|else:
	{set reportSystem to SystemDetails(system.systemname)}
}

{SetState('eddi_context_last_action', 'honk')}

{if !reportSystem.name || reportSystem.name = "":
    No tengo claro a que sistema de refiere.
|else:
	{if ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose":
		{if len(reportSystem.factions) = 0 || reportSystem.scannedbodies != 0:
			{set bodiesToScan to (event.totalbodies - reportSystem.scannedbodies)}
			{Pause(850)}
			{if bodiesToScan = 1:
				Queda un cuerpo para ser {OneOf("escaneado", "analizado", "descubierto")}.
			|elif bodiesToScan > 1:
				{if reportSystem.scannedbodies != 1: {set s to "s"}{set n to "n"}}
				{OneOf(
					'{reportSystem.scannedbodies} cuerpo{s} de {Occasionally(3, "un total de ")} {event.totalbodies}, {Occasionally(2, Pause(150))} ha{n} sido {OneOf("escaneado{s}", "analizado{s}", "explorado{s}", "catalogado{s}")}', 
					'Quedan {bodiesToScan} cuerpos para ser {OneOf("escaneados", "analizados", "explorados", "catalogados")}'
				)}
				{Occasionally(3, "en este sistema")}.
			|else:
				{if reportMaterials && hasSRV:
    	    		{set materialsReport to F("System materials report")}
    	    	}
			}
		}

		{set bodiesToMap to F("Bodies to map")}
		{if bodiesToMap: {if bodiesToScan > 0: {OneOf("De momento", "Por ahora")}} {bodiesToMap}}
   	    {if materialsReport: {materialsReport}}
	}
}

