|else:
    {set systemname to system.systemname}
}

{if !systemname || systemname = "":

    No tengo claro a que sistema te refieres.

|else:

    {set reportEngineer to EngineerDetails(systemname)}
    {if reportEngineer:
        {set report to cat(OneOf("Este sistema es el hogar", "En este sistema está la base"), " de ", reportEngineer.name)}
        {if len(reportEngineer.majorspecialties) > 0:
            {set reportMajor to cat(OneOf("{Occasionally(2, 'Quién está')} especializado", "{Occasionally(2, 'Que es')} especialista"), " en ", OneOf("mejorar", "actualizar", "modificar"), " ", List(reportEngineer.majorspecialties), ".")}
        }
        {if len(reportEngineer.minorspecialties) > 0:
        	{set reportMinor to cat(OneOf("puede mejorar", "puede actualizar", "mejora", "actualiza"), " ", List(reportEngineer.minorspecialties), " ", OneOf("pero", "aunque"), " ", OneOf("no es su fuerte", "no a su máximo grado"), ".")}
        }
        {if reportMajor:
        	{if reportMinor:
                {set report to cat(report, " ", reportMajor, " ", OneOf("Además", "También"), " ", reportMinor)}
            |else:
            	{set report to cat(report, " ", reportMajor)}
            }
		|else:
			{set report to cat(report, " ", OneOf("Quién", "Que"), " ", reportMinor)}
		}
		{report}
		{OneOf("Podrás encontrar", "encontrarás")} a {reportEngineer.name} en la estación {P(reportEngineer.stationname, "")}.
    }
}

Entered normal space.script:
{_ Entered normal space }
{_ Triggered when your ship enters normal space }

{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'disengage')}
{SetState('eddi_context_system_name', event.systemname)}
{SetState('eddi_context_body_name', event.bodyname)}
{SetState('eddi_context_body_type', event.bodytype)}
{SetState('eddi_context_body_system', event.systemname)}

{set station_notifications to:
    {set station_missions to F("Mission check station")}
    {set station_crimes to F("Crime check station")}
    {if station_missions || station_crimes:
        {_ Pause for station ATC before speaking again _}
        {Pause(1000)}
        {if station_missions:
            {Pause(250)}
            {station_missions}
        }
        {if station_crimes:
            {Pause(250)}
            {station_crimes}
        }
    }
}

{_ If following a glide event, pause is not required. _}
{if !(status.near_surface && status.fsd_status = "masslock"):
    {Pause(3000)}
}

{Pause(500)}

{if rand(2):
    {OneOf(
        "{ShipName()} ha", "Hemos")} {OneOf("salido de {Occasionally(2, 'velocidad de')} supercrucero",
        "{OneOf('pasado a', 'vuelto a', 'regresado a')} {OneOf('espacio', 'velocidad')} normal"
    )} 
|else:
    {OneOf(
        "saliendo de {Occasionally(2, 'velocidad de')} supercrucero",
        "{OneOf('pasando a', 'volviendo a', 'regresando a')} {OneOf('espacio', 'velocidad')} normal"
    )}
}


{if event.bodytype_invariant = 'Planet':
    {set time to SecondsSince(0) - GetState("eddi_context_approach_time")}
    {if time < 60:
    	{set settlement to GetState("eddi_context_last_settlement")}
		{set reportStation to StationDetails(settlement, event.systemname)}
		{set location to cat(OneOf("cerca", "en las proximidades"), " ", Occasionally(2, "del espacio aéreo"))}
        {if reportStation:
            {SetState('eddi_context_body_name', settlement)}
            {if reportStation.model != "Estación desconocida":
            	{creaArticulos(reportStation.model)}
                {set location to cat(location, " ", del, " ", P(reportStation.model, "station"), ", ")}
            }
            {set location to cat(location, " de ", P(settlement, "station"), ".")}
   			{set location to cat(location, " ", station_notifications)}
        |else:
            {set location to cat(location, " de ", P(settlement, "station"), ".")}
        }
    |else:
        {set reportBody to BodyDetails(event.bodyname , event.systemname)}
    	{creaArticulos(reportBody.bodytype)}
    	{set location to OneOf("en {Occasionally(2, 'la')} órbita", "cerca", "en las proximidades")}
        {set location to cat(location, " ", del, " ", reportBody.bodytype, " ", P(reportBody.bodyname, "body"))}
    }

|elif event.bodytype_invariant = 'Star':
