Friends status.script:
{_ Set uselist to "white" or "black", depending on which list you want to use to filter people _}
{_ "white" will only allow notifications from people in this list (e.g. "User1", "User2", & "User3") _}
{_ "black" will allow all notification EXCEPT those on this list (e.g. "User4", "User5", & "User6") _}
{_ to allow all notifications, you may also use an empty blacklist (i.e. "set blacklist to []" _}

{set uselist to "black"}

{set whitelist to ["User1", "User2", "User3"]}
{set blacklist to ["User4", "User5", "User6"]}

{if event.name:
    {if uselist = "white":
        {if find(whitelist, event.name) > -1:
            {event.name} is now {event.status}.
        }
    |elif uselist = "black":
        {if find(blacklist, event.name) = -1:
            {event.name} is now {event.status}.
        }
    }
|else:
    No se de quien me está hablando.
}

FSD engaged.script:
{_ Context _}
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'engage')}
{SetState('eddi_context_fsd_target', event.target)}

{_ Zero the 'remaining jump' context to only use values following this event _}
{SetState('eddi_context_remaining_jumps', 0)}

{if event.target = 'Hyperspace':
    {_ Add/update context to represent a jump _}
    {SetState('eddi_context_last_subject', 'jump')}
    {SetState('eddi_context_last_action', 'start')}
    {SetState('eddi_context_system_name', event.systemname)}
    {SetState('eddi_context_system_lastvisit', system.lastVisitSeconds)}

    {set reportsystem to SystemDetails(event.system)}

    {_ Wait for the FSD countdown and initial jump _}
    {Pause(6000)}
      

    {OneOf("Destino fijado.", "Salto en progreso.", "Telemetría obtenida.", "Destino confirmado.", "")}
    {OneOf(
        "{ShipName()} {OneOf('está saltando', 'va de camino', ' está en trayecto', 'en ruta')}",
        "{Occasionally(2, 'estamos')} saltando",
        "{Occasionally(2, 'vamos')} en dirección",
        "{Occasionally(2, 'vamos')} en ruta",
        "nos dirigimos",
        "viajando",
        "{Occasionally(2, 'estamos')} en trayecto",
        "{Occasionally(2, 'estamos')} en tránsito"
    )}
    a{OneOf("l sistema", "")} {P(corrigeTexto(reportsystem.systemname), "starsystem")}.{Pause(30)}
    {if reportsystem.name = homesystem.name:
        {OneOf("llegamos a casa", "Finalmente en casa", "Por fin en casa", "Hogar, dulce hogar")}, {F("Honorific")}!
    |else:
        {set scoopables to ["O", "B", "A", "F", "G", "K", "M"]}
        {if slice(event.stellarclass, 0, 1) = "D":
            ¡{OneOf("Atención", "Cuidado", "Advertencia")}!: {OneOf("detectada", "la estrella destino es una", "nos acercamos a una")} enana blanca.
        |elif event.stellarclass = "N":
            ¡{OneOf("Atención", "Cuidado", "Advertencia")}!: {OneOf("detectada", "el destino es una", "nos acercamos a una")} estrella de neutrones.
        |elif event.stellarclass = "H":
            ¡{OneOf("Atención", "Cuidado", "Advertencia")}!: {OneOf("detectado", "el destino es un", "nos acercamos a un")} agujero negro.
        |elif find(scoopables, event.stellarclass) = -1:
            {set currentfuel to round(status.fuel_percent, 0)}
            {if currentfuel < 25:
            	{OneOf("Atención Comandante:", "Tenga en cuenta que", "Cuidado Comandante,", "Le informo que")} la estrella {Occasionally(2, "de destino")} no permite el repostaje {Occasionally(2, "de combustible")}.
            }
        }
        
        {if lastsystem.allegiance && lastsystem.allegiance != "Ninguno" && lastsystem.allegiance != "Independiente" && (!reportsystem.allegiance || reportsystem.allegiance = "Ninguno" || reportsystem.allegiance = "Independiente"):
            Comandante {OneOf("estamos abandonando el espacio de", "hemos dejado", "ya no estamos en territorio de")} {lastsystem.allegiance}.
        |elif lastsystem.allegiance && lastsystem.allegiance != reportsystem.allegiance && system.allegiance && system.allegiance != "Ninguno":
            {OneOf("ahora estamos en", "hemos llegado a", "estamos entrando a")} {OneOf("{Occasionally(2, 'un')} espacio","una zona")}


			{if system.allegiance = "Independiente": de un gobierno independiente
			|elif system.allegiance = "La Alianza": de la Alianza
			|elif system.allegiance = "El Imperio": del Imperio
			|elif system.allegiance = "La Federación": de La Federación
			|elif system.allegiance = "Pirata": Pirata
			|elif system.allegiance = "Guardián": Guardián
			|elif system.allegiance = "Thargoide": Zargoide
			|elif system.allegiance = "Federación de Pilotos": de La Federación de Pilotos}.

            {if cmdr.title != "Comandante":
                 El Gobierno local le da la bienvenida {Oneof("como","con el título de")} {F("Honorific")}.
            }
        }              
    }
	{if reportsystem.name != homesystem.name:
        {if reportsystem.visits = 0:
        	{if ship.Role.edname != "Exploration":
            	{OneOf("Es nuestra primera visita", "Es la primera vez que estamos", "Nunca antes habíamos estado")} {OneOf("por aquí", "en este sistema")}
            }
        |elif reportsystem.visits = 1:
        	{OneOf("Es nuestra segunda visita", "Es la segunda vez que estamos")} {OneOf("por aquí", "en este sistema")}
        |elif reportsystem.visits = 2:
        	{Occasionally(2, '{OneOf("Es nuestra tercera visita", "Es la tercera vez que estamos")} {OneOf("por aquí", "en este sistema")}')}
        |elif reportsystem.visits = 3:
        	{Occasionally(2, '{OneOf("Es nuestra cuarta visita", "Es la cuarta vez que estamos")} {OneOf("por aquí", "en este sistema")}')}
        |else:
            {Occasionally(3, "Hemos {OneOf('visitado', 'estado en', 'pasado por')} este sistema {reportsystem.visits} veces")}
        }.
     }

     {if reportsystem.visits < 1:
        {if !reportsystem.population && lastsystem.population:
			{OneOf("Este sistema está despoblado", "En este sistema no hay {OneOf('presencia humana', 'asentamientos humanos', 'ninguna colonia humana', 'colonias humanas')}", "No hay {OneOf('presencia humana', 'asentamientos humanos', 'ninguna colonia humana', 'colonias humanas')} en este sistema", "La Humanidad aún no ha colonizado este sistema")}
        |elif reportsystem.population:
            {if SecondsSince(reportsystem.lastVisitSeconds) > 3600:
                {F("System report")}
            }
        }.
     }

     {if reportsystem.distancefromhome:
         {set ReportDistanceFromHome() to:
          	{return cat(
           		OneOf("Nos encontramos", "La posición actual está", "Actualmente estamos"), " a ",
           		humaniza(round(reportsystem.distancefromhome)), " años luz de ",
