Fuel check.script:
{_ Fuel Check _}
{_ Report fuel level _}

{_ FALLA CUANDO ESTA CASI A CERO -}

{_ Might be a taxi or multicrew session. We ignore those. We only want this when we're in our own ship. }
{if vehicle = "Ship":

	{for compartment in ship.compartments:
    	{if compartment.module.basename = "FuelScoop":
    	    {set scoop_present to true}
    	}
	}

	{set shipsfuel to:
    	{OneOf("Nivel", "Depósito", "Reserva")} de combustible
    	{Occasionally(2, "actual")} 
	}

	{set currentfuel to floor(status.fuel_percent)}
	{set currentpercent to:
    	al {currentfuel} por ciento {Occasionally(3, "de su capacidad")}
	}

	{set title to Occasionally(3,"{F('Honorific')}")}

	{set maxjump to round(status.fuel/ship.maxfuel, 2)}

	{if GetState("eddi_context_last_subject") = "jump" && GetState("eddi_context_last_action") = "complete" && maxjump < 1.25:
    	{OneOf("Peligro", "Alerta", "Atención")}. {shipsfuel} {OneOf("al mínimo", "bajo mínimos")}.
		{Pause(500)}

	    {if scoop_present:
    	    {if system.scoopable:
    	        {set scoop_system to system.systemname}
    	    |else:
    	        {set detail to JumpDetails('total')}
    	        {set scoop_system to RouteDetails('scoop', detail.distance)}
    	        {set scoop_distance to Distance(scoop_system)}
    	    }

    	    {if scoop_system && scoop_system != "":
    	        {if scoop_system = system.name:
    	            {set scoopable_bodies to []}
    	            {for body in system.bodies:
    	                {if body.scoopable:
    	                	{if body.mainstar || body.shortname = system.name || body.shortname = cat(system.name, " A") || body.shortname = "A":
    	                		{set scoopable_bodies to cat(scoopable_bodies, [OneOf('principal', 'primaria', 'de entrada al sistema')])}
    	                	|else:
    	                    	{set scoopable_bodies to cat(scoopable_bodies, [P(body.shortname, "body")])}
    	                    }
    	                }
    	            }
    	            {if len(scoopable_bodies) > 1:
    	            	{set s to "s"}
    	                {set resultado to cat("son las: ", List(scoopable_bodies))}
    	            |else:
    	            	{set s to ""}
    	                {set resultado to cat("es la ", Occasionally(2, 'estrella'), " ", List(scoopable_bodies))}
    	            }

					{set resultado to cat(resultado, Pause(50), " del sistema actual.")}
    	        |else:
    	            {set resultado to cat("está en el sistema ", P(scoop_system, 'starsystem'), " que está a ", round(scoop_distance, 1), " años luz de aquí ")}
    	        }
    	        La{s} estrella{s} recolectable{s} más cercana{s} {resultado}.


    	        {Pause(500)}
    	        {if maxjump < 0.5:
    	             El reabastecimiento {Occasionally(2, "de combustible")} es urgente, {title}.
    	        |else:
    	            El reabastecimiento {Occasionally(2, "de combustible")} es altamente recomendable, {title}.
    	        }
    	    |else:
    	        {OneOf("Peligro", "Alerta", "Atención")}. No hay estrellas recolectables en el rango del próximo salto.
    	        {Pause(250)}
    	        Se recomienda el cierre de sistemas no esenciales.
    	        {Pause(250)}
    	        Transpondedor de emergencia a la espera, {title}.
    	    }
    	|else:
    	    {if maxjump < 0.5:
    	        {OneOf("Es imperativo el reabastecimiento de combustible", "Reabastezca el combustible urgentemente")}.
    	    |else:
    	        {OneOf("Por favor, haga un repostaje de combustible cuanto antes", "Se recomienda encarecidamente que recargue combustible")}.
    	    }
    	}
	|elif currentfuel < 25:
    	{OneOf("Peligro", "Alerta", "Atención")} {title}.
    	{shipsfuel} {OneOf("por debajo del 25%", currentpercent)}.
	|elif currentfuel < 50:
	    {shipsfuel} {OneOf("por debajo del 50%", currentpercent)}.
	|elif currentfuel < 75:
    	{Occasionally(3, "{shipsfuel} {currentpercent}.")}
	|elif currentfuel < 100:
    	{Occasionally(4, "{shipsfuel} {currentpercent}.")}
	|else:
    	{shipsfuel} {Occasionally(2, "de la {OneOf(ShipName(), 'Nave')}")} {OneOf(currentpercent, "al máximo")}.
	}
}

