GetDistance.script:
{_Shirka}

{_ GetDistance}
{_ Find distance to destination}

{set dist to -1}

{set fs to system.name}
{set ts to GetState("eddi_context_system_system")}

{if fs && ts:
    {if ts = "":
        Origin system not designated.
    |elif fs = "":
        Destination system not designated.
    |else:
        {set fs to SystemDetails(fs)}
        {set ts to SystemDetails(ts)}

        {if !fs.name:
            Your origin is not in the Pilot's Federation star charts.
        |elif !ts.name:
            Your destination is not in the Pilot's Federation star charts.
        |else:
            {set dist to round(Distance(fs.x, fs.y, fs.z, ts.x, ts.y, ts.z), 2)}
            {if GetState("getdistance") = 'true':
                Distance {Occasionally(2, "from {fs.name}")} to
                {Occasionally(2, "{OneOf('target','destination')} system")}
                {ts.name} is {dist} light years.
            }
        }
    }
}

{SetState('distance', dist)}

