Jumped.script:
{_ Context }
{SetState('eddi_context_last_subject', 'jump')}
{SetState('eddi_context_last_action', 'complete')}
{SetState('eddi_context_system_name', system.name)}
{SetState('eddi_context_system_system', system.name)}
{SetState('eddi_context_fuel_remaining', event.fuelremaining)}
{SetState('eddi_context_fuel_used', event.fuelused)}
{SetState('eddi_context_wanted_list', "")}

{set next_system to RouteDetails("update")}
{F('Fuel check')}

{set jump_count to GetState("eddi_context_remaining_jumps")}
{if jump_count > 20:
    {set occasional to ceil(jump_count / 10)}
|elif jump_count > 5:
    {set occasional to 2}
|else:
    {set occasional to 1}
}

{if !GetState("long_route") && jump_count > 1: {SetState('long_route', true)}}

{_ destinationsystem and destinationdistance are EDDI's global variables _}
{if destinationsystem && destinationsystem.systemname != "":
    {if destinationdistance = 0:
        {ShipName()} ha llegado al sistema {P(destinationsystem.systemname, "starsystem")}.
        {Pause(1000)}
    |else:
    	{if rand(occasional) = 0:
    		{if jump_count > 8:
            	La distancia al sistema {P(destinationsystem.systemname, "starsystem")} es de unos {humaniza(destinationdistance)} años luz
            }
            {if jump_count > 0:
            	{if jump_count = 1:
    				{set s to ""}{set n to ""}
    			|else:
    				{set s to "s"}{set n to "n"}
    			}
    			, {Occasionally(2, "{OneOf('Queda{n}', 'Falta{n}')}")} {jump_count} salto{s} {Occasionally(2, OneOf("para llegar {Occasionally(2, OneOf('al final de la ruta', 'al destino'))}", "hasta {OneOf('el final de la ruta', 'el destino')}"))}
            }.
        }
    }
|else:
    {if  jump_count > 0 && rand(occasional) = 0 && vehicle = "Ship":
    	{if jump_count = 1:
    		{set s to ""}{set n to ""}
    	|else:
    		{set s to "s"}{set n to "n"}
    	}
        {Occasionally(2, "{OneOf('Queda{n}', 'Falta{n}')}")} {jump_count} salto{s} {Occasionally(2, OneOf("para llegar {Occasionally(2, OneOf('al final de la ruta', 'al destino'))}", "hasta {OneOf('el final de la ruta', 'el destino')}"))}.
	|elif jump_count = 0 && GetState("long_route"):
		Hemos llegado al final de la ruta.
		{SetState('long_route', false)}
    }
}        

{_ Report faction states only if it's been more than an hour (3600 seconds) since our last visit. _}
{if SecondsSince(GetState("eddi_context_system_lastvisit")) / 3600 > 1:
	{set state_report to F("System state report")}
}

{set system_engineer to F("Engineer report")}
{set system_missions to F("Mission check system")}
{set system_crimes to F("Crime check system")}

{if state_report || system_engineer || system_missions || (system_crimes && find(system_crimes, "Atención") < 0):
    {Pause(500)}
    {OneOf("Información", "Informe{Occasionally(2, ' de situación')}", "Reportando", "")}.
}

{if state_report:
    {Pause(500)}
    {state_report}.
}
{if system_engineer:
    {Pause(500)}
    {system_engineer}.
}
{if system_missions:
    {Pause(500)}
    {system_missions}.
}
{if system_crimes:
    {Pause(500)}
    {system_crimes}.
}

