Last scan value.script:
{_ This script replace the built-in function of EDDI with a more accurate formula _}
{_ it stores results in eddi_context_last_scan_estimated_value and eddi_context_last_scan_max_value _}

{set valuePlanetTypes to [
    "Mundo de amoníaco": 96932,
    "Mundo similar a la Tierra": 64831,
    "Gigante gaseoso con vida basada en el amoníaco": 300,
    "Gigante gaseoso con vida basada en el agua": 300,
    "Gigante gaseoso de Helio": 300,
    "Gigante gaseoso rico en Helio": 300,
    "Mundo de alto contenido metálico": 9654,
    "Cuerpo helado": 300,
    "Cuerpo con alto contenido metálico": 21790,
    "Mundo helado rocoso": 300,
    "Cuerpo rocoso": 300,
    "Gigante gaseoso de Clase 1": 1656,
    "Gigante gaseoso de Clase 2": 9654,
    "Gigante gaseoso de Clase 3": 300,
    "Gigante gaseoso de Clase 4": 300,
    "Gigante gaseoso de Clase 5": 300,
    "Gigante acuático": 300,
    "Gigante acuático con vida": 300,
    "Mundo acuático": 64831,
]}

{if !reportbody.bodytype: 
	{if GetState("eddi_context_last_subject") = "star":
		{set reportbody to BodyDetails(GetState("eddi_context_star_star"), GetState("eddi_context_star_system"))}
		{SetState("eddi_context_last_scan_estimated_value", reportbody.estimatedvalue)}
		{SetState("eddi_context_last_scan_max_value", reportbody.estimatedvalue)}
		{return fin}
	|else:
    	{set reportbody to BodyDetails(GetState("eddi_context_body_name"), GetState("eddi_context_body_system"))}
    }
}

{set k to valuePlanetTypes[reportbody.planettype]}
{if (reportbody.terraformstate = "Candidato para terraformación" ||reportbody.terraformstate = "Terraformado"):
	{if reportbody.planettype = "Cuerpo con alto contenido metálico":
		{set k to k + 100677}
	|elif reportbody.planettype = "Mundo acuático" || reportbody.planettype = "Mundo similar a la Tierra":
		{set k to k + 116295}
	|else:
		{set k to k + 93328}
	}
}

{set GetBodyValue(isMapped) to:

	{set q to 0.56591828}
	{set mappingMultiplier to 1}
	{if isMapped:
		{if !reportbody.alreadydiscovered && !reportbody.alreadymapped:
			{set mappingMultiplier to 3.699622554}
		|elif !reportbody.alreadymapped:
			{set mappingMultiplier to 8.0956}
		|else:
			{set mappingMultiplier to 3.3333333333}
		}
	}
	{set value to (k + k * q * pow(reportbody.earthmass,0.2)) * mappingMultiplier}
	{if isMapped:
		{if odyssey:
			{if (value * 0.3) > 555:
				{set value to value + (value * 0.3)}
			|else:
				{set value to value + 555}
			}
		}
	}
	{set value to max(500, value)}
	{if !reportbody.alreadydiscovered: {set value to value * 2.6}}

	{return round(value)}
}

{SetState("eddi_context_last_scan_estimated_value", GetBodyValue(reportbody.mapped))}
{SetState("eddi_context_last_scan_max_value", round(GetBodyValue(true) * 1.25))}

Launchbay report.script:
{if capi_active:
    {if len(ship.launchbays) > 0:
        {set hanger_count to 0}
        {for launchbay in ship.launchbays:
        	 {set hangar to: Hangar de
                {if launchbay.type = "VRS": 
                    {Spacialise(launchbay.type)}
                |else: 
                    {launchbay.type}
                }
                de tamaño {launchbay.size} 
            }
            {set hangar_count to hangar_count + 1}
            {set vehicles to []}
            {set vehicles_count to []}
            {if len(launchbay.vehicles) > 0:
                {for vehicle in launchbay.vehicles:
                    {set variant to cat(vehicle.name, "_", vehicle.description)}
                    {set here to find(vehicles , variant)}
                    {if here > -1:
                        {set count to vehicles_count[here] + 1}
                        {set vehicles_count to union(vehicles_count, [here:count])}
                    |else:
                        {set vehicles to cat(vehicles , [variant])}
                        {set vehicles_count to cat(vehicles_count, [1])}
                    }
                }
            }

            {if len(vehicles) > 0:
                {if hangar_count = 1: Tienes |else: y} un {hangar}, almacenando
                {set cur to 0}
                {while cur < len(vehicles):
                    {set count to vehicles_count[cur]}
                    {set name to split(vehicles[cur], "_")[0]}
                    {set description to split(vehicles[cur], "_")[1]}
                    {count} {if count = 1: {P(name)} |else: {P(name)}s}, con {description}
                    {set cur to cur + 1}
                    {if cur = len(vehicles)-1: y |elif cur < len(vehicles): ,}
                }
            |else:
                {if hangar_count = 1: Tienes |else: y} un {hangar} vacío
            }
            {if hangar_count = len(ship.launchbays): . |else: ,}
        }
    }
|else:
    El estado del hangar no está disponible.
    {Pause(500)}
    Por favor, compruebe la conexión a la base de datos de la federación de pilotos.
}

Limpet check.script:
{set controllers to 0}
{for compartment in ship.compartments:
    {if find(compartment.module.name, "drón") != -1 || find(compartment.module.name, "dron") != -1 || find(compartment.module.name, "Limpet") != -1:
        {set controllers to controllers + 1}
    }
}

{set limpets to 0}
{for cargo in inventory:
    {if cargo.name = "Drón":
        {set limpets to cargo.owned}
    }
}
{set tenpercent to ship.cargocapacity / 10 - 1}
{if controllers > 0 && limpets = 0:
    Aviso: llevamos un lanzador de drónes pero no llevamos ningún drón.
|elif controllers = 0 && limpets > 0:
    transportamos drónes pero no llevamos ningún lanzador
|elif controllers > 0 && limpets < tenpercent:
    sólo llevamos {limpets} drónes a bordo; debería considerar el adquirir más.
}

List launchbays.script:
{for launchbay in ship.launchbays:
    Tienes un hangar de tamaño {launchbay.size}, de tipo {launchbay.type}.
    {for vehicle in launchbay.vehicles:
        Tiene un {P(vehicle.name)} con {vehicle.loadout} de carga

        {if type(vehicle.mount) != null:
            {if vehicle.mount = 'F':
                , y armas fijas
            |elif vehicle.mount = 'G':
                , y armas de seguimiento
            }
        }
        .{_ full stop}

        {if vehicle.rebuilds != null:
            {if vehicle.rebuilds = 1:
