            }
        }

        {Pause(500)}
    }
}

Location.script:
{_ Fetch from Context, if a context is set _}
{if !event.systemname:
	Me faltan datos para decirte donde nos encontramos.
    {return false}
}

{_ Context _}
{SetState('eddi_context_last_subject', 'location')}
{SetState('eddi_context_system_name', event.systemname)}
{SetState('eddi_context_body_name', event.bodyname)}
{SetState('eddi_context_body_type', event.bodytype)}
{SetState('eddi_context_body_system', event.systemname)}

{if event.docked || event.station = event.bodyname:
	{_ Near or inside a station _}
    {set station to StationDetails(event.station, event.systemname)}
    {set station_missions to F("Mission check station")}
    {set station_crimes to F("Crime check station")}

	{creaArticulos(station.model)}
    {if event.docked:
        {set location to OneOf("acoplados en {el}", "atracados en {el}", "estacionados en {el}")}
    |else:
        {set location to OneOf("cerca {del}", "en los alrededores {del}", "próximos a{l}", "en las proximidades {del}")}
    }

    {set location to cat(location, " ", P(station.model), ", ", P(event.station))}
    {if event.station != event.bodyname:
        {set location to cat(location, ", en ", P(event.bodyname))}
    }

|elif event.bodyname:
	{_ Near something else _}
    {set system_missions to F("Mission check system")}
    {set system_crimes to F("Crime check system")}

	{creaArticulos(event.bodytype)}
    {set location to cat(OneOf("cerca {del}", "en los alrededores {del}", "próximos a{l}", "en las proximidades {del}"))}
    {if event.bodytype:
    	{set location to cat(location, " ", event.bodytype)}
    }
    {set location to cat(location, " ", P(event.bodyname))}
}

{OneOf("Actualmente", "Ahora mismo", "")} {OneOf("Nos encontramos", "Estamos")} {location}, en el sistema {P(event.system)}.

{if system.faction:
    {for faction in system.factions:
        {if system.faction = faction.name:
            {if faction.myreputation >= 75: 
                {set rep to "aliado")}
            |elif faction.myreputation >= 35: 
                {set rep to "amistoso")}
            |elif faction.myreputation >= 5: 
                {set rep to "cordial")}
            |elif faction.myreputation >= -15: 
                {set rep to ""} {_ Neutral }
            |elif faction.myreputation >= -90:
                {set rep to "enemistado"}
            |elif faction.myreputation >= -100: 
                {set rep to "hostil"}
            }
        }
    }
    {if len(rep) > 0:
        Tu {OneOf("estado", "reputación")} actual con la {OneOf("potencia", "facción")} {OneOf("al {OneOf('control', 'mando')} {Occasionally(2,'del sistema')}", "que controla el sistema", "local")}, {P(system.faction, "faction")}, es {rep}.
        {if rep = "hostil": {Ocasionally(2, "Por favor,")} {OneOf("extreme la precauciones", "tenga cuidado")} {Occasionally(3, F('Honorific'))}.}
    |else:
        {P(system.faction, "faction")} es la {OneOf("potencia", "facción")} {OneOf("al {OneOf('control', 'mando')} {Occasionally(2,'del sistema')}", "que controla el sistema", "local")}.
    }
}

{set state_report to F("System state report")}
{set system_engineer to F("Engineer report")}

{if state_report || system_engineer || system_missions || system_crimes || station_missions || station_crimes:
    {Pause(250)}
    {OneOf("Información", "Informe{Occasionally(2, ' de situación')}", "Reportando", "")}.
    {if state_report:
        {Pause(250)}
        {state_report}
    }
    {if system_engineer:
        {Pause(250)}
        {system_engineer}
    }
    {if station_missions:
        {Pause(250)}
        {station_missions}
    }
    {if station_crimes:
        {Pause(250)}
        {station_crimes}
    }
    {if system_missions:
        {Pause(250)}
        {station_missions}
    }
    {if system_crimes:
        {Pause(250)}
        {station_crimes}
    }
}

