Message received.script:
{_ Context _}
{SetState('eddi_context_last_subject', 'message')}
{SetState('eddi_context_last_action', 'receive')}
{SetState('eddi_context_message_name', event.from)}
{SetState('eddi_context_message_content', event.message)}

{_ "true" means spoken and "false" means muted.}
{_ "npc" controls whether NPC comms will be spoken.}
{_ "player" controls direct messages to you will be spoken.}
{_ "starsystem" controls whether player comms across game modes  will be spoken.}
{set spokenChannels to [
    "friend": true,
    "local": true,
    "multicrew": true,
    "npc": true,
    "player": true,
    "squadron": true,
    "starsystem": false,
    "voicechat": false,
    "wing": true,
]}

{_ "true" means spoken and "false" means muted.}
{_ "NPC" is the default source for NPC comms.}
{set spokenNpcSources to [
    "Ambushed pilot" : true,
    "Bounty hunter" : true,
    "Capital ship" : true,
    "Cargo hunter" : true,
    "Civilian pilot" : true,
    "Conflict zone" : true,
    "Cruise liner" : true,
    "Escort" : true,
    "Exploration convoy" : true,
    "Hitman" : true,
    "Messenger" : true,
    "Military" : true,
    "Miner" : true,
    "NPC" : true,
    "Passenger hunter" : true,
    "Passenger liner" : true,
    "Pirate" : true,
    "Police" : true,
    "Propagandist" : true,
    "Protester" : true,
    "Refugee" : true,
    "Rival power's agent" : true,
    "Search and rescue" : true,
    "Starship One" : true,
    "Station" : true,
    "Wedding convoy" : true,
]}

{if spokenChannels[event.channel_invariant] = true:
	{if event.player = true:
		{set words to split(event.message, " ")}
		{set msg to []}
		{set cur to 0}
		{while cur < len(words):        
			{set o7match to match(words[cur], "(?<!\\w)([o|O|0]7)(?!\\w)")}
			{if cur = 0 && (len(words) > 1 || len(o7match) = 0):
				{set postmsg to "ha enviado:"}
				{set premsg to OneOf('mensaje entrante de ', 'mensaje enviado por ', 'mensaje de ')}
				{OneOf("{set premsg to 'El'}", "{set postmsg to ':'}")}
			}
			{if len(o7match) > 0:
				{set remainder to slice(words[cur], 2)}
				{if len(words) = 1:
					{if event.channel_invariant = 'player': {set you to "te "}}
					{set salute to cat(you, "saluda")}
					{set msg to cat(msg, [cat(salute, remainder)])}
				|else:
					{set msg to cat(msg, [cat("saludo", remainder)])}
				}
			|else:
				{set msg to cat(msg, [words[cur]])}
			}
			{set cur to cur + 1}
		}
		{set message to join(msg, " ")}
		{premsg} {Occasionally(2, event.source)} {event.from} {postmsg} {Transmit(message)}

	|elif spokenNpcSources[event.source_invariant] = true:
		{set msg to event.message}
		{set msg to join(split(msg, " cr of cargo"), " credits of cargo")}
		{set msg to join(split(msg, "t of cargo"), " tons of cargo")}

		{set voiceNames to []}

		{_ Seleccionamos sólo las voces en español _}
		{for voiceDetail in VoiceDetails(event.seed): 
			{if find(voiceDetail.culturename, "español") = -1:
				{_ Esta es una voz que no es española, la saltamos _}
			|else:
				{set voiceNames to cat(voiceNames, [voiceDetail.name])}
			}
		}

		{_ Si queda alguna voz, usamos la primera no excluida _}
		{if len(voiceNames) > 0:
			{Transmit(Voice(msg, voiceNames[0]))}
		}
	}
}

Mission abandoned.script:
{_ Context }
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'abandon')}

{set mission to MissionDetails(event.missionid)}

{if event.communal:
    Ha abandonado un objetivo de comunidad: {mission.localisedname}
|else:
    {if find(mission.type, "Passenger") > -1:
        {set type to "de transporte de "}
        {set type to cat(type, mission.passengertype)}
    |else:
        {set type to mission.type}
    }
    {if type = "Hackeo": {set type to "de jaqueo"}
	|elif type = "Hack Megaship": {set type to "de jaqueo de mega-nave"}
    |elif type = "On Foot": {set type to "a pie"}
    |elif type = "Recaudación": {set type to cat("de ", OneOf('petición', 'adquisición') , " de mercancías")}
    |elif type = "Recaudación de escuadrón": {set type to cat("de ", OneOf('petición', 'adquisición') , " de mercancías para escuadrón")}
    }
    {set type to cat("misión ", type)}



    {type} {if mission.faction: de {mission.faction}} abandonada.
}

Mission accepted.script:
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'accepted')}

{set mission to MissionDetails(event.missionid)}

{if mission.type = "Donation":
    {OneOf("Has escogido","misión aceptada para")} {Occasionally(2,"generosamente")} donar
    {if mission.typeEDName = "Altruism": {mission.commodity} |else: créditos} para 
    {OneOf('mejorar','levantar','incrementar')} su {OneOf('reputación','estatus')} con {P(mission.faction)}
|elif event.communal:
    Ha aceptado el objetivo de comunidad: {mission.localisedname}
|else:
	{set a to "a"}
	{set la to "la"}

	{if find(mission.typeEDName, "Passenger") > -1:
		{set m to "o"}
		{set la to "el"}
		{set type to "transporte de "}
    	{set type to cat(type, mission.passengertype)}
	|elif mission.type = "Hackeo":
		{set type to "misión de jaqueo"}
	|elif mission.type = "Hack Megaship":
		{set type to "misión de jaqueo de mega-nave"}
	|elif mission.type = "On Foot":
		{set type to "misión a pie"}
	|elif mission.type = "Recaudación":
		{set type to cat("misión de ", OneOf('petición', 'adquisición') , " de mercancías")}
	|elif mission.type = "Recaudación de escuadrón":
		{set type to cat("misión de ", OneOf('petición', 'adquisición') , " de mercancías para escuadrón")}
	|elif mission.type:
		{set type to cat("misión de ", mission.type)}
	|else:
		{set type to "misión"}
	}

    {OneOf("{type} {if mission.faction: {OneOf('para', 'de', 'otorgada por')} {Occasionally(2, 'la facción')} {mission.faction}} aceptad{a}",
        "{type} aceptad{a} {if mission.faction: {OneOf('para', 'de', 'otorgada por')} {Occasionally(2, 'la facción')} {mission.faction}}",
        "Aceptad{a} {type} {if mission.faction: {OneOf('para', 'de', 'otorgada por')} {Occasionally(2, 'la facción')} {mission.faction}}",
        "Ha aceptado {la} {type} {if mission.faction: {OneOf('para', 'de', 'otorgada por')} {Occasionally(2, 'la facción')} {mission.faction}}")
    }
}

{Occasionally(2, F("Mission faction state"))}

{if mission.passengerswanted:
    ¡Atención! ¡Estamos {OneOf("transportando", "llevando")} pasajeros {OneOf("buscados {Occasionally(2, 'por la ley')}", "en busca y captura", "problemáticos")}!
    Podríamos ser atacados por la {OneOf("autoridad", "policía")} {Occasionally(2, "del sistema")}.
    {OneOf('Mucha precaución comandante', 'Se aconseja precaución')}. Evite ser escaneado.

|elif token(mission.name, "_", 2) = "Skimmer":
    Esta es una {OneOf('misión','operación')} {OneOf('de superficie', 'en superficie {Occasionally(2, "planetaria")}', 'en tierra')}

|elif token(mission.name, "_", 2) = "Surface":
    Esta es una {OneOf('misión','operación')} {OneOf('de superficie', 'en superficie {Occasionally(2, "planetaria")}', 'en tierra')}, puede que requiera un V R S
}.

{if !event.communal:
    {if missionsCount = 20:
        Ha alcanzado el número máximo de misiones que puedes tomar.
        Por favor, complete algunas antes de aceptar más.
    |else:
       {OneOf("{Occasionally(3,'Ahora mismo')} tienes {missionsCount} misión{if missionsCount > 1:es}",
            "{if missionsCount = 1:{Occasionally(3, 'Ahora')}} esta es tu {missionsCount}ª misión")}.
    }
}

Mission check expiring.script:
{_Shirka}

{_ Mission Check Expiring }
{_ Check what missions are close to expiring }


{if missionsCount > 0 && missionsCount != null:

    {set mission_destination to []}
    {set mission_expiration to []}
    {set mission_name to []}
    {set mission_id to []}
    {set cur to 0}

    {while cur < missionsCount:
        {set destination to missions[cur].destinationsystem}
        {if !destination: {set destination to missions[cur].destinationsystems} }
        {set expiry to missions[cur].expiryseconds}
        {set status to missions[cur].statusEDName}
        {set name to missions[cur].name}
        {set missionid to missions[cur].missionid}

        {if expiry != "" && (status != "failed" || status != "completed"):

            {set seconds to floor(abs(SecondsSince(expiry)))}

            {if seconds / 60 < missionWarning:

                {set mission_destination to cat(mission_destination, [destination])}
                {set mission_expiration to cat(mission_expiration, [seconds])}
                {set mission_name to cat(mission_name, [name])}
                {set mission_id to cat(mission_id, [missionid])}
            }
        }
        {set cur to cur + 1}
    }

    {if len(mission_destination) > 0:
        ¡Atención!
        {set cur to 0}
        {while cur < len(mission_destination):

            {set mission to MissionDetails(mission_id[cur])}
            {if find(mission.type, "Passenger") > -1:
                {set type to "de transporte de "}
                {set type to cat(type, mission.passengertype)}
            |else:
                {set type to mission.type}
            }

			{if type = "Hackeo": {set type to "de jaqueo"}
			|elif type = "Hack Megaship": {set type to "de jaqueo de mega-nave"}
    		|elif type = "On Foot": {set type to "a pie"}
    		|elif type = "Recaudación": {set type to cat("de ", OneOf('petición', 'adquisición') , " de mercancías")}
    		|elif type = "Recaudación de escuadrón": {set type to cat("de ", OneOf('petición', 'adquisición') , " de mercancías para escuadrón")}
    		}

            la misión {type}

            {if find(mission_destination[cur], "_") > -1:
                para múltiples destinos,
            |else:
                {if mission_destination[cur] != GetState("eddi_context_system_name"):
                    en el sistema {mission_destination[cur]}
                |else:
                    en {OneOf("este", "el actual")} sistema
                }
                ,
            }
            expira en 

            {set timer to split(format(mission_expiration[cur], "dl:dd:HH:mm:ss"), ":")}
            {set days to cast(timer[0], "n") - 1}
            {set hours to cast(timer[1], "n")}
            {set minutes to cast(timer[2], "n") - 1}

            {if days > 0: {days} day{if days > 1:s}{if hours = 0: and |else:,}}
            {if hours > 0: {hours} hora{if hours > 1:s} y}
            {minutes} minuto{if minutes > 1:s}.

            {set cur to cur + 1}
        }
    }
}

