Mission check galaxy.script:
{_ Setup allmissions function _}
{set allmissions(systemName, mission) to:
    {set here to find(systems, systemName)}
    {if here > -1:
        {if mission.statusEDName = "Active":       
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        |elif mission.statusEDName = "Claim":
            {set claim to systems_claim[here] + 1}
            {set systems_claim to union(systems_claim, [here:claim])}
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        |elif mission.statusEDName = "Complete":
            {set complete to systems_complete[here] + 1}
            {set systems_complete to union(systems_complete, [here:complete])}
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        }
    |else:
        {set systems to cat(systems, [systemName])}
        {if mission.statusEDName = "Active":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [0])}
            {set systems_complete to cat(systems_complete, [0])}
        |elif mission.statusEDName = "Claim":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [1])}
            {set systems_complete to cat(systems_complete, [0])}
        |elif mission.statusEDName = "Complete":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [0])}
            {set systems_complete to cat(systems_complete, [1])}
         }
    }
}

{if missionsCount + goalsCount > 0:
    {set failed to 0}
    {set multi to 0}
    {set pending to 0}
    {set systems to []}
    {set systems_active to []}
    {set systems_claim to []}
    {set systems_complete to []}

    {for mission in missions:
        {if mission.statusEDName = "Failed":
            {set failed to failed + 1}
        |else:
            {set pending to pending + 1}
            {if mission.destinationsystems:
                {set multi to multi + 1}
                {for destination in mission.destinationsystems:
                    {allmissions(destination.name, mission)}
                }
            |elif mission.destinationsystem && mission.destinationsystem != "":
                {allmissions(mission.destinationsystem, mission)}
            }
        }
    }
    {set systemsCount to len(systems)}

    Tienes
    {if failed > 0:
        {if failed = 1: una misión {Occasionally(2, "que está")} {OneOf("fallida", "expirada", "rescindida")}
        |else: {humaniza(failed, true)} misiones {OneOf("fallidas", "expiradas", "rescindidas")}
        }
        {if missionsCount > failed:
            de {Occasionally(2, "un total de")} {humaniza(missionsCount, true)} misiones.
        }
    |else:
        {if missionsCount = 1: una misión.
        |else: {Occasionally(2, "un total de")} {humaniza(missionsCount, true)} misiones.}
    }

    {if pending > 0:
        {if mission_report && systemsCount > 0:
            {set cur to 0}
            {while cur < systemsCount:
                {set active to systems_active[cur]}
                {set claim to systems_claim[cur]}
                {set complete to systems_complete[cur]}
                
                {if active > 0:
                    {humaniza(active, true)}
                    {if cur = 0 || cur = 5 || cur = systemsCount - 1:
						{if active = 1: misión |else: misiones}
					}
                }
                {if active = 1:
                	{OneOf('ubicada en', 'en', 'en el sistema')}
                |else:
                	{OneOf('ubicadas en', 'en', 'en el sistema')}
                }
                {P(systems[cur])}
                {if claim > 0 || complete > 0: {if active = 1: la cuál|else: de las cuales}}
                
                {if complete > 0:
                	{if complete = 1: está |else: {complete} están }
                	por completar
                    {if claim > 0: y }
                }
                {if claim > 0:
                	{if claim = 1:
                	   está
                	|else:
                	   {if claim = active:
                	       todas están
                	   |else:
                	   	   {claim} están
                	   }
                	}
                   por reclamar 
                }

                {set cur to cur + 1}
                {if cur = systemsCount - 1:, y {if systemsCount > 5: {OneOf("por último,", "para acabar,")}}
                |elif cur < systemsCount: , {if cur = systemsCount - 2: también}}

            }.
        |else:
            {if pending = 1: pendiente |else: {humaniza(pending, true)} pendientes} 
            {if systemsCount = 0:
                sin un sistema objetivo designado
            |elif systemCounts > 1 && missionsCount > 1:
            	repartidas en {humaniza(systemsCount)} {if systemsCount = 1: sistema {Occasionally(2,"estelar")}
                				  |else: sistemas {Occasionally(2,"estelares")}
                				  }
            }.
            {if multi = 1:
                {if multi = pending: Esta misión |else: Una de estas misiones} tiene varios destinos
            |elif multi > 1:
            	{if multi = pending: Todas |else: {multi} de} estas misiones tienen varios destinos
            }        
        }
    }
|else:
    {OneOf("Actualmente no tenemos ninguna misión.", "En estos momentos no hay misiones disponibles.", "Tenemos la agenda de misiones vacía")}
}

Mission Check Near Most.script:
{_Shirka}

{_ Mission Check Near Most}
{_ Find nearest system with missions, and system with most missions}


{if missionsCount > 0:

    {_ Setup nearest calculation function}
    {set nearcalc(name) to:

        {set distance to round(Distance(curr, name), 1)}

        {if distance < nearest_distance:
            {set nearest_distance to distance}
            {set nearest_name to name}
        }
        {set mission_systems to cat(mission_systems, [name])}
    }

    {set curr to GetState("eddi_context_system_system")}
    {set nearest_name to ""}
    {set mission_systems to []}
    {set systems_names to []}
    {set systems_count to []}

    {_ Max scan range. 500 is large, 100 above average, 20-50 normal, 5-10 small}
    {_ Missions closer than this will be reported}
    {set nearest_distance to 50}

    {for mission in missions:
        {if mission.statusEDName = "Active":
            {if mission.destinationsystems:
                {set multi to multi + 1}
                {for destination in mission.destinationsystems:
                    {nearcalc(destination.name)}
                }
            |elif mission.destinationsystem && mission.destinationsystem != "":
                {nearcalc(mission.destinationsystem)}
            }

            {set here to find(systems_names, mission.destinationsystem)}
            {if here > -1:
                {set number to systems_count[here] + 1}
                {set systems_count to union(systems_count, [here:number])}
            |else:
                {set systems_names to cat(systems_names, [mission.destinationsystem])}
                {set systems_count to cat(systems_count, [1])}
            }
        }
    }


    {_ Calculate system with most missions in _}
    {set system_most to ""}
    {set system_count to 0}

    {set cur to 0}
    {while cur < len(systems_names):
        {if systems_count[cur] > system_count:
            {set system_most to systems_names[cur]}
            {set system_count to systems_count[cur]}
        }
        {set cur to cur + 1}
    }

    {set equal to 0}
    {set cur to 0}
    {while cur < len(systems_count):
        {if systems_count[cur] = system_count:
            {set equal to equal + 1}
        }
        {set cur to cur + 1}
    }

    {_ Report nearest system}
    {if nearest_name != "":
        {if missionsCount = 1:
            {if multi = 1:
                You have one multi-destination mission. The nearest 
            |else:
                Your only mission
            }
        |else:
            Your nearest mission
        }
        {Occasionally(2, "destination")} is in the
        {if nearest_name = curr:
            current system
        |else:
            {nearest_name} system
            {if reported != "yes":,
                {if find(nearest_distance, ".00") > -1: {set nearest_distance to round(nearest_distance, 0)}}
                {nearest_distance} lightyears away
            }
        }.


        {_ Report system/s with most missions}
        {if equal = 1:
            {if system_most = nearest_name:
                {if missionsCount > 1:
                    It also has
                    {OneOf("{OneOf('the most','most of your')} missions in",
                           "the most missions for you to complete")}.
                }
            |else:
                {if system_most = curr:
                    {set system_most to "The current system"}
                |else:
                    {set system_most to cat("The", system_most, "system")}
                }
                {OneOf("Most of your missions are {Occasionally(2,'located')} in {system_most}",
                       "{system_most} has {OneOf('the most','most of your')} missions in",
                       "{system_most} has the most missions for you to complete")}.
            }
        |else:
            You have {system_count} mission{if system_count > 1:s} in each of
            {set cur to 0}
            {set length to equal - 1}
            {while cur < equal:
                the {systems_names[cur]}
                {set cur to cur + 1}
                {if cur = length:, and
                |elif cur < length:,
                }
            } systems.
        }
    }

    {if !system_most && !nearest_name && missionsCount > 0:
        You have no active missions at the moment.
    }
|elif GetState("eddi_context_last_subject") != "jump":
    You {Occasionally(2,"currently")} have no missions at {OneOf("the moment","this time")}.
}

Mission check station.script:
{_ Mission check station }
{_ Check what missions you have for the current station }

{if missionsCount > 0:
    {set current_station to GetState("eddi_context_body_name")}
    {set active to 0}
    {set claim to 0}
    {set depot to 0}
    {set total to 0}

    {if current_station && current_station != "":
        {for mission in missions:
            {if mission.destinationstation = current_station || (mission.originstation = current_station && find(mission.type, "Entrega") > -1):
                {if mission.status = "Activo":
                    {if find(mission.type, "Recaudación") > -1 || find(mission.type, "Entrega") > -1:
                        {set depot to depot + 1}
                        {set total to total + 1}
                    |else:
                    	{set active to active + 1}
                    	{set total to total + 1}
                    }
                |elif mission.status = "Reclamar":
                    {set claim to claim + 1}
                    {set total to total + 1}
                }
            }
        }
    }

	{if total > 0:
    	{set description to []}
    	{if depot > 0:
    		{set depotDescription to:
				el almacén de mercancías de {depot} {if depot = 1: misión |else: misiones}
			}
			{set description to cat(description, [depotDescription])}
		}
		{if active > 0:
			{set activeDescription to:
				{active} {if active = 1: misión {OneOf("pendiente", "activa")} |else: misiones {OneOf("pendientes", "activas")}}
			}
			{set description to cat(description, [activeDescription])}
		}
		{if claim > 0:
			{set claimDescription to:
				{claim} {if claim = 1: misión |else: misiones} {OneOf("por reclamar", "para cobrar")} 
			}
			{set description to cat(description, [claimDescription])}
		}
 
        {OneOf("En esta estación tenemos {List(description)}", "Tenemos {List(description)}, en esta estación")}.

    }
}

Mission check system.script:
{_ Setup addstation function }
{set addstation(stationName, mission) to:
    {set missions_here to missions_here + 1}
    {set station_based to station_based + 1}
    {set here to find(stations_here, station)}
    {if here > -1:
        {if mission.statusEDName = "Active":
        	{if find(mission.type, "Recaudación") > -1 || find(mission.type, "Entrega") > -1:
                {set depot to stations_depot[here] + 1}
                {set stations_depot to union(stations_depot, [here:depot])}
            |else:
            	{set active to stations_active[here] + 1}
            	{set stations_active to union(stations_active, [here:active])}
            }

        |elif mission.statusEDName = "Claim":
            {set claim to stations_claim[here] + 1}
            {set stations_claim to union(stations_claim, [here:claim])}
        }
    |else:
    	{set stations_here to cat(stations_here, [station])}
        {if mission.statusEDName = "Active":
            {if find(mission.type, "Recaudación") > -1 || find(mission.type, "Entrega") > -1:
                {set stations_active to cat(stations_active, [0])}
                {set stations_depot to cat(stations_depot, [1])}
            |else:
                {set stations_active to cat(stations_active, [1])}
                {set stations_depot to cat(stations_depot, [0])}
            }
            {set stations_claim to cat(stations_claim, [0])}
        |elif mission.statusEDName = "Claim":
            {set stations_active to cat(stations_active, [0])}
            {set stations_claim to cat(stations_claim , [1])}
            {set stations_depot to cat(stations_depot, [0])}
        }
    }
}

{if missionsCount + goalsCount > 0:
    {set missions_here to 0}
    {set station_based to 0}
    {set stations_active to []}
    {set stations_claim to []}
    {set stations_depot to []}
    {set stations_here to []}
    {set non_stations to []}

    {_ Find all missions in the current system and identify all the stations }
    {for mission in missions:
        {if mission.statusEDName != "Failed":
            {if mission.destinationsystems:
                {for destination in mission.destinationsystems:
                    {if destination.name = system.name && !destination.visited:
                        {set missions_here to missions_here + 1}
                        {if find(mission.invariantTags, "Transport") > -1:
                            {set type to "Transporte de "}
                            {set type to cat(type, mission.passengertype)}
                        |else:
                            {set type to mission.tags[0]}
                        }

                        {if find(non_stations, type) = -1:
                            {set non_stations to cat(non_stations, [type])}
                        }
                    }
                }
            |elif mission.destinationsystem = system.name:
                {if mission.destinationstation && mission.destinationstation != "":
                    {addstation(mission.destinationstation, mission)}
                |else:
                    {if mission.statusEDName = "Active":
                        {set missions_here to missions_here + 1}
                        {if find(mission.invariantTags, "Transport") > -1:
                            {set type to "Transporte de "}
                            {set type to cat(type, mission.passengertype)}
                        |else:
                            {set type to mission.tags[0]}
                        }
                        {if find(non_stations, type) = -1:
                            {set non_stations to cat(non_stations, [type])}
                        }
                    }
                }
            |elif mission.originsystem = system.name && find(mission.invariantTags, "Delivery") > -1:
                {if mission.originstation && mission.originstation != "":
                    {addstation(mission.originstation, mission)}
                }
            }
        }
    }

    {if missions_here > 0:
        {set missionSummaries to []}
        {set cur to 0}
        {for station in stations_here:
            {set estacion to StationDetails(station)}
            {set active to stations_active[cur]}
            {set claim to stations_claim[cur]}
            {set depot to stations_depot[cur]}
            {set summaries to []}

            {if active > 0:
                {set activeSummary to: 
                    {active}
                    {if active = 1:
                    	{Occasionally(len(summaries)*50, "misión")} {OneOf("activa", "pendiente")}
                    |else:
                    	{Occasionally(len(summaries)*50, "misiones")} {OneOf("activas", "pendientes")}
                    }
                }
                {set summaries to cat(summaries, [activeSummary])}
            }
            {if claim > 0:
                {set claimSummary to: 
                    {claim}
                    {if claim = 1:
                    	{Occasionally(len(summaries)*50, "misión")}
                    |else:
                    	{Occasionally(len(summaries)*50, "misiones")}
                    } {OneOf("por reclamar", "para cobrar")}
                }
                {set summaries to cat(summaries, [claimSummary])}
            }
            {if depot > 0:
            	{set depotSummary to: 
                    {depot}
                    {if depot = 1:
                    	almacén de recogidas
                    |else:
                    	almacenes de recogidas
                    }
                }
                {set summaries to cat(summaries, [depotSummary])}
            }

            {if len(summaries) > 0:
            	{if estacion.model != "Estación desconocida" && estacion.model:
            		{creaArticulos(estacion.model)}
            		{set modelo to cat(", en ", Occasionally(2, '{el} {P(estacion.model)}'), " ", station)}
            	}
                {set missionSummaries to cat(missionSummaries, [List(summaries)], modelo)}
            }
            {set cur to cur + 1}
        }
        
        {set remaining to missions_here - station_based}
        {if remaining > 0:
            {set otherMissions to:
                {if remaining > 1: {if len(missionSummaries) > 0: otras} {remaining} misiones |else: {if len(missionSummaries) > 0: otra |else: {remaining}} misión} {if len(missionSummaries) > 0: sin estación de destino}
            }
            {set missionSummaries to cat(missionSummaries, [otherMissions])}
        }  

        {if len(missionSummaries) > 0:
            {Pause(500)}
            {set report to cat(OneOf("Aquí", "En este sistema"), " tienes ", List(missionSummaries))}
            {leeTexto(report)}.
        }
    }
}

Mission completed.script:
{_ Mission completed }
{_ Triggered when you complete a mission }


{_ Context }
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'complete')}

{set mission to MissionDetails(event.missionid)}
{set ranktype to token(mission.name,"_",2)}

{if cmdr.gender = "Female":
    {set o to "a"}
|else:
    {set o to "o"}
}

{set m to "a"}
{set la to "la"}

{if find(mission.typeEDName, "Passenger") > -1:
	{set m to "o"}
	{set la to "el"}
	{set type to "transporte de "}
    {set type to cat(type, mission.passengertype)}
|elif mission.type = "Hackeo":
	{set type to "misión de jaqueo"}
|elif mission.type = "Hack Megaship":
	{set type to "misión de jaqueo de mega-nave"}
|elif mission.type = "On Foot":
	{set type to "misión a pie"}
|elif mission.type = "Recaudación":
	{set type to cat("misión de ", OneOf('petición', 'adquisición') , " de mercancías")}
|elif mission.type = "Recaudación de escuadrón":
	{set type to cat("misión de ", OneOf('petición', 'adquisición') , " de mercancías para escuadrón")}
|elif mission.type:
	{set type to cat("misión de ", mission.type)}
|else:
	{set type to "misión"}
}

{if mission.type = "Donativo":
	{if event.amount && event.commodity:
		{set donate to cat(event.amount, " toneladas de ",event.commodity)}
	|else:
		{set donate to humaniza(event.donation)}
		{set donate to cat(donate, " ", de, " créditos")}
	}

	{OneOf("Ha hecho un donativo {Occasionally(2, 'de {donate}')} a la {Occasionally(2, 'agradecida')} facción {P(mission.faction)}",
		"{P(mission.faction)} ha recibido su donación {Occasionally(2, 'de {donate}')}",
		"{P(mission.faction)} le da las gracias por su donación {Occasionally(2, 'de {donate}')}"
	)}.

|elif lcase(ranktype) = "rankemp" || lcase(ranktype) = "rankfed":
	{OneOf("Ha completado la misión de {type} satisfactoriamente", "La misión de {type} se ha completado satisfactoriamente")}.

|elif mission.communal:
	Ha completado el objetivo de comunidad: {mission.name}.
	{set recompensa to humaniza(mission.reward)}
	{if find(recompensa,"ón",len(recompensa)-3)!=-1 || find(recompensa,"nes",len(recompensa)-4)!=-1 || find(recompensa,"dio",len(recompensa)-4)!=-1: {set de to "de"}}
	Ha {OneOf('recibido','sido recompensad{o} con')} {recompensa} {de} créditos por su {OneOf("contribución","participación","esfuerzo")}.

|else:
	{if event.reward > 0:
	    
		{set recompensa to humaniza(event.reward)}
		{if find(recompensa,"ón",len(recompensa)-3)!=-1 || find(recompensa,"nes",len(recompensa)-4)!=-1 || find(recompensa,"dio",len(recompensa)-4)!=-1: {set de to "de"}}
		{OneOf("{type} completad{m}. Has {OneOf('recibido','sido recompensad{o} con')} {recompensa} {de} créditos",
			"Has {OneOf('recibido','sido recompensad{o} con')} {recompensa} {de} créditos por {OneOf('finalizar {la} {type}','completar {la} {type} satisfactoriamente', 'completar satisfactoriamente {la} {type}')}"
		)}
	|else:

		{OneOf("{type} completad{m}","Has completado satisfactoriamente {la} {type}",
			"{la} {type} ha sido completado satisfactoriamente"
		)}
	}
}

{Occasionally(2, F("Mission faction state"))}.

{if event.rewardCommodity && event.rewardCommodity != "":
    {Pause(100)}
    {if event.rewardCommodityAmount = 0:
        {if event.reward > 0:
            {OneOf("Sin embargo", "Pero")} 
        }
        no puede aceptar la recompensa en cargamento, {Occasionally(2,"dado que")}
        {if ship.cargocapacity = 0:
            no queda espacio disponible en la nave.
        |else:
            la bahía de carga está {Occasionally(2,"completamente ")}llena.
        }
    |else:         
        {if event.reward > 0:
            {OneOf("Además", "También")} 
        }
        
        {OneOf("{event.rewardCommodityAmount} tonelada{if event.rewardCommodityAmount > 1:s} de {event.rewardCommodity} han sido cargadas a la nave",
               "{event.rewardCommodityAmount} tonelada{if event.rewardCommodityAmount > 1:s} de {event.rewardCommodity} han sido añadidas a nuestro cargamento",
               "hemos recibido {event.rewardCommodityAmount} tonelada{if event.rewardCommodityAmount > 1:s} de {event.rewardCommodity} como recompensa",
               "han cargado a la nave {event.rewardCommodityAmount} tonelada{if event.rewardCommodityAmount > 1:s} de {event.rewardCommodity} como recompensa",
             )}.
    }
|elif event.rewardMaterial && event.rewardMaterial != "":
    {if event.reward > 0:
        {OneOf("Además", "También")} 
    }
 {OneOf("ha recibido", "ha sido recompensado con")}
    {event.rewardMaterialAmount} unidad{if event.rewardMaterialAmount > 1:es} de {event.rewardMaterial}
    .
}

{_ Update to next system in route if conditions met _}
{Pause(1000)}
{set next_system to RouteDetails("update")}

