Route details.script:
{_ Context _}
{SetState('eddi_context_search_system', event.system)}
{SetState('eddi_context_search_station', event.station)}
{SetState('eddi_context_search_distance', event.distance)}
{SetState('eddi_context_search_count', event.count)}

{set clip_notify to false}
{set type to event.routetype}
{if type = "encoded" || type = "facilitator" || type = "guardian" || type = "human" || type = "manufactured" || type = "raw" || type = "scorpion":

    {if type = "facilitator":
        {set service to "contacto de Interestélar fáctor"}
    |elif type = "encoded":
        {set service to "comerciante de datos codificados"}
    |elif type = "manufactured":
        {set service to "comerciante materiales manufacturados"}
    |elif type = "raw":
        {set service to "comerciante de materias primas")}
    |elif type = "guardian":
        {set service to "broker de tecnología guardián"}
    |elif type = "human":
        {set service to "broker de tecnología humana")}
    |elif type = "scorpion":
        {set service to "vendedor de V R S scorpion"}
    }

    {if event.system && event.system != "":
        El {service} más cercano se encuentra en {P(event.station, "station")}, en el
        {if event.system = system.name:
            {OneOf("actual sistema", "sistema actual")}
        |else:
            {set clip_notify to true}
            {SetClipboard(event.system)}
            sistema {P(event.system, "starsystem")}, a {Occasionally(2, "aproximadamente")} {round(event.distance, 1)} años luz
        }.
    |else:
        No se ha encontrado ningún {service} dentro del rango de los sensores.
    }
|elif type = "expiring":
    {if event.system && event.system != "":
        {set mission to MissionDetails(event.missionids[0])}

        {if find(mission.invariantTags, "Transport") > -1:
            {set type to "de transporte de "}
            {set type to cat(type, mission.passengertype)}
        |else:
            {set type to mission.tags[0]}
        }
        {if type = "Hackeo": {set type to "de jaqueo"}
        |elif type = "Hack Megaship": {set type to "de jaqueo de mega-nave"}
        |elif type = "On Foot": {set type to "a pie"}
        |elif type = "Recaudación": {set type to cat("de ", OneOf('petición', 'adquisición') , " de mercancías")}
        |elif type = "Recaudación de escuadrón": {set type to cat("de ", OneOf('petición', 'adquisición') , " de mercancías para escuadrón")}
        }
        {set type to cat("misión ", type)}

        La {type} {if mission.faction: para {P(mission.faction, "faction")}}
        {if mission.destinationsystem: en el
            {if mission.destinationsystem = system.name:
                {OneOf("actual sistema", "sistema actual")}
            |else:
                {set clip_notify to true}
                {SetClipboard(mission.destinationsystem)}
                sistema {P(mission.destinationsystem, "starsystem")}
            }
        } está a punto de expirar.
    |else:
        No se han encontrado misiones activas.
    }
|elif type = "farthest":
    {if event.system && event.system != "":
        El objetivo {if missionsCount = 1: de la única misión que tienes |else: de misión más lejano} se encuentra en el
        {if event.system = system.name:
            {OneOf("actual sistema", "sistema actual")}
        |else:
            {set clip_notify to true}
            {SetClipboard(event.system)}
            sistema {P(event.system, "starsystem")}, a {Occasionally(2, "aproximadamente")} {round(event.distance, 1)} años luz
        }.
    |else:
        No se han encontrado misiones con un sistema de destino designado.
    }
|elif type = "most":
    {if missionsCount = 1:
        El único objetivo de misión que tenemos está {Occasionally(2,"localizado")} en
        {if missions[0].destinationsystem = "":
            un sistema {Occasionally(2, "estelar")} desconocido
        |elif missions[0].destinationsystem = system.name:
            el {OneOf("actual sistema", "sistema actual")}
        |else:
            el sistema {Occasionally(2, "estelar")} {P(missions[0].destinationsystem, "starsystem")}
        }.
    |elif event.system && event.system != "":
        {set systems to split(event.route, "_")}
        {set systems_count to len(systems)}

        {if systems_count = 1:
            {if event.system = system.name:
                {set system_most to 'el {OneOf("actual sistema", "sistema actual")}'}
            |else:
                {set clip_notify to true}
                {SetClipboard(event.system)}
                {set system_most to cat("el sistema ", Occasionally(2, "estelar "), P(event.system, 'starsystem'))}
            }
            {OneOf("La mayoría de las misiones están {Occasionally(2,'localizadas')} en {system_most}",
                "{system_most} tiene {OneOf('más','la mayoría de las')} misiones",
                "{system_most} tiene la mayoría de las misiones para completar")
            }.
        |elif systems_count > 2:
            Tenemos {event.count} objetivo{if event.count > 1:s} de misión en los sistemas
            {_ Don't include the home system from the route list _}
            {set cur to 0}
            {set systems_count to systems_count - 1}
            {while cur < len(systems_count):
                {P(systems[cur], "starsystem")}
                {set cur to cur + 1}
                {if cur = systems_count-1: y |elif cur < systems_count: ,}
            }.
        }
    |else:
        No se han encontrado misiones con un sistema de destino designado.
    }
|elif type = "nearest":
    {if event.system && event.system != "":
        El objetivo {if missionsCount = 1: de la única misión que tienes |else: de misión más cercano} se encuentra en el
        {if event.system = system.name:
            {OneOf("actual sistema", "sistema actual")}
        |else:
            {set clip_notify to true}
            {SetClipboard(event.system)}
            sistema {P(event.system, "starsystem")}, a {Occasionally(2, "aproximadamente")} {round(event.distance, 1)} años luz
        }.
    |else:
        No se han encontrado misiones con un sistema de destino designado.
    }
|elif type = "route":
    {if event.system && event.system != "":
        Ruta de misiones calculada para {event.count - 1} sistema{if event.count - 1 > 1: s}.
        La distancia total de la ruta es de {Occasionally(2, "aproximadamente")} {round(event.routedistance, 1)} años luz.
        {if event.system != system.systemname:
            El primer destino es el sistema {P(event.system, "starsystem")}, a {round(event.distance, 1)} años luz de {OneOf("distancia","aquí", "")}.
            {set clip_notify to true}
            {SetClipboard(event.system)}
        |else:
            {F("Mission check system")}
        }
    |else:
        Imposible calcular una ruta de misiones.
    }
|elif type = "source":
    {if event.count > 0:
        {set systems to split(event.route, "_")}

        {set pos to 0}
        {while pos < event.count:

            {set source_cargo to []}
            {for missionid in event.missionids:
                {set mission to MissionDetails(missionid)}
                {if mission.sourcesystem = systems[pos]:
                    {set cargo to CargoDetails(missionid)}
                    {if find(source_cargo, cargo.name) = -1:
                        {set source_cargo to cat(source_cargo, [cargo.name])}
                    }
                }
            }

            {if pos = 0:
                El {if event.count = 1: único punto de recogida |else: punto de recogida más cercano}
            |elif pos = 1:
                además, el punto de recogida
            |elif pos = event.count - 1:
                por último, el punto de recogida
            |else:
                el punto de recogida
            }
            de
            {set cur to 0}
            {while cur < len(source_cargo):
                {source_cargo[cur]}
                {set cur to cur + 1}
                {if cur = len(source_cargo) - 1: y |elif cur < len(source_cargo): ,}
            }
            está en el
            {if systems[pos] = system.name:
                {OneOf("actual sistema", "sistema actual")}
            |else:
                {set clip_notify to true}
                {SetClipboard(systems[pos])}
                sistema {Occasionally(2, "estelar ")} {P(systems[pos], "starsystem")},
                {set distance to Distance(system.name, systems[pos])}
                a {round(distance, 1)} años luz
            }.
            {set pos to pos + 1}
       }
         {SetState('eddi_context_cargo_source', event.route)}
    |else:
        Origen de cargamento no asignado a ningún sistema concreto.
    }
|elif type = "update":
    {if event.system && event.system != "":
        {if event.system != system.systemname && event.system != nextsystem.systemname:
            {_ Set Next system (unless we are already en route) }
            El próximo {OneOf("punto de ruta", "sistema", "sistema estelar", "destino")} es {P(event.system, "starsystem")}, a {Occasionally(2, "aproximadamente")} {round(event.distance, 1)} años luz.
            {set clip_notify to true}
            {SetClipboard(event.system)}
        |else:
            {_ Next station }
            {F("Mission check system")}
        }
    }
|elif type = "scoop":
    {if event.system && event.system != "":
        {set title to Occasionally(3,"{F('Honorific')}")}
        {if event.system = system.name:
            {set scoopable_bodies to []}
            {for body in system.bodies:
                {if body.scoopable:
                    {set scoopable_bodies to cat(scoopable_bodies, [P(body.shortname, "body")])}
                }
            }
            {if len(scoopable_bodies) > 1:
                {set s to "s"}
                {set report to cat("son las: ", List(scoopable_bodies))}
            |else:
                {set s to ""}
                {set report to cat("es la ", Occasionally(2, 'estrella'), " ", scoopable_bodies[0])}
            }
            {set report to cat(report, Pause(50), " del sistema actual.")}
        |else:
            {set report to cat("está en el sistema ", P(event.system, "starsystem"), " que está a ", Occasionally(2, "aproximadamente"), " ", round(event.distance, 1), " años luz de aquí ")}
            {set clip_notify to true}
            {SetClipboard(event.system)}
        }
        La{s} estrella{s} recolectable{s} más cercana{s} {report}.
        {Pause(500)}
        {if maxjump < 0.5:
            {OneOf("Es imperativo el reabastecimiento {Occasionally(2, 'de combustible')}", "Reabastezca el combustible urgentemente", "El reabastecimiento {Occasionally(2, 'de combustible')} es extremadamente urgente")}, {title}.
        |else:
            {OneOf("Por favor, haga un repostaje {Occasionally(2, 'de combustible')} cuanto antes", "Se recomienda encarecidamente que recargue combustible", "El reabastecimiento {Occasionally(2, 'de combustible')} es altamente recomendable")}, {title}.
        }
    |else:
        {OneOf("Peligro", "Alerta", "Atención")}. No hay estrellas recolectables en el rango del próximo salto.
        {Pause(250)}
        Se recomienda el apagado de sistemas no esenciales.
        {Pause(250)}
        Transpondedor de emergencia a la espera, {title}.
    }
|elif type = "neutron":
    {if event.system && event.system != "" && event.system != system.systemname:
        Ruta por autopista de neutrones calculada.
        {if ship && ship.phoneticname:
            {ship.phoneticname}
        |else:
            Su {ship.phoneticmodel}
        }
        llegará a 
        {event.route[event.count - 1]}
        en
        {event.count - 1} {if event.count - 1 = 1: salto. |else: saltos.}
        {if event.count - 1 > 1: 
            La distancia total de la ruta es de {Occasionally(2, "aproximadamente")} {round(event.routedistance, 1)} años luz.
            El primer destino es {P(event.system, "starsystem")}, a {round(event.distance)} años luz.
        }
        {set clip_notify to true}
        {SetClipboard(event.system)}
    |else:
        Imposible calcular ruta por autopista de neutrones.
    }
|elif type = "carrier":
    {if event.system && event.system != "" && event.system != system.systemname:
        Ruta calculada.
        Route calculated. 
        {if carrier && carrier.name:
            {carrier.name}
        |else:
            Su {OneOf("portanaves", "carrier", "portaflotas")}
        }
        llegará a 
        {event.route[event.count - 1]}
        en
        {event.count - 1} {if event.count - 1 = 1: salto. |else: saltos.}
        {if event.count - 1 > 1: 
            Se necesitan {event.tritiumused} toneladas de tritio para completar el trayecto.
            El primer destino es {P(event.system, "starsystem")}, a {round(event.distance)} años luz.
        }
        {set clip_notify to true}
        {SetClipboard(event.system)}
    |else:
        Imposible calcular la ruta de {OneOf("la portanaves", "el carrier", "la portaflotas")}.
    }
|elif type = "set":
    Ayuda de navegación activada.
|elif type = "cancel":
    Ayuda de navegación desactivada.
|elif type = "recalculating":
    Recalculando {Occasionally(2, "ruta")}.
}

{if clip_notify && !va_active:
    {Pause(200)}
    Sistema copiado al portapapeles.
}

