Star report.script:
	
{_ Fetch from context }
{set reportbody to BodyDetails(GetState("eddi_context_star_star"), GetState("eddi_context_star_system"))}
{if !reportbody: {set reportbody to BodyDetails(GetState("eddi_context_star_star"))}}
{if !reportbody && event: {set reportbody to event.star}}

{if !reportbody.name || reportbody.name = "":

    No consigo saber a que estrella se refiere.

|else:

    {_ Set up a list of notable star characteristics _}
    {set notables to []}

    {_ Set up various groups of stars _}
    {set mainsequence to ["O", "B", "A", "F", "G", "K", "M"]}
    {set fastburning to ["WC", "WN", "WO", "WNC", "W"]}
    {set browndwarves to ["L", "T", "Y"]}

    {for starclass in mainsequence:
        {if reportbody.stellarclass = starclass:
            {set mainsequence to Occasionally(mainSequenceFrequency, "de secuencia principal")}
            {set mainsequence to 1}
        }
    }    
    {for starclass in browndwarves:
        {if reportbody.stellarclass = starclass:
            {set browndwarf to 1}
        }
    }    
    {for starclass in fastburning:
        {if reportbody.stellarclass = starclass: 
            {set fastburning to 1}
        }
    }

    {_ Is this a rare class of star? _}
    {if reportRareStarClasses:
        {if reportbody.starClass.percentage < 0.01:
            {set rarity to "Increiblemente rara"}
        |elif reportbody.starClass.percentage < 0.1:
            {set rarity to "Muy rara"}
        |elif reportbody.starClass.percentage < 1.0:
            {set rarity to "Rara"}
        |elif reportbody.starClass.percentage < 5:
            {set rarity to "Poco común"}
        }
    }

    {_ Is the star notable in some other way? _}

    {if reportAge && rand(reportAge) = 0:
        {set ageInYears to reportbody.age * 1000000}
        {if ageInYears <= 1000000:
            {set ps to ", por lo que es una de las estrellas más jóvenes de la galaxia"}
        |elif ageInYears >= 13000000000:
            {set ps to ", por lo que es una de las estrellas más {OneOf('lonjevas', 'viejas', 'ancianas')} de la galaxia"}
        }
        {if reportbody.ageprobability:
            {if reportbody.ageprobability < 0.01:
                {set notables to cat(notables, [cat("una edad increiblemente baja de ", humaniza(ageInYears), " años", ps)])}
            |elif reportbody.ageprobability < 0.1:
                {set notables to cat(notables, [cat("una edad extremadamente baja de ", humaniza(ageInYears), " años", ps)])}
            |elif reportbody.ageprobability < 5:
                {set notables to cat(notables, [cat("una edad inusualemente baja de ", humaniza(ageInYears), " años", ps)])}
            |elif reportbody.ageprobability > 99.99:
                {set notables to cat(notables, [cat("una edad increiblemente alta de ", humaniza(ageInYears), " años", ps)])}
            |elif reportbody.ageprobability > 99.9:
                {set notables to cat(notables, [cat("una edad extremadamente alta de ", humaniza(ageInYears), " años", ps)])}
            |elif reportbody.ageprobability > 95:
                {set notables to cat(notables, [cat("una edad inusualmente alta de ", humaniza(ageInYears), " años", ps)])}
            }
        }
        {set notables to Occasionally(reportAge, notables)}
    }

    {if reportMass && reportbody.massprobability:
        {if reportbody.massprobability < 0.01:
            {set notables to cat(notables, [cat("una masa solar increíblemente baja de ", humaniza(reportbody.solarmass, 1), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.massprobability < 0.1:
            {set notables to cat(notables, [cat("una masa solar extremadamente baja de ", humaniza(reportbody.solarmass, 1), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.massprobability < 1:
            {set notables to cat(notables, [cat("una masa solar inusualmente baja de ", humaniza(reportbody.solarmass, 1), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.massprobability > 99.99:
            {set notables to cat(notables, [cat("una masa solar increíblemente alta de ", humaniza(reportbody.solarmass, 1), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.massprobability > 99.9:
            {set notables to cat(notables, [cat("una masa solar extremadamente alta de ", humaniza(reportbody.solarmass, 1), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.massprobability > 99:
            {set notables to cat(notables, [cat("una masa solar inusualmente alta de ", humaniza(reportbody.solarmass, 1), Occasionally(2, 'veces la del sol'))])}
        }
    }

    {if reportRadius && reportbody.radiusprobability:
        {if reportbody.radiusprobability < 0.01:
            {set notables to cat(notables, [cat("un radio solar increíblemente bajo de ", humaniza(reportbody.solarradius), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.radiusprobability < 0.1:
            {set notables to cat(notables, [cat("un radio solar extremadamente bajo de ", humaniza(reportbody.solarradius), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.radiusprobability < 5:
            {set notables to cat(notables, [cat("un radio solar inusualmente bajo de ", humaniza(reportbody.solarradius), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.radiusprobability > 99.99:
            {set notables to cat(notables, [cat("un radio solar extremadamente alto de ", humaniza(reportbody.solarradius), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.radiusprobability > 99.9:
            {set notables to cat(notables, [cat("un radio solar increíblemente alto de ", humaniza(reportbody.solarradius), Occasionally(2, 'veces la del sol'))])}
        |elif reportbody.radiusprobability > 99:
            {set notables to cat(notables, [cat("un radio solar inusualmente alto de ", humaniza(reportbody.solarradius), Occasionally(2, 'veces la del sol'))])}
        }
    }

    {if reportRotation && reportbody.rotationalperiodprobability:
        {if reportbody.rotationalperiodprobability > 99.99:
            {set notables to cat(notables, [cat(OneOf( "una velocidad{set a to 'a'}", "un ratio{set a to 'o'}"), " de rotación extremadamente ", OneOf("alt{a}", "rápid{a}"), " de ", humaniza(reportbody.rotationalperiod), " días ", Occasionally(2, "por revolución"))])}
        |elif reportbody.rotationalperiodprobability > 99.9:
            {set notables to cat(notables, [cat(OneOf( "una velocidad{set a to 'a'}", "un ratio{set a to 'o'}"), " de rotación increíblemente ", OneOf("alt{a}", "rápid{a}"), " de ", humaniza(reportbody.rotationalperiod), " días ", Occasionally(2, "por revolución"))])}
        |elif reportbody.rotationalperiodprobability > 99:
            {set notables to cat(notables, [cat(OneOf( "una velocidad{set a to 'a'}", "un ratio{set a to 'o'}"), " de rotación inusualmente ", OneOf("alt{a}", "rápid{a}"), " de ", humaniza(reportbody.rotationalperiod), " días ", Occasionally(2, "por revolución"))])}
        |elif reportbody.rotationalperiodprobability > 95:
            {set notables to cat(notables, [cat(OneOf( "una velocidad{set a to 'a'}", "un ratio{set a to 'o'}"), " de rotación ", OneOf("alt{a}", "rápid{a}"), " de ", humaniza(reportbody.rotationalperiod), " días ", Occasionally(2, "por revolución"))])}
        }
    }
    
    {if reportTemps && reportbody.tempprobability:
        {if reportbody.tempprobability < 0.01:
            {set notables to cat(notables, [cat("una temperatura superficial increíblemente baja de ", humaniza(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability < 0.1:
            {set notables to cat(notables, [cat("una temperatura superficial extremadamente baja de ", humaniza(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability < 1:
            {set notables to cat(notables, [cat("una temperatura superficial inusualmente baja")])}
        |elif reportbody.tempprobability < 5:
            {set notables to cat(notables, [cat("una temperature superficial inferior a la media")])}
        |elif reportbody.tempprobability > 99.99:
            {set notables to cat(notables, [cat("una temperatura superficial extremadamente alta ", humaniza(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability > 99.9:
            {set notables to cat(notables, [cat("una temperatura superficial increíblemente alta de ", humaniza(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability > 99:
            {set notables to cat(notables, [cat("una temperatura superficial inusualmente alta")])}
        |elif reportbody.tempprobability > 95:
            {set notables to cat(notables, [cat("una temperatura superficial superior a la media")])}
        }
    }
    
    {if reportDensity && reportbody.densityprobability:
        {if reportbody.densityprobability < 0.01:
            {set notables to cat(notables, [cat("una densidad media increíblemente baja de ", humaniza(reportbody.density), " kilogramos por metro cúbico")])}
        |elif reportbody.densityprobability < 0.1:
            {set notables to cat(notables, [cat("una densidad media extremadamente baja de ", humaniza(reportbody.density), " kilogramos por metro cúbico")])}
        |elif reportbody.densityprobability < 1:
            {set notables to cat(notables, [cat("una densidad media inusualemente baja")])}
        |elif reportbody.densityprobability < 5:
            {set notables to cat(notables, [cat("una densidad media inferior a lo normal")])}
        |elif reportbody.densityprobability > 99.99:
            {set notables to cat(notables, [cat("una densidad media extremadamente alta de ", humaniza(reportbody.density), " kilogramos por metro cúbico")])}
        |elif reportbody.densityprobability > 99.9:
            {set notables to cat(notables, [cat("una densidad media increíblemente alta de ", humaniza(reportbody.density), " kilogramos por metro cúbico")])}
        |elif reportbody.densityprobability > 99:
            {set notables to cat(notables, [cat("una densidad media inusualemente alta")])}
        |elif reportbody.densityprobability > 95:
            {set notables to cat(notables, [cat("una densidad media superior a lo normal")])}
        }
    }

    {if reportRapidRotation && reportbody.rotationalperiodprobability:
        {if reportbody.rotationalperiodprobability < 0.1:
            {set notables to cat(notables, [cat("un periodo rotacional increiblemente corto de ", humaniza(reportbody.rotationalperiod), " días")])}
        |elif reportbody.rotationalperiodprobability < 1:
            {set notables to cat(notables, [cat("un periodo rotacional extremadamente corto de ", humaniza(reportbody.rotationalperiod), " días")])}
        |elif reportbody.rotationalperiodprobability < 3:
            {set notables to cat(notables, [cat("un periodo rotacional inusualmente corto de ", humaniza(reportbody.rotationalperiod), " días")])}
        }
    }

    {if reportAbsMagnitude && reportbody.absolutemagnitudeprobability:
        {if reportbody.absolutemagnitudeprobability < 0.01:
            {set notables to cat(notables, [cat(OneOf("increiblemente brillante", "increiblemente luminosa"))])}
        |elif reportbody.absolutemagnitudeprobability < 0.1:
            {set notables to cat(notables, [cat(OneOf("extremadamente brillante", "extremadamente luminosa"))])}
        |elif reportbody.absolutemagnitudeprobability < 1:
            {set notables to cat(notables, [cat(OneOf("inusualmente brillante", "inusualmente luminosa"))])}
        |elif reportbody.absolutemagnitudeprobability < 5:
            {set notables to cat(notables, [cat(OneOf("de alto brillo", "de alta luminosidad"))])}
        }
    }

    {_ Is the star ringed? _}
    {if reportRingedStars:
        {if reportbody.rings:
            {set ringcompositions to []}
            {set cur to 0}
            {for ring in reportbody.rings:
                {if find(ring.name, "Ring") > -1:
                    {if find(ringcompositions, ring.composition) = -1:
                        {set ringcompositions to cat(ringcompositions, [cat(ring.composition)])}
                    }
                }
                {set cur to cur + 1}
            }
            {set cur to 0}
            {set length to len(ringcompositions) - 1}
            {while cur < len(ringcompositions):
                {set ringsdescription to cat(ringsdescription, ringcompositions[cur])}
                {set cur to cur + 1}
                {if cur = length: 
                    {set ringsdescription to cat(ringsdescription, " y ")}
                |elif cur < length:
                    {set ringsdecription to cat(ringsdescription, ", ")}
                }
            }
            {if ringsdescription:
                {if len(ringcompositions) = 1:
                    {set rings to "anillo"}
                |else:
                    {set rings to "anillos"} 
                }
                {if reportbody.reserveLevel.invariantName != "None": 
                    {set reserves to reportbody.reserves}
                }
                {set notables to cat(notables, [cat(rings, reserves, " de ", ringsdescription)])}
            }
        }
    }

    {_ Report notable stars _}

    {if len(notables) > 0:

        {_ Setup descriptions for our star _}
        
        {if find(["L", "T", "Y"], reportbody.stellarclass) > -1:
            {set shortdescription to "enana marrón"}
        |elif reportbody.stellarclass = "TTS":
            {set class to ""}  
            {set shortdescription to "estrella T tauri"}
            {set contraccion to OneOf('colapso gravitatorio', 'colapso gravitacional', 'contracción gravitacional')}
            {set desc2 to Occasionally(2, '{OneOf("en proceso de {contraccion}", "que es tan joven que aún no ha entrado en su secuencia principal", "que se encuentra en una fase de pre-secuencia principal")}')}
        |elif reportbody.stellarclass = "AEBE":
            {set age to "muy joven"}
            {set shortdescription to "protoestrella"}
            {set desc2 to Occasionally(2, "que aún no ha empezado su secuencia principal {Occasionally(4, ', pero está en camino de convertirse en una estrella de clase A, o B')}")}
        |elif reportbody.stellarclass = "WC":
            {set desc1 to "rica en carbono{Occasionally(2, ', de combustión rápida')}"}
            {set shortdescription to "estrella Wolf-Rayet"}
            {set desc2 to Occasionally(2, ", que está perdiendo una masa considerable y expone el carbono ionizado de su núcleo, ")}
        |elif reportbody.stellarclass = "WN":
            {set desc1 to "rica en helio y nitrógeno{Occasionally(2, ' de combustión rápida')}"}
            {set shortdescription to "estrella Wolf-Rayet"}
            {set desc2 to Occasionally(2, ", que está perdiendo una masa considerable y expone helio ionizado y nitrógeno de su núcleo, ")}
        |elif reportbody.stellarclass = "WO":
            {set desc1 to "rica en oxígeno{Occasionally(2, ' de combustión rápida')}"}
            {set shortdescription to "estrella Wolf-Rayet"}
            {set desc2 to Occasionally(2, ", que está perdiendo una masa considerable y expone oxígeno ionizado de su núcleo, ")}
        |elif reportbody.stellarclass = "WNC":
            {set shortdescription to "estrella Wolf-Rayet"}
            {set desc2 to Occasionally(2, ", que está perdiendo una masa considerable y expone nitrógeno ionizado, carbono, oxígeno, y helio de su núcleo, ")}
        |elif reportbody.stellarclass = "W":
            {set shortdescription to "estrella Wolf-Rayet"} 
            {set desc2 to Occasionally(2, "que está perdiendo una masa considerable y expone los elementos más pesados de su núcleo, ")}
        |elif reportbody.stellarclass = "CS":
            {set shortdescription to "estrella de carbono"}
        |elif find(["C", "CN", "CH", "CHd"], reportbody.stellarclass) > -1:
            {set age to Occasionally(2, "de secuencia tardía")} 
            {set shortdescription to "estrella rica en carbono"}
        |elif reportbody.stellarclass = "CJ":
            {set age to Occasionally(2, "de secuencia tardía")} 
            {set shortdescription to "estrella de carbono"}
            {set desc2 to Occasionally(2, "con altos niveles de carbono 13 en su atmósfera")}
        |elif find(["MS", "S"], reportbody.stellarclass) > -1:
            {set age to Occasionally(2, "de secuencia tardía")} 
            {set shortdescription to "estrella"}
            {set desc2 to Occasionally(4, "que una vez fue una Estrella Clase M, y ahora está en camino de convertirse en una estrella de carbono")}
        |elif reportbody.stellarclass = "DA":
            {set shortdescription to "enana blanca"}
            {set desc2 to Occasionally(4, "con una atmósfera rica en hidrógeno")}
        |elif reportbody.stellarclass = "DAB":
            {set shortdescription to "enana blanca"}
            {set desc2 to Occasionally(4, "con una atmósfera rica en hidrógeno y helio")}
        |elif slice(reportbody.stellarclass, 0, 1) = "D":
            {set shortdescription to "enana blanca"}
        |elif slice(reportbody.stellarclass, 0, 1) = "N":
            {set shortdescription to "neutron star"}
            {set desc2 to Occasionally(4, "que tiene un peligroso núcleo de alta densidad")}
        |elif reportbody.stellarclass = "H":
            {set shortdescription to "agujero negro"}
        |elif reportbody.stellarclass = "X":
            {set shortdescription to "entidad estelar exótica"}
        |elif reportbody.stellarclass = "SuperMassiveBlackHole":
            {set shortdescription to "agujero negro supermasivo"}
        |elif reportbody.stellarclass = "A_BlueWhiteSuperGiant":
            {set class to Spacialise('A')} 
            {set shortdescription to "supergigante blanco-azulada"}
            {set desc2 to Occasionally(4, "que ha comenzado a expandirse para transformarse en una supergigante roja")}
        |elif reportbody.stellarclass = "F_WhiteSuperGiant":
            {set class to Spacialise('F')} 
            {set shortdescription to "supergigante blanca"}
            {set desc2 to Occasionally(4, "que ha comenzado a expandirse para transformarse en una supergigante roja")}
        |elif reportbody.stellarclass = "M_RedSuperGiant":
            {set class to Spacialise('M')} 
            {set shortdescription to "supergigante roja"}
            {set desc2 to Occasionally(4, "en su fase de combustión de helio")}
        |elif reportbody.stellarclass = "M_RedGiant":
            {set class to Spacialise('M')} 
            {set shortdescription to "gigante roja"}
            {set desc2 to Occasionally(4, "que se está expandiendo para convertirse en una supergigante roja")}
        |elif reportbody.stellarclass = "K_OrangeGiant":
            {set class to Spacialise('M')} 
            {set shortdescription to "gigante naranja"}
            {set desc2 to Occasionally(4, "que se expande mientras se dirige hacia el final de su vida")}
        |elif reportbody.stellarclass = "RoguePlanet":
            {set class to ""}  
            {set shortdescription to 'planeta {OneOf("interestelar", "vagabundo", "errante", "huérfano")}'}
        |elif reportbody.stellarclass = "Nebula":
            {set class to ""}  
            {set shortdescription to "nebulosa"}
        |elif reportbody.stellarclass = "StellarRemnantNebula":
            {set class to ""}  
            {set shortdescription to OneOf("nebulosa remanente", "remanente de Supernova")}
        }
        
        {if !shortdescription:
            {set shortdescription to "estrella"}
        }
        {set description to shortdescription}
        {if desc1: 
            {set description to cat(description, " ", desc1)}
        }
        {if desc2: 
            {set description to cat(description, " ", desc2)}
        }

        {_ Set additional common elements _}

        {if !class:
            {set class to Spacialise(reportbody.stellarclass)}
        }
        
        {_ Begin speaking _}

        {if len(notables) > 0:
            {if reportbody.shortname != reportbody.systemname:
                {P(reportbody.shortname, "body")} es
                {shortdescription}
                {mainsequence}
                {if fastburning = 1: {Occasionally(2, "de quema rápida")}}
            |else:
                Esta {shortdescription} es
                {mainsequence}
                {if fastburning = 1: {Occasionally(2, "de quema rápida")}}
            }

            {if rarity:
                {rarity}{if reportbody.chromaticity: ", "} y
            }

            {if len(notables) = 1:
                notable,
            |elif len(notables) = 2:
                {OneOf('interesante,', 'notable', 'remarcable')}
            |elif len(notables) = 3:
                {OneOf('exceptional,', 'muy remarcable,')}
            |elif len(notables) = 4:
                {OneOf('extraordinaria,')}
            |elif len(notables) >= 5:
                {OneOf('única en su tipo,', 'única,')}
            }

            {if reportClass = 1 && class && len(class) > 0: 
                {set class to cat("de clase", " ", class)}
                {if reportSubClass && reportbody.stellarsubclass:
                    {set class to cat(class, " ", reportbody.stellarsubclass)}
                }
            }
            {Occasionally(3, "en comparación a {shortdescription}s {class} similares,")}

            {if len(notables) > 0:
                con {List(notables)}.
            }

            {if reportbody.chromaticity && reportChromaticity >= 1:
                {if reportbody.chromaticity = "blanco":
                    {set color to OneOf("de color blanco", "blanca")}
                |else:
                    {set color to reportbody.chromaticity}
                }
                {Occasionally(reportChromaticity, "Como puedes ver, emite una luz {color}.")}
            }
        }
    }   

    {if reportScanValue && rand(reportScanValue) = 0: {F("Report last scan value")} }

    {if reportHabZone && event.mainstar && rand(reportHabZone) = 0: {F("Star habitable zone")} }
}

Star scanned.script:
{_ Context }
{SetState('eddi_context_last_subject', 'star')}
{SetState('eddi_context_last_action', 'scan')}
{SetState('eddi_context_star_system', event.systemname)}
{SetState('eddi_context_star_star', event.bodyname)}
{SetState('eddi_context_last_scan_estimated_value', event.estimatedvalue)}
{SetState('eddi_context_last_scan_max_value', event.estimatedvalue)}

{if !GetState("eddi_context_star_lastscan_cooldown"): {SetState('eddi_context_star_lastscan_cooldown', 0)}}

{if (SecondsSince(GetState("eddi_context_star_lastscan_timestamp")) > GetState("eddi_context_star_lastscan_cooldown")) && (ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose"):
	{_ Establece espera entre escaneos _}
	{SetState('eddi_context_star_lastscan_timestamp', SecondsSince(0))}
	{SetState('eddi_context_star_lastscan_cooldown', 0)}
    
    {if event.mainstar && !event.alreadydiscovered && event.scantype = "AutoScan":
        {set report to "Este sistema {Ocasionally(3, 'estelar')} {Occasionally(2, 'aún')} no {OneOf('ha sido', 'está', 'se ha')} {OneOf('registrado', 'reportado', 'cartografiado')} {Ocasionally(4, 'con Uvisersal Cartographics')}{Pause(500)}"}
    }
    
    {set report to cat(report, F("Star report"))}
    
    {_ Espera entre escaneos: 0,05 segundos por letra _}
	{SetState('eddi_context_star_lastscan_cooldown', round(GetState("eddi_context_star_lastscan_cooldown") + len(report) * 0.05))}

    {report}
}

Surface signals detected.script:
{if (vehicle = "Ship" || vehicle = "Multicrew") && environment != "Docked":

	{set surfacesignals to []}
	{for signal in event.surfacesignals: 
		{_ traducción del plural del tipo de señal}
	
		{if signal.source = "Señal Geológica de Superficie": 
			{set tipopl to "Señales geológicas de Superficie"}        
		|elif signal.source = "Señal no identificada":
	  		{set tipopl to "Señales no identificadas"}
		|elif signal.source = "Señal no humana":
	   		{set tipopl to "Señales no humanas"}
		|elif signal.source = "Señal Biológica de Superficie":
	   		{set tipopl to "Señales Biológicas de Superficie"}
		|elif signal.source = "Señal de Superficie Humana":
	   		{set tipopl to "Señales de Superficie Humanas"}
		|elif signal.source = "Señal de Superficie Zargoide":
	   		{set tipopl to "Señales de Superficie Zargoide"}
		|elif signal.source = "Señal de Superficie Guardián":
	   		{set tipopl to "Señales de Superficie Guardián"}
		}

		{if signal.amount > 1:
	   		{set surfacesignals_desc to cat(signal.amount, " ", tipopl)}
	   		{set s to "s"}
	   	|else:
	    	{set surfacesignals_desc to cat(signal.amount, " ", signal.source)}
	    }

		{set surfacesignals to cat(surfacesignals, [surfacesignals_desc])}
	}

	{if len(surfacesignals) > 1: {set s to "s"}}
	{set body to BodyDetails(event.bodyname)}
    {if body:
        {OneOf("Detectada{s}", "Identificada{s}")} {List(surfacesignals)} en {Occasionally(2, "{creaArticulos(body.bodytype)}{el} {body.bodytype}")} {P(body.shortname)}.
    |else:
        {OneOf("Detectada{s}", "Identificada{s}")} {List(surfacesignals)}.
    }

	
	
}

Swapout check.script:
{_ Function to find a given module in outfitting}
{set outfitting(module) to:
    {for outfittingmodule in station.outfitting:
        {if outfittingmodule.EDDBID = module.EDDBID:
            {return outfittingmodule}
        }
    }
}

{_ Function to report if a module is cheaper in outfitting that on the ship}
{set reportIfCheaper(shipmodule) to:
    {_ Only consider if this has no modifications and is not a specialized Guardian module}
    {if !shipmodule.modified && find(module.edname, "Guardian") = -1:
        {set outfittingmodule to outfitting(shipmodule)}
        {if outfittingmodule:
            {if outfittingmodule.price > 0 && (outfittingmodule.price < shipmodule.price):
                {set saving to shipmodule.price - outfittingmodule.price}
                {_ Only bother if it's a relatively good saving... more than 2% of the ship value or more than 0.1% of the commander's credit balance}
                {if (saving / ship.value) > 0.02 || (cmdr.credits > 0 && (saving / cmdr.credits) > 0.001):
                	{if find(saving,"ón",len(saving)-3)!=-1 || find(saving,"nes",len(saving)-4)!=-1 || find(saving,"dio",len(saving)-4)!=-1: {set de to "de"}}
                	{creaArticulos(shipmodule.name)}
                    Puede ahorrar {saving} {de} créditos intercambiando {OneOf("aquí", "en esta estación")} su{s} {shipmodule.name}.
                }
            }
        }
    }
}

{reportIfCheaper(ship.powerplant)}
{reportIfCheaper(ship.thrusters)}
{reportIfCheaper(ship.frameshiftdrive)}
{reportIfCheaper(ship.lifesupport)}
{reportIfCheaper(ship.powerdistributor)}
{reportIfCheaper(ship.sensors)}
{for hardpoint in ship.hardpoints:
    {if hardpoint.module:
        {reportIfCheaper(hardpoint.module)}
    }
}
{for compartment in ship.compartments:
    {if compartment.module:
        {reportIfCheaper(compartment.module)}
    }
}

