System state changed.script:
{_ Context }
{SetState('eddi_context_last_subject', 'system')}
{SetState('eddi_context_last_action', 'change_state')}
{SetState('eddi_context_system', event.system)}

{_ Set up our system name}
{if event.system = homesystem.name:
  {set system to OneOf('{P(event.system, "starsystem")}',
                       'Tu sistema hogar')}
|elif event.system = system.name:
  {set system to OneOf('{P(event.system, "starsystem")}',
                       'Este sistema')}
|else:
  {set system to OneOf('{P(event.system, "starsystem")}'
                       'El sistema {P(event.system, "starsystem")}'))}
}

{if event.newstate != 'None':
  {_ System has entered a new state}
  {OneOf('Las transmisiones locales informan que', 'Información. ', 'Transmisión interceptada. Informan que ')}
  {if event.newstate = 'Boom':
    {OneOf('{system} está experimentando un auge comercial',
           '{system} está entrando en un período de mayor éxito comercial')}
  |elif event.newstate = 'Bust':
    {system} está experimentando dificultades económicas
  |elif event.newstate = 'Civil Unrest':
    {OneOf('{system} está experimentando disturbios civiles',
           'hay disturbios en el {system} system')}
  |elif event.newstate = 'Civil War':
    {OneOf('{system} está en guerra con otra facción en el mismo sistema',
           'una guerra civil estalló en {system}',
           '{system} ha caído en una guerra civil',
           'han estallado hostilidades en {system}')}
  |elif event.newstate = 'Election':
    {system} está pasando por elecciones
  |elif event.newstate = 'Expansion':
    {OneOf("una facción en {system} está intentando expandirse a un sistema vecino",
           "una facción en {system} está preparándose para expandirse a un sistema vecino")}
  |elif event.newstate = 'Famine':
    {system} está sufriendo hambruna
  |elif event.newstate = 'Investment':
    En {system} la facción controladora está invirtiendo fuertemente en su infraestructura local
  |elif event.newstate = 'Lockdown':
    {system} ha aumentado la seguridad debido a un bloqueo por parte de la facción dominante
  |elif event.newstate = 'Outbreak':
    {system} sufre de un brote de enfermedad contagiosa
  |elif event.newstate = 'Retreat':
    La facción que controla {system} se está retirando de un sistema vecino
  |elif event.newstate = 'War':
    {system} ahora está en guerra con un sistema vecino
  |else:
   {OneOf('{system} ha entrado en {event.state}',
           '{system} ahora está en {event.state}')}
  }.
}

System state report.script:
{_ Fetch from Context, if a context is set _}
{if GetState("eddi_context_system_name"):
    {set reportSystem to SystemDetails(GetState("eddi_context_system_name"))}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{if !reportSystem.name || reportSystem.name = "":
    No tengo claro a que sistema se refiere.
|else:
	{_ Report any ongoing conflicts _}
    {set conflictfactions to []}
    {if reportSystem.conflicts != null:
        {for conflict in reportSystem.conflicts:
        	{set conflictnone to 0}
            {P(conflict.faction1, "faction")}

            {if conflict.status = "active":
                {OneOf("está", "va")}
            |elif conflict.status = "pending":
                pronto estará
            |else:
            	{set conflictnone to 1}
                se está recuperando 
            }
           	{if conflict.status != "pending":
            	{if conflict.faction1dayswon > conflict.faction2dayswon:
	               {if conflict.margin >= 3:
    	               {if conflictnone: de dominar |else: dominando}
    	           |elif conflict.margin = 2:
    	               {if conflictnone: de ganar |else: ganando}
    	           |else:
    	               {if conflictnone: de una ajustada victoria |else: en cabeza}
    	           }
    	        |elif conflict.faction2dayswon > conflict.faction1dayswon:
    	           {if conflict.margin >= 2:
    	               {if conflictnone: de haber perdido |else: perdiendo}
    	           |else:
    	               {if conflictnone: de una apretada derrota | else: a la cola}
    	           }
    	        |else:
    	           {if conflictnone:de ir }empatado
    	        }
    	        {conflict.faction1dayswon} a {conflict.faction2dayswon}
    	    }

			{creaArticulos(conflict.state)}

            en un{o}{s} {conflict.state} con {P(conflict.faction2, "faction")} {if conflict.stake: por el control de {P(conflict.stake, "station")}}.
            {set conflictfactions to cat(conflictfactions, [conflict.faction1])}
            {set conflictfactions to cat(conflictfactions, [conflict.faction2])}
        }
    }

    {_ List our non-conflict faction states _}
    {set otherstates to []}
    {for faction in reportSystem.factions:
        {if faction.influence > 0 && find(conflictfactions, faction.name) = -1:
            {for presence in faction.presences:
                {if presence.systemName = reportSystem.name:
                    {if presence.FactionState.invariantName != "None":

                        {_ Note our controlling faction, if present in our `otherstates` list. _}
                        {if faction.name = reportSystem.faction:
                            {set factions to token(factions, "", index, "controlling ")}
                        }

                        {_ Add the faction to our list _}
                        {if has(otherstates, presence.state):
                            {set factions to otherstates[presence.state])}
                            {set factions to cat(factions, ",", P(faction.name, "faction"))}
                            {set otherstates to union(otherstates, [presence.state: factions])}
                        |else:
                            {set otherstates to union(otherstates, [presence.state: P(faction.name, "faction")])}
                        }
                    }
                }
            }
        }
    }

    {_ Sort from most factions with each state to least factions with each state. _}
    {declare by_items(a, b) as:
        {return cmp(len(split(b, ",")), len(split(a, ",")))}
    }
    {sort(otherstates, by_items)}

    {_ Prepare summaries for each state _}
    {set statesummaries to []}
    {for state, factions in otherstates:

        {set factions to split(factions, ",")}

        {set statesummary to:
            {if len(factions) > 1:
                las facciones {List(factions)} están
            |else:
                la facción {factions[0]} está
            }
            {if state = "Retirada":
                en retirada
            |elif state = "Guerra":
                en guerra
            |elif state = "Auge":
                experimentando un auge económico; podría hacer buenos negocios con ellos
            |elif state = "Libertad Civil":
                disfrutando de un período de mayor libertad civil
            |elif state = "Desórdenes públicos":
                experimentando disturbios civiles
            |elif state = "Guerra civil":
                en guerra civil
            |elif state = "Hambruna":
                {OneOf("sufriendo {Occasionally(2,'de')}", "pasando")} {Occasionally(2, "una")} hambruna
            |elif state = "Bloqueo":
                en bloqueo
            |elif state = "Epidemia":
                tratando de contener  {OneOf("un brote de enfermedad", "una epidemia")}
            |elif state = "Expansión":
                preparandose para expandirse a otro sistema
            |elif state = "Quiebra":
                {OneOf("en quiebra","en crisis financiera")}
            |elif state = "Elecciones":
                en periodo electoral
            |elif state = "Inversión":
                invirtiendo en infraestructuras
            |elif state = "Incursión":
                sufriendo una invasión alienígena. {Emphasize("¡Alerta!",'strong')}
            |elif state = "Ataque Pirata":
                sufriendo ataques de piratas
            |elif state = "Plaga":
                sufriendo de una plaga de cultivos
            |else: {_ catch-all, worded to avoid deciding between a/an}
                en estado de {state}
            }
        }
        {set statesummaries to cat(statesummaries, [statesummary])}
    }

    {_ Describe our non-conflict states _}
    {if len(statesummaries) > 0:
        {Pause(100)}
        {List(statesummaries)}
    }

}

